  300 CONTINUE                                                          GEO 2210
      IF(NPR.NE.1)WRITE(IPR,46)                                         GEO 2220
   46 FORMAT(///,T3,'J',T09,'Z',T17,'N2 CONT',T28,'MOL SCAT',T43,       GEO 2230
     1    'AER 1',T55,'AER 2',T67,'AER 3',T79,'AER 4',T091,'CIRRUS',/   GEO 2240
     2    ,T8,'(KM)',/)                                                 GEO 2250
      LP = 1                                                            GEO 2260
      IF(LEN.EQ.1  .OR. H1.GT.H2) LP = 0                                GEO 2270
      DO 310 J=1,JMAX                                                   GEO 2280
      L = LJ(J)                                                         GEO 2290
      IF(J.EQ.JTURN) LP = 1                                             GEO 2300
      LZ = L+LP                                                         GEO 2310
      IF(NPR.NE.1)WRITE(IPR,48) J,ZP(LZ),(WPATH(J,KMOL(K)),K=9,15)      GEO 2320
   48 FORMAT(I3,F9.3,1P7E12.3)                                          GEO 2330
  310 CONTINUE                                                          GEO 2340
C*****PRINT PATH SUMMARY                                                GEO 2350
315   IF(ISSGEO.EQ.1) GO TO 320                                         GEO 2360
      IF(NPR.NE.1)WRITE(IPR,47)                                         GEO 2370
   47 FORMAT(///'   J    Z       H2O       O3        CO2       CO    ', GEO 2380
     +'    CH4       N2O       O2        NH3       NO        NO2   ',   GEO 2390
     +'    SO2'/'      (KM)   (G/CM**2)  (                           ', GEO 2400
     +'                 ATM CM                                     ',   GEO 2410
     +'        )'/)                                                     GEO 2420
      LP = 1                                                            GEO 2430
      IF(LEN.EQ.1  .OR. H1.GT.H2) LP = 0                                GEO 2440
      DO 312 J=1,JMAX                                                   GEO 2450
      L = LJ(J)                                                         GEO 2460
      IF(J.EQ.JTURN) LP = 1                                             GEO 2470
      LZ = L+LP                                                         GEO 2480
      IF(NPR.NE.1)WRITE(IPR,49) J,ZP(LZ),WPATH(J,17),WPATH(J,31),       GEO 2490
     + WPATH(J,36),WPATH(J,44),WPATH(J,46),WPATH(J,47),WPATH(J,50),     GEO 2500
     + WPATH(J,52),WPATH(J,54),WPATH(J,55),WPATH(J,56)                  GEO 2510
  312 CONTINUE                                                          GEO 2520
   49 FORMAT(I4,F8.2,1P11E10.2)                                         GEO 2530
      WRITE(IPR,40)H1,H2,ANGLE,RANGE,BETA,PHI,HMIN,BENDNG,LEN           GEO 2540
   40 FORMAT(//,'0SUMMARY OF THE GEOMETRY CALCULATION',//,              GEO 2550
     1 10X,'H1      = ',F10.3,' KM',/,10X,'H2      = ',F10.3,' KM',/,   GEO 2560
     110X,'ANGLE   = ',F10.3,' DEG',/,10X,'RANGE   = ',F10.3,' KM',/,   GEO 2570
     310X,'BETA    = ',F10.3,' DEG',/,10X,'PHI     = ',F10.3,' DEG',/,  GEO 2580
     4 10X,'HMIN    = ',F10.3,' KM',/,10X,'BENDING = ',F10.3,' DEG',/,  GEO 2590
     5 10X,'LEN     = ',I10)                                            GEO 2600
  320 CONTINUE                                                          GEO 2610
C*****CALCULATE THE AEROSOL WEIGHTED MEAN RH                            GEO 2620
      IF(W(7).GT.0.0 .AND. ICH(1).LE.7)  W15   = W(15)/W(7)             GEO 2630
      IF(W(12).GT.0.0 .AND. ICH(1).GT.7)  W15   = W(15)/W(12)           GEO 2640
C                                                                       GEO 2650
C     INVERSE OF LOG REL HUM                                            GEO 2660
C                                                                       GEO 2670
      IF(W(7).GT.0.0 .AND. ICH(1).LE.7)  THEN                           GEO 2680
           W(15) = 100. - EXP(W15)                                      GEO 2690
      ENDIF                                                             GEO 2700
      IF(W(12).GT.0.0 .AND. ICH(1).GT.7)  THEN                          GEO 2710
           W(15) = 100. - EXP(W15)                                      GEO 2720
      ENDIF                                                             GEO 2730
      IF (W(7).LE.0.0 .AND. ICH(1).LE.7 )W(15) = 0.                     GEO 2740
      IF(W(12).LE.0.0 .AND. ICH(1).GT.7)  W(15) = 0.                    GEO 2750
C                                                                       GEO 2760
C*****PRINT TOTAL PATH AMOUNTS                                          GEO 2770
      IF(ISSGEO.EQ.1) RETURN                                            GEO 2780
      WRITE(IPR,50)  (W(KMOL(K)),K=4,16)                                GEO 2790
   50 FORMAT(////,' EQUIVALENT SEA LEVEL TOTAL ABSORBER AMOUNTS',//,    GEO 2800
     1    T18,'HNO3',T28,'O3 UV',                                       GEO 2810
     2    T40,'CNTMSLF1',T52,'CNTMSLF2',T63,'CNTMFRN',/,                GEO 2820
     3                   T16,'(ATM CM)',T27,'(ATM CM)',T40,             GEO 2830
     4    '(MOL CM-2)',T52,'(MOL CM-2)',T64,'(MOL CM-2)',               GEO 2840
     +    T76,'(MOL CM-2)',                                             GEO 2850
     +    //,10X,1P5E12.3,///,                                          GEO 2860
     5    T15,'N2 CONT',T26,'MOL SCAT',T41,'AER 1', T53,'AER 2',        GEO 2870
     6    T65,'AER 3',T77, 'AER 4',T87,'CIRRUS',T99,'MEAN RH'/,         GEO 2880
     7    T99,'(PRCNT)',//,10X,1P7E12.3,0PF12.2)                        GEO 2890
C                                                                       GEO 2900
      WRITE(IPR,60) W(17),W(31),W(36),W(44),W(46),W(47),W(50),          GEO 2910
     +              W(52),W(54),W(55),W(56)                             GEO 2920
   60 FORMAT(                                                           GEO 2930
     1 //T18,'H2O',T29,'O3',T41,'CO2',T53,'CO',T65,'CH4',T77,'N2O',     GEO 2940
     +   T90,'O2',                                                      GEO 2950
     2  /T15,'(G/CM**2)',T29,'(',T57,'ATM CM',T91,')',//10X,1P7E12.3,   GEO 2960
     3 //T18,'NH3',T29,'NO',T41,'NO2',T53,'SO2',/T18,'(',T34,'ATM CM',  GEO 2970
     +   T55,')',                                                       GEO 2980
     4 //10X,1P4E12.3)                                                  GEO 2990
      IF(JMAXST .GT. MAXGEO) THEN                                       GEO 3000
      WRITE(IPR,900)MAXGEO,JMAXST                                       GEO 3010
 900  FORMAT(//'  CURRENT GEOMETRY DIMENSION ',I5 ,/                    GEO 3020
     X,' JMAXST = ',I5,' RESET AVTRAT TDIFF1 TDIFF2 TO 2. 10. 20.')     GEO 3030
      STOP 'GEO :JMAXST .GT. MAXGEO'                                    GEO 3040
      ENDIF                                                             GEO 3050
      DO 350 IK=1,JMAXST                                                GEO 3060
      IL=LJ(IK)                                                         GEO 3070
      RNPATH(IK)=SP(IL)                                                 GEO 3080
      RRAMTK(IK)=RRAMT(IL)                                              GEO 3090
350   CONTINUE                                                          GEO 3100
C                                                                       GEO 3110
500   FORMAT(/10X,'ICH(1),W(15),W(7),W(12)=',I5,1P3E12.3/)              GEO 3120
      RETURN                                                            GEO 3130
