      SUBROUTINE GEOINP(H1,H2,ANGLE,RANGE,BETA,ITYPE,LEN,HMIN,PHI,      GEI  100
     1    IERROR)                                                       GEI  110
C***********************************************************************GEI  120
C     GEOINP INTERPRETS THE ALLOWABLE COMBINATIONS OF INPUT PATH        GEI  130
C     PARAMETERS INTO THE STANDARD SET H1,H2,ANGLE,PHI,HMIN, AND LEN.   GEI  140
C     THE ALLOWABLE COMBINATIONS OF INPUT PARAMETERS ARE- FOR ITYPE = 2,GEI  150
C     (SLANT PATH H1 TO H2) A. H1, H2, AND ANGLE, B. H1, ANGLE, AND     GEI  160
C     RANGE, C. H1, H2, AND RANGE, D. H1, H2, AND BETA -                GEI  170
C     FOR ITYPE = 3 (SLANT PATH H1 TO SPACE H2 = 100 KM),               GEI  180
C     A. H1 AND ANGLE, B. H1 AND HMIN (INPUT AS H2).                    GEI  190
C     THE SUBROUTINE ALSO DETECTS BAD INPUT (IMPOSSIBLE GEOMETRY) AND   GEI  200
C     ITYPE = 2 CASES WHICH INTERSECT THE EARTH, AND RETURNS THESE      GEI  210
C     CASES WITH ERROR FLAGS.                                           GEI  220
C     THE SUBROUTINE FNDHMN IS CALLED TO CALCULATE HMIN, THE MINIMUM    GEI  230
C     HEIGHT ALONG THE PATH, AND PHI, THE ZENITH ANGLE AT H2, USING THE GEI  240
C     ATMOSPHERIC PROFILE STORED IN /MODEL/                             GEI  250
C***********************************************************************GEI  260
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                GEI  270
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              GEI  280
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     GEI  290
      ITER = 0                                                          GEI  300
      IF(ITYPE.NE.3) GO TO 120                                          GEI  310
C*****SLANT PATH TO SPACE                                               GEI  320
C*****NOTE: IF BOTH HMIN AND ANGLE ARE ZERO, THEN ANGLE IS              GEI  330
C*****ASSUMED SPECIFIED                                                 GEI  340
      IF(H2.NE.0.0)  GO TO 110                                          GEI  350
C*****CASE 3A: H1,SPACE,ANGLE                                           GEI  360
      IF(NPR.NE.1)WRITE(IPR,10)                                         GEI  370
   10 FORMAT(//,' CASE 3A: GIVEN H1,H2=SPACE,ANGLE')                    GEI  380
      H2 =  ZMAX                                                        GEI  390
      CALL FNDHMN(H1,ANGLE,H2,LEN,ITER,HMIN,PHI,IERROR)                 GEI  400
      IF(IERROR.EQ.-5) RETURN                                           GEI  410
      GO TO 200                                                         GEI  420
  110 CONTINUE                                                          GEI  430
C*****CASE 3B: H1,HMIN,SPACE                                            GEI  440
      IF(NPR.NE.1)WRITE(IPR,12)                                         GEI  450
   12 FORMAT(//,' CASE 3B: GIVEN H1, HMIN, H2=SPACE')                   GEI  460
      HMIN = H2                                                         GEI  470
      H2 = ZMAX                                                         GEI  480
      IF(H1.LT.HMIN) GO TO 9001                                         GEI  490
      CALL FNDHMN(HMIN,90.0,H1,LEN,ITER,HMIN,ANGLE,IERROR)              GEI  500
      CALL FNDHMN(HMIN,90.0,H2,LEN,ITER,HMIN,PHI,IERROR)                GEI  510
      IF(HMIN.LT.H1) LEN = 1                                            GEI  520
      GO TO 200                                                         GEI  530
  120 CONTINUE                                                          GEI  540
      IF(ITYPE.NE.2) GO TO 9002                                         GEI  550
      IF(RANGE.NE.0.0.OR.BETA.NE.0.0) GO TO 130                         GEI  560
C*****CASE 2A: H1, H2, ANGLE                                            GEI  570
      IF(NPR.NE.1)WRITE(IPR,16)                                         GEI  580
   16 FORMAT(//, ' CASE 2A: GIVEN H1, H2, ANGLE')                       GEI  590
      IF(H1.GE.H2.AND.ANGLE.LE.90.0) GO TO 9004                         GEI  600
      IF(H1.EQ.0.0 .AND. ANGLE.GT.90.0) GO TO 9007                      GEI  610
      IF(H2.LT.H1.AND.ANGLE.GT.90.0.AND.NPR.NE.1) WRITE(IPR,15) LEN     GEI  620
   15 FORMAT(//,' EITHER A SHORT PATH (LEN=0) OR A LONG PATH ',         GEI  630
     1    'THROUGH A TANGENT HEIGHT (LEN=1) IS POSSIBLE: LEN = ',       GEI  640
     2    I3)                                                           GEI  650
      H2ST = H2                                                         GEI  660
      CALL FNDHMN(H1,ANGLE,H2,LEN,ITER,HMIN,PHI,IERROR)                 GEI  670
      IF(H2.NE.H2ST)  GO TO 9007                                        GEI  680
      GO TO 200                                                         GEI  690
  130 CONTINUE                                                          GEI  700
      IF(BETA.EQ.0.0) GO TO 133                                         GEI  710
      CALL FDBETA(H1,H2,BETA,ANGLE,PHI,LEN,HMIN,IERROR)                 GEI  720
      GO TO 200                                                         GEI  730
  133 CONTINUE                                                          GEI  740
      IF(ANGLE.EQ.0.0) GO TO 140                                        GEI  750
C*****CASE 2B: H1, ANGLE, RANGE                                         GEI  760
C*****ASSUME NO REFRACTION                                              GEI  770
      IF(NPR.NE.1)WRITE(IPR,18)                                         GEI  780
   18 FORMAT(//,' CASE 2B:, GIVEN H1, ANGLE, RANGE',//                  GEI  790
     1    10X,'NOTE: H2 IS COMPUTED FROM H1, ANGLE, AND RANGE ',        GEI  800
     2    'ASSUMING NO REFRACTION')                                     GEI  810
      R1 = RE+H1                                                        GEI  820
      R2 = SQRT(R1**2+RANGE**2+2.0*R1*RANGE*COS(+ANGLE/DEG))            GEI  830
      H2 = R2-RE                                                        GEI  840
      IF(H2.GE.0.0)  GO TO 135                                          GEI  850
      H2 = 0.0                                                          GEI  860
      R2 = RE+H2                                                        GEI  870
      RANGE = -R1*COS(ANGLE/DEG)-SQRT(R2**2-R1**2*(SIN(ANGLE/DEG))**2)  GEI  880
      IF(NPR.NE.1)WRITE(IPR,17) RANGE                                   GEI  890
   17 FORMAT(//,10X,'CALCULATED H2 IS LESS THAN ZERO:',/,               GEI  900
     1    10X,'RESET H2 = 0.0 AND RANGE = ',F10.3)                      GEI  910
  135 CONTINUE                                                          GEI  920
C*****NOTE: GEOMETRIC PHI IS NEEDED TO DETERMINE LEN(0 OR 1).           GEI  930
C*****PHI IS THEN RECOMPUTED IN FNDHMN                                  GEI  940
      ZARG1=(R2**2+RANGE**2-R1**2)/(2.0*R2*RANGE)                       GEI  950
      ERARG1=ABS(ZARG1)-1.0                                             GEI  960
      IF((ERARG1.GT.1.0E-6).OR.(ERARG1.LT.0.0)) GO TO 137               GEI  970
      IF (ZARG1.LT.0.0) GO TO 138                                       GEI  980
      ZARG1=1.0                                                         GEI  990
      GO TO 137                                                         GEI 1000
  138 ZARG1=-1.0                                                        GEI 1010
  137 PHI = 180.0-ACOS(ZARG1)*DEG                                       GEI 1020
      LEN = 0                                                           GEI 1030
      IF(ANGLE.GT.90.0.AND.PHI.GT.90.0) LEN = 1                         GEI 1040
      CALL FNDHMN(H1,ANGLE,H2,LEN,ITER,HMIN,PHI,IERROR)                 GEI 1050
      GO TO 200                                                         GEI 1060
  140 CONTINUE                                                          GEI 1070
C*****CASE 2C: H1, H2, RANGE                                            GEI 1080
      IF(NPR.NE.1)WRITE(IPR,19)                                         GEI 1090
   19 FORMAT(//,' CASE 2C: GIVEN H1, H2, RANGE',//,                     GEI 1100
     1    10X,'NOTE: ANGLE IS COMPUTED FROM H1, H2, AND RANGE ',        GEI 1110
     2    'ASSUMING NO REFRACTION')                                     GEI 1120
      IF(ABS(H1-H2).GT.RANGE) GO TO 9003                                GEI 1130
      R1 = H1+RE                                                        GEI 1140
      R2 = H2+RE                                                        GEI 1150
C%    PHI = 180.0-ACOS((R2**2+RANGE**2-R1**2)/(2.0*R2*RANGE))*DEG       *** 1160
      ZARG2=(R1**2+RANGE**2-R2**2)/(2.0*R1*RANGE)                       GEI 1170
      ERARG2=ABS(ZARG2)-1.0                                             GEI 1180
      IF((ERARG2.GT.1.0E-6).OR.(ERARG2.LT.0.0)) GO TO 187               GEI 1190
      IF (ZARG2.LT.0.0) GO TO 188                                       GEI 1200
      ZARG2=1.0                                                         GEI 1210
      GO TO 187                                                         GEI 1220
  188 ZARG2=-1.0                                                        GEI 1230
  187 ANGLE = 180.0-ACOS(ZARG2)*DEG                                     GEI 1240
      ZARG3=(R2**2+RANGE**2-R1**2)/(2.0*R2*RANGE)                       GEI 1250
      ERARG3=ABS(ZARG3)-1.0                                             GEI 1260
      IF((ERARG3.GT.1.0E-6).OR.(ERARG3.LT.0.0)) GO TO 197               GEI 1270
      IF (ZARG3.LT.0.0) GO TO 198                                       GEI 1280
      ZARG3=1.0                                                         GEI 1290
      GO TO 197                                                         GEI 1300
  198 ZARG3=-1.0                                                        GEI 1310
  197 PHI = 180.0-ACOS(ZARG3)*DEG                                       GEI 1320
      BETA = PHI +ANGLE -180.                                           GEI 1330
C#    RANGE = 0.                                                        GEI 1340
      IF(RANGE .GT. 2.0  .AND. BETA .GT. 0)THEN                         *** 1341
      CALL FDBETA(H1,H2,BETA,ANGLE,PHI,LEN,HMIN,IERROR)                 GEI 1350
      ELSE                                                              *** 1351
CJ                                                                      GEI 1360
      LEN = 0                                                           *** 1370
      IF(ANGLE.GT.90.0.AND.PHI.GT.90.0) LEN = 1                         *** 1380
      CALL FNDHMN(H1,ANGLE,H2,LEN,ITER,HMIN,PHI,IERROR)                 *** 1390
      ENDIF                                                             *** 1392
  200 CONTINUE                                                          GEI 1400
C*****TEST IERROR AND RECHECK LEN                                       GEI 1410
      IF(IERROR.NE.0)  RETURN                                           GEI 1420
      LEN = 0                                                           GEI 1430
      IF(HMIN.LT.AMIN1(H1,H2)) LEN = 1                                  GEI 1440
C*****REDUCE PATH ENDPOINTS ABOVE ZMAX TO ZMAX                          GEI 1450
      IF(HMIN.GE.ZMAX) GO TO 9008                                       GEI 1460
      IF(H1.GT.ZMAX .OR. H2.GT.ZMAX)  CALL REDUCE(H1,H2,ANGLE,PHI,ITER) GEI 1470
C*****AT THIS POINT THE FOLLOWING PARAMETERS ARE DEFINED:               GEI 1480
C*****    H1,H2,ANGLE,PHI,HMIN,LEN                                      GEI 1490
      IF(NPR.NE.1)WRITE(IPR,20)  H1,H2,ANGLE,PHI,HMIN,LEN               GEI 1500
   20 FORMAT(///,' SLANT PATH PARAMETERS IN STANDARD FORM',//,          GEI 1510
     1  10X,'H1      = ',F10.3,' KM',/,10X,'H2      = ',F10.3,' KM',/,  GEI 1520
     2  10X,'ANGLE   = ',F10.3,' DEG',/,10X,'PHI     = ',F10.3,' DEG',/,GEI 1530
     3    10X,'HMIN    = ',F10.3,' KM',/,10X,'LEN     = ',I10)          GEI 1540
      RETURN                                                            GEI 1550
C*****                                                                  GEI 1560
C*****ERROR MESAGES                                                     GEI 1570
C*****                                                                  GEI 1580
 9001 CONTINUE                                                          GEI 1590
      WRITE(IPR,40) H1,HMIN                                             GEI 1600
   40 FORMAT('0GEOINP, CASE 3B (H1,HMIN,SPACE): ERROR IN INPUT DATA',   GEI 1610
     1    //,10X,'H1 = ',F12.6,'    IS LESS THAN HMIN = ',F12.6)        GEI 1620
      GO TO 9900                                                        GEI 1630
 9002 WRITE(IPR,42) ITYPE,ITYPE                                         GEI 1640
   42 FORMAT('0GEOINP: ERROR IN INPUT DATA, ITYPE NOT EQUAL TO ',       GEI 1650
     1    ' 2, OR 3.   ITYPE = ',I10,E23.14)                            GEI 1660
      GO TO 9900                                                        GEI 1670
 9003 WRITE(IPR,43) H1,H2,RANGE                                         GEI 1680
   43 FORMAT('0GEOINP, CASE 2C (H1,H2,RANGE): ERROR IN INPUT DATA',//,  GEI 1690
     110X,'ABS(H1-H2) GT RANGE;  H1 = ',F12.6,'    H2 = ',F12.6,        GEI 1700
     2    '    RANGE = ',F12.6)                                         GEI 1710
      GO TO 9900                                                        GEI 1720
 9004 CONTINUE                                                          GEI 1730
      WRITE(IPR,44)  H1,H2,ANGLE                                        GEI 1740
   44 FORMAT('0GEOINP, CASE 2A (H1,H2,ANGLE): ERROR IN INPUT DATA',     GEI 1750
     1  //,10X,'H1 = ',F12.6,'    IS GREATER THAN OR EQUAL TO H2 = ',   GEI 1760
     2    F12.6 ,/,10X,'AND ANGLE = ',F12.6,'    IS LESS THAN OR ',     GEI 1770
     3    'EQUAL TO 90.0')                                              GEI 1780
      GO TO 9900                                                        GEI 1790
 9007 WRITE(IPR,48)                                                     GEI 1800
   48 FORMAT('0GEOINP, ITYPE = 2: SLANT PATH INTERSECTS THE EARTH',     GEI 1810
     1    ' AND CANNOT REACH H2')                                       GEI 1820
      GO TO 9900                                                        GEI 1830
 9008 WRITE(IPR,50) ZMAX,H1,H2,HMIN                                     GEI 1840
   50 FORMAT(' GEOINP-  THE ENTIRE PATH LIES ABOVE THE TOP ZMAX ',      GEI 1850
     1    'OF THE ATMOSPHERIC PROFILE',//,10X,'ZMAX = ',G12.6,5X,       GEI 1860
     2    '  H1 = ',G12.6,5X,'  H2 = ',G12.6,'  HMIN = ',G12.6)         GEI 1870
 9900 IERROR = 1                                                        GEI 1880
      RETURN                                                            GEI 1890
      END                                                               GEI 1900
      SUBROUTINE REDUCE(H1,H2,ANGLE,PHI,ITER)                           RED  100
C***********************************************************************RED  110
C     ZMAX IS THE HIGHEST LEVEL IN THE ATMOSPHERIC PROFILE STORED IN    RED  120
C     COMMON /MODEL/.  IF H1 AND/OR H2 ARE GREATER THAN ZMAX, THIS      RED  130
C     SUBROUTINE REDUCES THEM TO ZMAX AND RESETS ANGLE AND/OR PHI       RED  140
C     AS NECESSARY. THIS REDUCTION IS NECESSARY,FOR EXAMPLE FOR         RED  150
C     SATELLITE ALTITUDES, BECAUSE (1) THE DENSITY PROFILES ARE         RED  160
C     POORLY DEFINED ABOVE ZMAX AND (2) THE CALCULATION TIME FOR        RED  170
C     PATHS ABOVE ZMAX CAN BE EXCESSIVE ( EG. FOR GEOSYNCHRONOUS        RED  180
C     ALTITUDES)                                                        RED  190
C***********************************************************************RED  200
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                RED  210
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              RED  220
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     RED  230
      IF(H1.LE.ZMAX .AND. H2.LE.ZMAX)  RETURN                           RED  240
      CALL FINDSH(H1,SH,GAMMA)                                          RED  250
      CPATH = ANDEX(H1,SH,GAMMA)*(RE+H1)*SIN(ANGLE/DEG)                 RED  260
      CALL FINDSH(ZMAX,SH,GAMMA)                                        RED  270
      CZMAX = ANDEX(ZMAX,SH,GAMMA)*(RE+ZMAX)                            RED  280
      ANGMAX = 180.0-ASIN(CPATH/CZMAX)*DEG                              RED  290
      IF(H1.LE.ZMAX) GO TO 120                                          RED  300
      H1 = ZMAX                                                         RED  310
      ANGLE = ANGMAX                                                    RED  320
  120 CONTINUE                                                          RED  330
      IF(H2.LE.ZMAX)  GO TO 130                                         RED  340
      H2 = ZMAX                                                         RED  350
      PHI = ANGMAX                                                      RED  360
  130 CONTINUE                                                          RED  370
       WRITE(IPR,20) ZMAX,ANGMAX                                        RED  380
   20 FORMAT(///,' FROM SUBROUTINE REDUCE: ',/,                         RED  390
     1   4X,'ONE OR BOTH OF H1 AND H2 ARE ABOVE THE TOP OF THE ',       RED  400
     2   'ATMOSPHERIC PROFILE ZMAX = ',F10.3,' AND HAVE BEEN RESET ',   RED  410
     3   'TO ZMAX',/,4X,'ANGLE AND/OR PHI HAVE ALSO BEEN RESET TO ',    RED  420
     4    'THE ZENITH ANGLE AT ZMAX = ',F10.3,' DEG')                   RED  430
  200 CONTINUE                                                          RED  440
      RETURN                                                            RED  450
      END                                                               RED  460
      SUBROUTINE FDBETA(H1,H2,BETAS,ANGLE,PHI,LEN,HMIN,IERROR)          ***  100
C********************************************************************** FDB  110
C     GIVEN H1,H2,AND BETA (THE EARTH CENTERED ANGLE) THIS SUBROUTINE   FDB  120
C     CALCULATES THE INITIAL ZENITH ANGLE AT H1 THROUGH AN ITERATIVE    FDB  130
C     PROCEDURE                                                         FDB  140
C********************************************************************** FDB  150
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                FDB  160
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              FDB  170
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     FDB  180
      DOUBLE PRECISION RA,RB,SG,ANGLE1,ANGLE2,BETA,DBETA                ***  181
C     DATA TOLRNC/1.0E-4/,ITERMX/10/,BETD /0.02/                        FDB  190
      DATA TOLRNC/5.0E-3/,ITERMX/10/,BETD /0.04/                        ***  200
      DATA ZER /0./                                                     FDB  210
      BETA = BETAS                                                      ***  211
      IFLAG = 0                                                         FDB  220
      IF(H1.GT.H2) GO TO 100                                            FDB  230
      IORDER = 1                                                        FDB  240
      HA = H1                                                           FDB  250
      HB = H2                                                           FDB  260
      GO TO 120                                                         FDB  270
  100 CONTINUE                                                          FDB  280
      IORDER = -1                                                       FDB  290
      HA = H2                                                           FDB  300
      HB = H1                                                           FDB  310
  120 CONTINUE                                                          FDB  320
C*****SET PARAMETER TO SUPRESS CALCULATION OF AMOUNTS                   FDB  330
      IAMTB = 2                                                         FDB  340
C*****GUESS AT ANGLE, INTEGRATE TO FIND BETA, TEST FOR                  FDB  350
C*****CONVERGENCE, AND ITERATE                                          FDB  360
C*****FIRST GUESS AT ANGLE: USE THE GEOMETRIC SOLUTION(NO REFRACTION)   FDB  370
      WRITE(IPR,24)                                                     FDB  380
   24 FORMAT(///,' CASE 2D: GIVEN H1, H2,  BETA:',//,                   FDB  390
     1    ' ITERATE AROUND ANGLE UNTIL BETA CONVERGES',//,              FDB  400
     2    ' ITER    ANGLE',T21,'BETA',T30,'DBETA',T40,'RANGE',          FDB  410
     3    T51,'HMIN',T61,'PHI',T70,'BENDING',/,                         FDB  420
     4    T10,'(DEG)',T21,'(DEG)',T30,'(DEG)',T41,'(KM)',               FDB  430
     5    T51,'(KM)',T60,'(DEG)',T71,'(DEG)',/)                         FDB  440
      ITER = 0                                                          FDB  450
      RA = RE+HA                                                        FDB  460
      RB = RE+HB                                                        FDB  470
      SG = SQRT(RA**2+RB**2-2.0*RA*RB*COS(BETA/DEG))                    FDB  480
      ANGLE1 = 180.0-ACOS((RA**2+SG**2-RB**2)/(2.0*RA*SG))*DEG          FDB  490
      HMIN = HA                                                         FDB  500
      IF(ANGLE1.GT.90.0)  HMIN = RA*SIN(ANGLE1/DEG)-RE                  FDB  510
      HMING = HMIN                                                      FDB  520
      ANGLS1 = ANGLE1                                                   FDB* 521
      CALL FNDHMN(HA,ANGLS1,HB,LEN,ITER,HMIN,PHI,IERROR)                FDB* 530
      LEN = 0                                                           FDB  540
      IF(HMIN.LT.HA) LEN = 1                                            FDB  550
      CALL RFPATH(HA,HB,ANGLS1,PHI,LEN,HMIN,IAMTB,BETA1,RANGE,BENDNG)   FDB* 560
      WRITE(IPR,26)  ITER,ANGLE1,BETA ,ZER  ,SG    ,HMING,ZER,ZER       FDB  570
C                                                                       FDB  580
C     OBTAIN DERIVATIVE                                                 FDB  590
C                                                                       FDB  600
      SG = SQRT(RA**2+RB**2-2.0*RA*RB*COS((BETA+BETD)/DEG))             FDB  610
      ANGLEP = 180.0-ACOS((RA**2+SG**2-RB**2)/(2.0*RA*SG))*DEG          FDB  620
      DANG = ANGLE1 - ANGLEP                                            FDB  630
      IF(HMIN.GE.0.0) GO TO 310                                         FDB  640
      IFLAG = 1                                                         FDB  650
      HMIN = 0.0                                                        FDB  660
      CALL FNDHMN(HMIN,90.0,HA,LEN,ITER,HMIN,ANGLS1,IERROR)             FDB* 670
  310 CONTINUE                                                          FDB  680
      ITER = 1                                                          FDB  690
      LEN = 0                                                           FDB  700
      IF(ANGLE1.GT.90.0) LEN = 1                                        FDB  710
      CALL FNDHMN(HA,ANGLS1,HB,LEN,ITER,HMIN,PHI,IERROR)                FDB* 720
      LEN = 0                                                           FDB  730
      IF(HMIN.LT.HA) LEN = 1                                            FDB  740
      CALL RFPATH(HA,HB,ANGLS1,PHI,LEN,HMIN,IAMTB,BETA1,RANGE,BENDNG)   FDB* 750
      DBETA = BETA-BETA1                                                FDB  760
      WRITE(IPR,26)  ITER,ANGLE1,BETA1,DBETA,RANGE,HMIN,PHI,BENDNG      FDB  770
   26 FORMAT(I5,3F10.4,2F10.3,2F10.4)                                   FDB  780
      IF(IFLAG.EQ.1 .AND. BETA1.LT.BETA) GO TO 9005                     FDB  790
  320 CONTINUE                                                          FDB  800
      ANGLEP = ANGLE1 -DANG                                             FDB  810
      LEN = 0                                                           FDB  820
      IF(ANGLEP.GT.90.0) LEN = 1                                        FDB  830
      CALL FNDHMN(HA,ANGLEP,HB,LEN,ITER,HMIN,PHI,IERROR)                FDB  840
      LEN = 0                                                           FDB  850
      IF(HMIN.LT.HA) LEN = 1                                            FDB  860
      CALL RFPATH(HA,HB,ANGLEP,PHI,LEN,HMIN,IAMTB,BETAP,RANGE,BENDNG)   FDB  870
      IF(ABS(BETA1-BETAP).LT.TOLRNC) GO TO 330                          FDB  880
      ITER = ITER+1                                                     FDB  890
      DC = BETAP - BETA1                                                FDB  900
C     DERIV = -DC/BETD                                                  FDB  910
       ANGLE2 = ANGLE1+(ANGLE1-ANGLEP)*(BETA-BETA1)/(BETA1-BETAP)       FDB  920
      LEN = 0                                                           FDB  930
      IF(ANGLE2.GT.90.0) LEN = 1                                        FDB  940
      ANGLS2 = ANGLE2                                                   FDB* 941
      CALL FNDHMN(HA,ANGLS2,HB,LEN,ITER,HMIN,PHI,IERROR)                FDB* 950
      LEN = 0                                                           FDB  960
      IF(HMIN.LT.HA) LEN = 1                                            FDB  970
      CALL RFPATH(HA,HB,ANGLS2,PHI,LEN,HMIN,IAMTB,BETA2,RANGE,BENDNG)   FDB* 980
      DBETA = BETA-BETA2                                                FDB  990
      WRITE(IPR,26)  ITER,ANGLE2,BETA2,DBETA,RANGE,HMIN,PHI,BENDNG      FDB 1000
      IF(BETA2.LT.BETA.AND.HMIN.LT.0.0) GO TO 9005                      FDB 1010
      ANGLE1 = ANGLE2                                                   FDB 1020
      ANGLS1 = ANGLE1                                                   FDB*1021
C                                                                       FDB 1030
      BETA1 = BETA2                                                     FDB 1040
C     BETA2 = BETA3                                                     FDB 1050
      IF(ABS(BETA-BETA2).LT.TOLRNC) GO TO 340                           FDB 1060
      IF(ITER.GT.ITERMX) GO TO 9006                                     FDB 1070
      GO TO 320                                                         FDB 1080
  330 ANGLE2 = ANGLEP                                                   FDB 1090
      ANGLS2 = ANGLE2                                                   FDB*1091
      BETA = BETAP                                                      FDB 1100
  340 CONTINUE                                                          FDB 1110
      IF(HMIN.LT.0.0) GO TO 9005                                        FDB 1120
C*****CONVERGED TO A SOLUTION                                           FDB 1130
      ANGLE = ANGLE2                                                    FDB 1140
      BETA = BETA2                                                      FDB 1150
C*****ASSIGN ANGLE AND PHI TO PROPER H1 AND H2                          FDB 1160
      IF(IORDER.EQ.1) GO TO 350                                         FDB 1170
      TEMP = PHI                                                        FDB 1180
      PHI = ANGLE                                                       FDB 1190
      ANGLE = TEMP                                                      FDB 1200
  350 CONTINUE                                                          FDB 1210
      BETAS = BETA                                                      *** 1211
      RETURN                                                            FDB 1220
C*****                                                                  FDB 1230
C*****ERROR MESSAGES                                                    FDB 1240
C*****                                                                  FDB 1250
 9005 CONTINUE                                                          FDB 1260
      WRITE(IPR,45)                                                     FDB 1270
   45 FORMAT('0FDBETA, CASE 2D(H1,H2,BETA): REFRACTED TANGENT ',        FDB 1280
     1    'HEIGHT IS LESS THAN ZERO-PATH INTERSECTS THE EARTH',         FDB 1290
     2    //,10X,'BETA IS TOO LARGE FOR THIS H1 AND H2')                FDB 1300
      GO TO 9900                                                        FDB 1310
 9006 CONTINUE                                                          FDB 1320
      WRITE(IPR,46) H1,H2,BETA,ITER,ANGLE1,BETA1,ANGLE2,BETA2           FDB 1330
C    1    ANGLE3,BETA3                                                  FDB 1340
   46 FORMAT('0FDBETA, CASE 2D (H1,H2,BETA): SOLUTION DID NOT ',        FDB 1350
     1    ' CONVERGE',//,10X,'H1 = ',F12.6,'    H2 = ',F12.6,           FDB 1360
     2    '    BETA = ',F12.6,'    ITERATIONS = ',I4,//,                FDB 1370
     3    10X,'LAST THREE ITERATIONS ',//,                              FDB 1380
     4    (10X,'ANGLE = ',F15.9,'    BETA = ',F15.9))                   FDB 1390
 9900 IERROR = 1                                                        FDB 1400
      RETURN                                                            FDB 1410
      END                                                               FDB 1420
      SUBROUTINE FNDHMN(H1,ANGLE,H2,LEN,ITER,HMIN,PHI,IERROR)           FNH  100
C***********************************************************************FNH  110
C     THIS SUBROUTINE CALCULATES THE MINIMUM ALTITUDE HMIN ALONG        FNH  120
C     THE REFRACTED PATH AND THE FINAL ZENITH ANGLE PHI.                FNH  130
C     THE PARAMETER LEN INDICATES WHETHER THE PATH GOES THROUGH         FNH  140
C     A TANGENT HEIGHT (LEN=1) OR NOT (LEN=0).  IF ANGLE > 90 AND       FNH  150
C     H1 > H2, THEN LEN CAN EITHER BE 1 OR 0, AND THE CHOICE IS         FNH  160
C     LEFT TO THE USER.                                                 FNH  170
C     THE (INDEX OF REFRACTION - 1.0) IS MODELED AS AN EXPONENTIAL      FNH  180
C     BETWEEN THE LAYER BOUNDARIES, WITH A SCALE HEIGHT SH AND AN       FNH  190
C     AMOUNT AT THE GROUND GAMMA.                                       FNH  200
C     CPATH IS THE REFRACTIVE CONSTANT FOR THIS PATH AND                FNH  210
C     EQUALS  INDEX(H1)*(RE+H1)*SIN(ANGLE).                             FNH  220
C***********************************************************************FNH  230
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                FNH  240
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              FNH  250
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     FNH  260
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               FNH  270
      DATA DH/1.0/,ETA/5.0E-8/                                          FNH  280
C*****ETA MAY BE TOO SMALL FOR SOME COMPUTERS. TRY 1.0E-7 FOR 32 BIT    FNH  290
C*****WORD MACHINES                                                     FNH  300
C     CRFRCT IS REFRACTIVE CONSTANT FOR THE PATH                        FNH  310
C                                                                       FNH  320
      CRFRCT(H) = (RE+H)*ANDEX(H,SH,GAMMA)                              FNH  330
      N = 0                                                             FNH  340
      CALL FINDSH(H1,SH,GAMMA)                                          FNH  350
      CPATH = CRFRCT(H1)*SIN(ANGLE/DEG)                                 FNH  360
      CALL FINDSH(H2,SH,GAMMA)                                          FNH  370
      CH2 = CRFRCT(H2)                                                  FNH  380
      IF(ABS(CPATH/CH2).GT.1.0) GO TO 200                               FNH  390
      IF(ANGLE.GT.90.0)  GO TO 100                                      FNH  400
      LEN = 0                                                           FNH  410
      HMIN = H1                                                         FNH  420
      GO TO 160                                                         FNH  430
  100 CONTINUE                                                          FNH  440
      IF(H1.LE.H2)  LEN = 1                                             FNH  450
      IF(LEN.EQ.1)  GO TO 110                                           FNH  460
      LEN = 0                                                           FNH  470
      HMIN = H2                                                         FNH  480
      GO TO 160                                                         FNH  490
  110 CONTINUE                                                          FNH  500
C*****LONG PATH THROUGH A TANGENT HEIGHT.                               FNH  510
C*****SOLVE ITERATIVELY FOR THE TANGENT HEIGHT HT.                      FNH  520
C*****HT IS THE HEIGHT FOR WHICH  INDEX(HT)*(RE+HT) = CPATH.            FNH  530
      CALL FINDSH(0.0,SH,GAMMA)                                         FNH  540
      CMIN = CRFRCT(0.0)                                                FNH  550
C*****FOR BETA CASES (ITER>0), ALLOW FOR HT < 0.0                       FNH  560
      IF(ITER.EQ.0 .AND. CPATH.LT.CMIN)  GO TO 150                      FNH  570
      HT1 = (RE+H1)*SIN(ANGLE/DEG)-RE                                   FNH  580
      CALL FINDSH(HT1,SH,GAMMA)                                         FNH  590
      CT1 = CRFRCT(HT1)                                                 FNH  600
      HT2 = HT1-DH                                                      FNH  610
      CALL FINDSH(HT2,SH,GAMMA)                                         FNH  620
      CT2 = CRFRCT(HT2)                                                 FNH  630
C*****ITERATE TO FIND HT                                                FNH  640
      N = 2                                                             FNH  650
  120 CONTINUE                                                          FNH  660
      IF(CT2 .EQ. CT1) GO TO 125                                        FNH  670
      N = N+1                                                           FNH  680
      HT3 = HT2+(HT2-HT1)*(CPATH-CT2)/(CT2-CT1)                         FNH  690
      CALL FINDSH(HT3,SH,GAMMA)                                         FNH  700
      CT3 = CRFRCT(HT3)                                                 FNH  710
      DC = CPATH-CT3                                                    FNH  720
      IF(ABS((CPATH-CT3)/CPATH).LT.ETA)  GO TO 130                      FNH  730
      IF(N.GT.15) GO TO 210                                             FNH  740
      HT1 = HT2                                                         FNH  750
      CT1 = CT2                                                         FNH  760
      HT2 = HT3                                                         FNH  770
      CT2 = CT3                                                         FNH  780
      GO TO 120                                                         FNH  790
  125 HT3 = HT2                                                         FNH  800
  130 CONTINUE                                                          FNH  810
      HT = HT3                                                          FNH  820
      HMIN = HT                                                         FNH  830
      GO TO 160                                                         FNH  840
  150 CONTINUE                                                          FNH  850
      IF(ISSGEO.EQ.0) GO TO 155                                         FNH  860
      IERROR=-5.                                                        FNH  870
      RETURN                                                            FNH  880
155   CONTINUE                                                          FNH  890
C*****TANGENT PATH INTERSECTS EARTH                                     FNH  900
      H2 = 0.0                                                          FNH  910
      HMIN = 0.0                                                        FNH  920
      LEN = 0                                                           FNH  930
      CH2 = CMIN                                                        FNH  940
      IF(ISSGEO.NE.1)WRITE(IPR,22) H1,ANGLE                             FNH  950
   22 FORMAT(///,' TANGENT PATH WITH H1 = ',F10.3,' AND ANGLE = ',      FNH  960
     1    F10.3,' INTERSECTS THE EARTH',//,10X,'H2 HAS BEEN RESET ',    FNH  970
     2    'TO 0.0 AND LEN TO 0')                                        FNH  980
  160 CONTINUE                                                          FNH  990
C*****CALCULATE THE ZENITH ANGLE PHI AT H2                              FNH 1000
      PHI = ASIN(CPATH/CH2)*DEG                                         FNH 1010
      IF(ANGLE.LE.90.0 .OR. LEN.EQ.1)  PHI = 180.0-PHI                  FNH 1020
      RETURN                                                            FNH 1030
C*****H2 LT TANGENT HEIGHT FOR THIS H1 AND ANGLE                        FNH 1040
  200 CONTINUE                                                          FNH 1050
      WRITE(IPR,20)                                                     FNH 1060
   20 FORMAT('0H2 IS LESS THAN THE TANGENT HEIGHT FOR THIS PATH ',      FNH 1070
     1    'AND CANNOT BE REACHED')                                      FNH 1080
      IERROR = 2                                                        FNH 1090
      RETURN                                                            FNH 1100
  210 CONTINUE                                                          FNH 1110
      DC = CPATH-CT3                                                    FNH 1120
      WRITE(IPR,24)  N,CPATH,CT3,DC,HT3                                 FNH 1130
   24 FORMAT(///,'0FROM SUBROUTINE FNDHMN :',//,                        FNH 1140
     1    10X,'THE PROCEEDURE TO FIND THE TANGENT HEIGHT DID NOT ',     FNH 1150
     2    'CONVERG AFTER ',I3,'  ITERATIONS',//,                        FNH 1160
     3    10X,'CPATH   = ',F12.5,' KM',//,10X,'CT3     = ',F12.5,' KM', FNH 1170
     4    //,10X,'DC      = ',E12.3,' KM',//,                           FNH 1180
     5    10X,'HT3     = ',F12.5,' KM')                                 FNH 1190
      STOP 20                                                           FNH 1200
      END                                                               FNH 1210
      SUBROUTINE FINDSH(H,SH,GAMMA)                                     FIN  100
C*****GIVEN AN ALTITUDE H, THIS SUBROUTINE FINDS THE LAYER BOUNDARIES   FIN  110
C*****ZM(I1) AND ZM(I2) WHICH CONTAIN H,  THEN CALCULATES THE SCALE     FIN  120
C*****HEIGHT (SH) AND THE VALUE AT THE GROUND (GAMMA+1) FOR THE         FIN  130
C*****INDEX OF REFRACTION                                               FIN  140
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              FIN  150
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     FIN  160
      COMMON /MODEL/ Z(34),P(34),T(34),RFNDX(34),DENSTY(63,34),         FIN  170
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         FIN  180
      DO 100 IM=2,IMOD                                                  FIN  190
      I2 = IM                                                           FIN  200
      IF(Z(IM).GE.H)  GO TO 110                                         FIN  210
  100 CONTINUE                                                          FIN  220
      I2 = IMOD                                                         FIN  230
  110 CONTINUE                                                          FIN  240
      I1 = I2-1                                                         FIN  250
      CALL SCALHT(Z(I1),Z(I2),RFNDX(I1),RFNDX(I2),SH,GAMMA)             FIN  260
      RETURN                                                            FIN  270
      END                                                               FIN  280
      SUBROUTINE SCALHT(Z1,Z2,RFNDX1,RFNDX2,SH,GAMMA)                   SCA  100
C***********************************************************************SCA  110
C     THIS SUBROUTINE CALCULATES THE SCALE HEIGHT SH OF THE (INDEX OF   SCA  120
C     REFRACTION-1.0) FROM THE VALUES OF THE INDEX AT THE ALTITUDES Z1  SCA  130
C     AND Z2 ( Z1 < Z2). IT ALSO CALCULATES THE EXTRAPOLATED VALUE      SCA  140
C     GAMMA OF THE (INDEX-1.0) AT Z = 0.0                               SCA  150
C***********************************************************************SCA  160
      RF1 = RFNDX1+1.0E-20                                              SCA  170
      RF2 = RFNDX2+1.0E-20                                              SCA  180
      RATIO = RF1/RF2                                                   SCA  190
      IF(ABS(RATIO-1.0).LT.1.0E-05)  GO TO 100                          SCA  200
      SH = (Z2-Z1)/ALOG(RATIO)                                          SCA  210
      GAMMA = RF1*(RF2/RF1)**(-Z1/(Z2-Z1))                              SCA  220
      GO TO 110                                                         SCA  230
  100 CONTINUE                                                          SCA  240
C*****THE VARIATION IN THE INDEX OF REFRACTION WITH HEIGHT IS           SCA  250
C*****INSIGNIFICANT OR ZERO                                             SCA  260
      SH = 0.0                                                          SCA  270
      GAMMA = RFNDX1                                                    SCA  280
  110 CONTINUE                                                          SCA  290
      RETURN                                                            SCA  300
      END                                                               SCA  310
      FUNCTION   RADREF(H,SH,GAMMA)                                     RDF  100
C***********************************************************************RDF  110
C     COMPUTES THE RADIUS OF CURVATURE OF THE REFRACTED RAY FOR         RDF  120
C     HORIZONTAL PATH:  RADREF = ANDEX/ D(ANDEX)/D(RADIUS)              RDF  130
C***********************************************************************RDF  140
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     RDF  150
      IF(SH.EQ.0.0) GO TO 20                                            RDF  160
      HSH = H/SH                                                        RDF  170
      IF(HSH .GT. BIGEXP) GO TO 20                                      RDF  180
      RADREF = SH*(1.0+EXP(HSH )/GAMMA)                                 RDF  190
      RETURN                                                            RDF  200
   20 RADREF = BIGNUM                                                   RDF  210
      RETURN                                                            RDF  220
      END                                                               RDF  230
      SUBROUTINE RFPATH(H1,H2,ANGLE,PHI,LEN,HMIN,IAMT,BETA,RANGE,BENDNG)RFP  100
C***********************************************************************RFP  110
C     THIS SUBROUTINE  TRACES THE REFRACTED RAY FROM H1 WITH A          RFP  120
C     INITIAL ZENITH ANGLE ANGLE TO H2 WHERE THE ZENITH ANGLE IS PHI,   RFP  130
C     AND CALCULATES THE ABSORBER AMOUNTS (IF IAMT.EQ.1) ALONG          RFP  140
C     THE PATH.  IT STARTS FROM THE LOWEST POINT ALONG THE PATH         RFP  150
C     (THE TANGENT HEIGHT HMIN IF LEN = 1 OR HA = MIN(H1,H2) IF LEN = 0)RFP  160
C     AND PROCEEDS TO THE HIGHEST POINT.  BETA AND RANGE ARE THE        RFP  170
C     EARTH CENTERED ANGLE AND THE TOTAL DISTANCE RESPECTIVELY          RFP  180
C     FOR THE REFRACTED PATH FROM H1 TO H2                              RFP  190
C***********************************************************************RFP  200
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                RFP  210
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  RFP  220
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              RFP  230
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     RFP  240
      COMMON /RFRPTH/ ZP(35),PP(35),TP(35),RFNDXP(35),SP(35),           RFP  250
     1    PPSUM(35),TPSUM(35),RHOPSM(35),DENP(63,35),AMTP(63,35)        RFP  260
      COMMON /SOLS/ AH1(68),ARH(68),                                    RFP  270
     X   WPATHS(68,63),PA(68),PR(68),ATHETA(35),ADBETA(35),LJ(69),JTURN,RFP  280
     X ANGSUN,CSZEN(68)                                                 RFP  290
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               RFP  300
      COMMON /PATH/ PL(68),QTHETA(68),ITEST,HI,HF,AHT(68)               RFP  310
      DIMENSION HLOW(2)                                                 RFP  320
      CHARACTER*2 HLOW                                                  RFP  330
      DATA HLOW/'H1','H2'/                                              RFP  340
      MAX = 0                                                           RFP  350
      IF(H1.GT.H2) GO TO 90                                             RFP  360
      IORDER = 1                                                        RFP  370
      HA = H1                                                           RFP  380
      HB = H2                                                           RFP  390
      ANGLEA = ANGLE                                                    RFP  400
      GO TO 95                                                          RFP  410
   90 CONTINUE                                                          RFP  420
      IORDER = -1                                                       RFP  430
      HA = H2                                                           RFP  440
      HB = H1                                                           RFP  450
      ANGLEA = PHI                                                      RFP  460
   95 CONTINUE                                                          RFP  470
      JNEXT = 1                                                         RFP  480
      IF(IAMT.EQ.1 .AND. NPR.NE.1)  WRITE(IPR,20)                       RFP  490
   20 FORMAT('1CALCULATION OF THE REFRACTED PATH THROUGH THE ',         RFP  500
     1    'ATMOSPHERE',///,                                             RFP  510
     4    T3,'I',T11,'ALTITUDE',T27,'THETA',T34,'DRANGE',T44,'RANGE',   RFP  520
     5    T53,'DBETA',T63,'BETA',T72,'PHI',T80,'DBEND',T87,'BENDING',   RFP  530
     6    T 98,'PBAR',T106,'TBAR',T113,'RHOBAR',/,                      RFP  540
     7    T07,'FROM',T18,'TO',/,T07,'(KM)',T17,'(KM)',T27,'(DEG)',      RFP  550
     8    T36,'(KM)',T45,'(KM)',T53,'(DEG)',T62,'(DEG)',T71,'(DEG)',    RFP  560
     9    T80,'(DEG)',T89,'(DEG)',T098,'(MB)',T106,'(K)',               RFP  570
     1    T111,'(GM CM-3)',/)                                           RFP  580
      IF(LEN.EQ.0) GO TO 100                                            RFP  590
C*****LONG PATH: FILL IN THE SYMETRIC PART FROM THE TANGENT HEIGHT      RFP  600
C*****TO HA                                                             RFP  610
      CALL FILL(HMIN,HA,JNEXT)                                          RFP  620
      JHA = JNEXT                                                       RFP  630
  100 CONTINUE                                                          RFP  640
C*****FILL IN THE REMAINING PATH FROM HA TO HB                          RFP  650
      IF(HA.EQ.HB) GO TO 110                                            RFP  660
      CALL FILL(HA,HB,JNEXT)                                            RFP  670
  110 CONTINUE                                                          RFP  680
      JMAX = JNEXT                                                      RFP  690
      IPATH = JMAX                                                      RFP  700
C*****INTEGRATE EACH SEGMENT OF THE PATH                                RFP  710
C*****CALCULATE CPATH SEPERATELY FOR LEN = 0,1                          RFP  720
      IF(LEN.EQ.1) GO TO 115                                            RFP  730
      CALL FINDSH(HA,SH,GAMMA)                                          RFP  740
      CPATH = (RE+HA)*ANDEX(HA,SH,GAMMA)*SIN(ANGLEA/DEG)                RFP  750
      GO TO 116                                                         RFP  760
  115 CONTINUE                                                          RFP  770
      CALL FINDSH(HMIN,SH,GAMMA)                                        RFP  780
      CPATH = (RE+HMIN)*ANDEX(HMIN,SH,GAMMA)                            RFP  790
  116 CONTINUE                                                          RFP  800
      BETA = 0.0                                                        RFP  810
      S = 0.0                                                           RFP  820
      BENDNG = 0.0                                                      RFP  830
      IF(LEN.EQ.0) GO TO 140                                            RFP  840
C*****DO SYMETRIC PART, FROM TANGENT HEIGHT(HMIN) TO HA                 RFP  850
      IHLOW = 1                                                         RFP  860
      IF(IORDER.EQ.-1)  IHLOW = 2                                       RFP  870
      IF(IAMT.EQ.1 .AND. NPR.NE.1) WRITE(IPR,24) HLOW(IHLOW)            RFP  880
   24 FORMAT(' ',T7,'TANGENT',T17,A2,/,T7,'HEIGHT',/)                   RFP  890
      SINAI = 1.0                                                       RFP  900
      COSAI = 0.0                                                       RFP  910
      THETA = 90.0                                                      RFP  920
      J2 = JHA-1                                                        RFP  930
      DO 120 J=1,J2                                                     RFP  940
      CALL SCALHT(ZP(J),ZP(J+1),RFNDXP(J),RFNDXP(J+1),SH,GAMMA)         RFP  950
      CALL LAYER(J,SINAI,COSAI,CPATH,SH,GAMMA,IAMT,DS,DBEND)            RFP  960
      DBEND = DBEND*DEG                                                 RFP  970
      PHI = ASIN(SINAI)*DEG                                             RFP  980
      DBETA = THETA-PHI+DBEND                                           RFP  990
      PHI = 180.0-PHI                                                   RFP 1000
      S = S+DS                                                          RFP 1010
C                                                                       RFP 1020
C   SAVE REFRACTED RAY PATH LENGTH FOR USE WITH MULTIPLE SCATTERING     RFP 1030
C                                                                       RFP 1040
      BENDNG = BENDNG+DBEND                                             RFP 1050
      BETA = BETA+DBETA                                                 RFP 1060
      IF(IAMT.NE.1) GO TO 118                                           RFP 1070
      PBAR = PPSUM(J)/RHOPSM(J)                                         RFP 1080
      TBAR = TPSUM(J)/RHOPSM(J)                                         RFP 1090
      RHOBAR = RHOPSM(J)/DS                                             RFP 1100
      IF(IAMT.EQ.1 .AND. NPR.NE.1) WRITE(IPR,22) J,ZP(J),ZP(J+1),       RFP 1110
     1    THETA,DS,S,DBETA,BETA,PHI,DBEND,BENDNG,PBAR,TBAR,RHOBAR       RFP 1120
      J2A = J2 - J +  1                                                 RFP 1130
      J2B = J2 + J                                                      RFP 1140
      QTHETA(J2A)=  PHI  *CA                                            RFP 1150
      QTHETA(J2B) = THETA*CA                                            RFP 1160
      MAX = J2B                                                         RFP 1170
   22 FORMAT(' ',I2,2F9.3,9F9.3,F8.2,1PE9.2)                            RFP 1180
  118 CONTINUE                                                          RFP 1190
      IF(ISSGEO.EQ.1) GO TO 119                                         RFP 1200
      ATHETA(J)=THETA                                                   RFP 1210
      ADBETA(J)=DBETA                                                   RFP 1220
  119 CONTINUE                                                          RFP 1230
      THETA = 180.0-PHI                                                 RFP 1240
  120 CONTINUE                                                          RFP 1250
C*****DOUBLE PATH QUANTITIES FOR THE OTHER PART OF THE SYMETRIC PATH    RFP 1260
      BENDNG = 2.0*BENDNG                                               RFP 1270
      BETA = 2.0*BETA                                                   RFP 1280
      S = 2.0*S                                                         RFP 1290
      IF(IAMT.EQ.1 .AND. NPR.NE.1) WRITE(IPR,26) S,BETA,BENDNG          RFP 1300
   26 FORMAT('0',T10,'DOUBLE RANGE, BETA, BENDING',/,                   RFP 1310
     1    T10,'FOR SYMMETRIC PART OF PATH',T40,F9.3,T58,F9.3,           RFP 1320
     2    T85,F9.3,/)                                                   RFP 1330
      JNEXT = JHA                                                       RFP 1340
      GO TO 150                                                         RFP 1350
  140 CONTINUE                                                          RFP 1360
C*****SHORT PATH                                                        RFP 1370
      JNEXT = 1                                                         RFP 1380
C*****ANGLEA IS THE ZENITH ANGLE AT HA IN DEG                           RFP 1390
C*****SINAI IS SIN OF THE INCIDENCE ANGLE                               RFP 1400
C*****COSAI IS CARRIED SEPERATELY TO AVOID A PRECISION PROBLEM          RFP 1410
C*****WHEN SINAI IS CLOSE TO 1.0                                        RFP 1420
      THETA = ANGLEA                                                    RFP 1430
      IF(ANGLEA.GT.45.0) GO TO 145                                      RFP 1440
      SINAI = SIN(ANGLEA/DEG)                                           RFP 1450
      COSAI = -COS(ANGLEA/DEG)                                          RFP 1460
      GO TO 150                                                         RFP 1470
  145 CONTINUE                                                          RFP 1480
      SINAI = COS((90.0-ANGLEA)/DEG)                                    RFP 1490
      COSAI = -SIN((90.0-ANGLEA)/DEG)                                   RFP 1500
  150 CONTINUE                                                          RFP 1510
C*****DO PATH FROM HA TO HB                                             RFP 1520
      IF(HA.EQ.HB) GO TO 170                                            RFP 1530
      JO = MAX - JNEXT + 1                                              RFP 1540
      J1 = JNEXT                                                        RFP 1550
      J2 = JMAX-1                                                       RFP 1560
C                                                                       RFP 1570
      IF(H1 .GT. H2) THEN                                               RFP 1580
      JD =  J2 - J1 + 1                                                 RFP 1590
      DO  1158 J = 1,MAX                                                RFP 1600
      KA = MAX - J + 1                                                  RFP 1610
      JA = KA + JD                                                      RFP 1620
      QTHETA(JA) = QTHETA(KA)                                           RFP 1630
1158  CONTINUE                                                          RFP 1640
      MAX =  MAX + JD                                                   RFP 1650
      JO = 0                                                            RFP 1660
      ENDIF                                                             RFP 1670
C                                                                       RFP 1680
      IHLOW = 1                                                         RFP 1690
      IF(IORDER.EQ.-1)  IHLOW = 2                                       RFP 1700
      IHIGH = MOD(IHLOW,2)+1                                            RFP 1710
      IF(IAMT.EQ.1 .AND. NPR.NE.1) WRITE(IPR,28) HLOW(IHLOW),           RFP 1720
     1    HLOW(IHIGH)                                                   RFP 1730
   28 FORMAT(' ',T11,A2,' TO ',A2,/)                                    RFP 1740
      DO 160 J=J1,J2                                                    RFP 1750
      CALL SCALHT(ZP(J),ZP(J+1),RFNDXP(J),RFNDXP(J+1),SH,GAMMA)         RFP 1760
      CALL LAYER(J,SINAI,COSAI,CPATH,SH,GAMMA,IAMT,DS,DBEND)            RFP 1770
      DBEND = DBEND*DEG                                                 RFP 1780
      PHI = ASIN(SINAI)*DEG                                             RFP 1790
      DBETA = THETA-PHI+DBEND                                           RFP 1800
      PHI = 180.0-PHI                                                   RFP 1810
      S = S+DS                                                          RFP 1820
C                                                                       RFP 1830
C    SAVE LAYER REFRACTED PATH LENGTH FOR USE WITH MULTIPLE SCATTERING  RFP 1840
C                                                                       RFP 1850
      PL(J)=DS                                                          RFP 1860
      BENDNG = BENDNG+DBEND                                             RFP 1870
      BETA = BETA+DBETA                                                 RFP 1880
      IF(IAMT.NE.1) GO TO 158                                           RFP 1890
      PBAR = PPSUM(J)/RHOPSM(J)                                         RFP 1900
      TBAR = TPSUM(J)/RHOPSM(J)                                         RFP 1910
      RHOBAR = RHOPSM(J)/DS                                             RFP 1920
      IF(IAMT.EQ.1 .AND. NPR.NE.1) WRITE(IPR,22) J,ZP(J),ZP(J+1),       RFP 1930
     1    THETA,DS,S,DBETA,BETA,PHI,DBEND,BENDNG,PBAR,TBAR,RHOBAR       RFP 1940
  158 CONTINUE                                                          RFP 1950
C                                                                       RFP 1960
C   SAVE LAYER REFRACTED PATH ANGLE FOR USE WITH MULTIPLE SCATTERING    RFP 1970
C                                                                       RFP 1980
C                                                                       RFP 1990
      IF(H2 .GT. H1)  THEN                                              RFP 2000
      QTHETA(J + JO)=THETA*CA                                           RFP 2010
      MAX  = J + JO                                                     RFP 2020
      ELSE                                                              RFP 2030
      J2B  = J2 - J + 1                                                 RFP 2040
      QTHETA(J2B)= PHI * CA                                             RFP 2050
      ENDIF                                                             RFP 2060
C                                                                       RFP 2070
      IF(ISSGEO.EQ.1) GO TO 159                                         RFP 2080
      ADBETA(J)=DBETA                                                   RFP 2090
      ATHETA(J)=THETA                                                   RFP 2100
  159 CONTINUE                                                          RFP 2110
      THETA = 180.0-PHI                                                 RFP 2120
  160 CONTINUE                                                          RFP 2130
  170 CONTINUE                                                          RFP 2140
      IF(ISSGEO.EQ.0) ATHETA(JMAX)=THETA                                RFP 2150
      IF(IORDER.EQ.-1) PHI = ANGLEA                                     RFP 2160
      RANGE = S                                                         RFP 2170
      RETURN                                                            RFP 2180
      END                                                               RFP 2190
      SUBROUTINE FILL(HA,HB,JNEXT)                                      FLL  100
C********************************************************************   FLL  110
C     THIS SUBROUTINE DEFINES THE ATMOSPHERIC BOUNDARIES OF THE PATH    FLL  120
C     FROM HA TO HB AND INTERPOLATES (EXTRAPOLATES) THE DENSITIES TO    FLL  130
C     THESE BOUNDARIES ASSUMING THE DENSITIES VARY EXPONENTIALLY        FLL  140
C     WITH HEIGHT                                                       FLL  150
C********************************************************************   FLL  160
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                FLL  170
      COMMON /MODEL/ Z(34),P(34),T(34),RFNDX(34),DENSTY(63,34),         FLL  180
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         FLL  190
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              FLL  200
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     FLL  210
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               FLL  220
      COMMON /RFRPTH/ ZP(35),PP(35),TP(35),RFNDXP(35),SP(35),           FLL  230
     1    PPSUM(35),TPSUM(35),RHOPSM(35),DENP(63,35),AMTP(63,35)        FLL  240
      IF(HA.LT.HB) GO TO 90                                             FLL  250
      WRITE(IPR,22) HA,HB,JNEXT                                         FLL  260
   22 FORMAT('0SUBROUTINE FILL- ERROR, HA .GE. HB',//,                  FLL  270
     1    10X,'HA, HB, JNEXT = ',2E25.15,I6)                            FLL  280
      STOP                                                              FLL  290
   90 CONTINUE                                                          FLL  300
C*****FIND Z(IA): THE SMALLEST Z(I).GT.HA                               FLL  310
      DO 100 I=1,IMAX                                                   FLL  320
      IF(HA.GE.Z(I)) GO TO 100                                          FLL  330
      IA = I                                                            FLL  340
      GO TO 110                                                         FLL  350
  100 CONTINUE                                                          FLL  360
      IA = IMAX+1                                                       FLL  370
      IB = IA                                                           FLL  380
      GO TO 130                                                         FLL  390
C*****FIND Z(IB): THE SMALLEST Z(I).GE.HB                               FLL  400
  110 CONTINUE                                                          FLL  410
      DO 120 I=IA,IMAX                                                  FLL  420
      IF(HB-Z(I).GT. .0001) GO TO 120                                   FLL  430
      IB = I                                                            FLL  440
      GO TO 130                                                         FLL  450
  120 CONTINUE                                                          FLL  460
      IB = IMAX+1                                                       FLL  470
  130 CONTINUE                                                          FLL  480
C*****INTERPOLATE DENSITIES TO HA, HB                                   FLL  490
      ZP(JNEXT) = HA                                                    FLL  500
      I2 = IA                                                           FLL  510
      IF(I2.EQ.1) I2 = 2                                                FLL  520
      IF(I2.GT.IMAX) I2 = IMAX                                          FLL  530
      I1 = I2-1                                                         FLL  540
      A = (HA-Z(I1))/(Z(I2)-Z(I1))                                      FLL  550
      CALL EXPINT(PP(JNEXT),P(I1),P(I2),A)                              FLL  560
      TP(JNEXT) = T(I1)+(T(I2)-T(I1))*A                                 FLL  570
      CALL EXPINT(RFNDXP(JNEXT),RFNDX(I1),RFNDX(I2),A)                  FLL  580
      DO 140 K=1,KMAX                                                   FLL  590
      CALL EXPINT(DENP(K,JNEXT),DENSTY(K,I1),DENSTY(K,I2),A)            FLL  600
  140 CONTINUE                                                          FLL  610
      IF(IA.EQ.IB) GO TO 160                                            FLL  620
C*****FILL IN DENSITIES BETWEEN HA AND HB                               FLL  630
      I1 = IA                                                           FLL  640
      I2 = IB-1                                                         FLL  650
      DO 150 I=I1,I2                                                    FLL  660
      JNEXT = JNEXT+1                                                   FLL  670
      ZP(JNEXT) = Z(I)                                                  FLL  680
      PP(JNEXT) = P(I)                                                  FLL  690
      TP(JNEXT) = T(I)                                                  FLL  700
      RFNDXP(JNEXT) = RFNDX(I)                                          FLL  710
      DO 150 K=1,KMAX                                                   FLL  720
      DENP(K,JNEXT) = DENSTY(K,I)                                       FLL  730
  150 CONTINUE                                                          FLL  740
  160 CONTINUE                                                          FLL  750
C*****INTERPOLATE THE DENSITIES TO HB                                   FLL  760
      JNEXT = JNEXT+1                                                   FLL  770
      ZP(JNEXT) = HB                                                    FLL  780
      I2 = IB                                                           FLL  790
      IF(I2.EQ.1) I2 = 2                                                FLL  800
      IF(I2.GT.IMAX) I2 = IMAX                                          FLL  810
      I1 = I2-1                                                         FLL  820
      A = (HB-Z(I1))/(Z(I2)-Z(I1))                                      FLL  830
      CALL EXPINT(PP(JNEXT),P(I1),P(I2),A)                              FLL  840
      TP(JNEXT) = T(I1)+(T(I2)-T(I1))*A                                 FLL  850
      CALL EXPINT(RFNDXP(JNEXT),RFNDX(I1),RFNDX(I2),A)                  FLL  860
      DO 170 K=1,KMAX                                                   FLL  870
      CALL EXPINT(DENP(K,JNEXT),DENSTY(K,I1),DENSTY(K,I2),A)            FLL  880
  170 CONTINUE                                                          FLL  890
      RETURN                                                            FLL  900
      END                                                               FLL  910
      SUBROUTINE LAYER(J,SINAI,COSAI,CPATH,SH,GAMMA,IAMT,S,BEND)        LYR  100
C*****************************************************************      LYR  110
C     THIS SUBROUTINE CALCULATES THE REFRACTED PATH FROM Z1 TO Z2       LYR  120
C     WITH THE SIN OF THE INITIAL INCIDENCE ANGLE SINAI                 LYR  130
C*****************************************************************      LYR  140
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              LYR  150
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     LYR  160
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               LYR  170
      COMMON /RFRPTH/ ZP(35),PP(35),TP(35),RFNDXP(35),SP(35),           LYR  180
     1    PPSUM(35),TPSUM(35),RHOPSM(35),DENP(63,35),AMTP(63,35)        LYR  190
      DIMENSION HDEN(63),DENA(63),DENB(63)                              LYR  200
      DATA EPSILN/1.0E-5/                                               LYR  210
C*****INITIALIZE LOOP                                                   LYR  220
      N = 0                                                             LYR  230
      Z1 = ZP(J)                                                        LYR  240
      Z2 = ZP(J+1)                                                      LYR  250
      H1 = Z1                                                           LYR  260
      R1 = RE+H1                                                        LYR  270
      DHMIN = DELTAS**2/(2.0*R1)                                        LYR  280
      SINAI1 = SINAI                                                    LYR  290
      COSAI1 = COSAI                                                    LYR  300
      Y1 = COSAI1**2/2.0+COSAI1**4/8.0+COSAI1**6*3.0/48.0               LYR  310
      Y3 = 0.0                                                          LYR  320
      X1 = -R1*COSAI1                                                   LYR  330
      RATIO1 = R1/RADREF(H1,SH,GAMMA)                                   LYR  340
      DSDX1 = 1.0/(1.0-RATIO1*SINAI1**2)                                LYR  350
      DBNDX1 = DSDX1*SINAI1*RATIO1/R1                                   LYR  360
      S = 0.0                                                           LYR  370
      BEND = 0.0                                                        LYR  380
      IF(IAMT.EQ.2) GO TO 110                                           LYR  390
C*****INITIALIZE THE VARIABLES FOR THE CALCULATION OF THE               LYR  400
C*****ABSORBER AMOUNTS                                                  LYR  410
      PA = PP(J)                                                        LYR  420
      PB = PP(J+1)                                                      LYR  430
      TA = TP(J)                                                        LYR  440
      TB = TP(J+1)                                                      LYR  450
      RHOA = PA/(GCAIR*TA)                                              LYR  460
      RHOB = PB/(GCAIR*TB)                                              LYR  470
      DZ = ZP(J+1)-ZP(J)                                                LYR  480
      HP = -DZ/ALOG(PB/PA)                                              LYR  490
      IF(ABS(RHOB/RHOA-1.0).LT.EPSILN)  GO TO 90                        LYR  500
      HRHO = -DZ/ALOG(RHOB/RHOA)                                        LYR  510
      GO TO 95                                                          LYR  520
   90 HRHO = 1.0E30                                                     LYR  530
   95 CONTINUE                                                          LYR  540
      DO 105 K=1,KMAX                                                   LYR  550
      DENA(K) = DENP(K,J)                                               LYR  560
      DENB(K) = DENP(K,J+1)                                             LYR  570
      IF(DENA(K).LE.0.0 .OR. DENB(K).LE.0.0)  GO TO 100                 LYR  580
      IF(ABS(1.0-DENA(K)/DENB(K)).LE.EPSILN)  GO TO 100                 LYR  590
C*****USE EXPONENTIAL INTERPOLATION                                     LYR  600
      HDEN(K) = -DZ/ALOG(DENB(K)/DENA(K))                               LYR  610
      GO TO 105                                                         LYR  620
C*****USE LINEAR INTERPOLATION                                          LYR  630
  100 HDEN(K) = 0.0                                                     LYR  640
  105 CONTINUE                                                          LYR  650
  110 CONTINUE                                                          LYR  660
C*****                                                                  LYR  670
C*****LOOP THROUGH PATH                                                 LYR  680
C*****INTEGRATE PATH QUANTITIES USING QUADRATIC INTEGRATION WITH        LYR  690
C*****UNEQUALLY SPACED POINTS                                           LYR  700
C*****                                                                  LYR  710
  115 CONTINUE                                                          LYR  720
      N = N+1                                                           LYR  730
      DH = -DELTAS*COSAI1                                               LYR  740
      IF(DH.LT.DHMIN) DH = DHMIN                                        LYR  750
      H3 = H1+DH                                                        LYR  760
      IF(H3.GT.Z2) H3 = Z2                                              LYR  770
      DH = H3-H1                                                        LYR  780
      R3 = RE+H3                                                        LYR  790
      H2 = H1+DH/2.0                                                    LYR  800
      R2 = RE+H2                                                        LYR  810
      SINAI2 = CPATH/(ANDEX(H2,SH,GAMMA)*R2)                            LYR  820
      SINAI3 = CPATH/(ANDEX(H3,SH,GAMMA)*R3)                            LYR  830
      RATIO2 = R2/RADREF(H2,SH,GAMMA)                                   LYR  840
      RATIO3 = R3/RADREF(H3,SH,GAMMA)                                   LYR  850
      IF((1.0-SINAI2).GT.EPSILN)  GO TO 116                             LYR  860
C*****NEAR A TANGENT HEIGHT, COSAI = -SQRT(1-SINAI**2) LOSES            LYR  870
C*****PRECISION. USE THE FOLLOWING ALGORITHM TO GET COSAI.              LYR  880
      Y3 = Y1+(SINAI1*(1.0-RATIO1)/R1+4.0*SINAI2*(1.0-RATIO2)/R2+       LYR  890
     1    SINAI3*(1.0-RATIO3)/R3)*DH/6.0                                LYR  900
      COSAI3 = -SQRT(2.0*Y3-Y3**2)                                      LYR  910
      X3 = -R3*COSAI3                                                   LYR  920
      DX = X3-X1                                                        LYR  930
      W1 = 0.5*DX                                                       LYR  940
      W2 = 0.0                                                          LYR  950
      W3 = 0.5*DX                                                       LYR  960
      GO TO 118                                                         LYR  970
C*****                                                                  LYR  980
  116 CONTINUE                                                          LYR  990
      COSAI2 = -SQRT(1.0-SINAI2**2)                                     LYR 1000
      COSAI3 = -SQRT(1.0-SINAI3**2)                                     LYR 1010
      X2 = -R2*COSAI2                                                   LYR 1020
      X3 = -R3*COSAI3                                                   LYR 1030
C*****CALCULATE WEIGHTS                                                 LYR 1040
      D31 = X3-X1                                                       LYR 1050
      D32 = X3-X2                                                       LYR 1060
      D21 = X2-X1                                                       LYR 1070
      IF(D32.EQ.0.0 .OR. D21.EQ.0.0) GO TO 117                          LYR 1080
      W1 = (2-D32/D21)*D31/6.0                                          LYR 1090
      W2 = D31**3/(D32*D21*6.0)                                         LYR 1100
      W3 = (2.0-D21/D32)*D31/6.0                                        LYR 1110
      GO TO 118                                                         LYR 1120
  117 CONTINUE                                                          LYR 1130
      W1 = 0.5*D31                                                      LYR 1140
      W2 = 0.0                                                          LYR 1150
      W3 = 0.5*D31                                                      LYR 1160
C*****                                                                  LYR 1170
  118 CONTINUE                                                          LYR 1180
      DSDX2 = 1.0/(1.0-RATIO2*SINAI2**2)                                LYR 1190
      DSDX3 = 1.0/(1.0-RATIO3*SINAI3**2)                                LYR 1200
      DBNDX2 = DSDX2*SINAI2*RATIO2/R2                                   LYR 1210
      DBNDX3 = DSDX3*SINAI3*RATIO3/R3                                   LYR 1220
C*****INTEGRATE                                                         LYR 1230
      DS = W1*DSDX1+W2*DSDX2+W3*DSDX3                                   LYR 1240
      S = S+DS                                                          LYR 1250
      DBEND = W1*DBNDX1+W2*DBNDX2+W3*DBNDX3                             LYR 1260
      BEND = BEND+DBEND                                                 LYR 1270
      IF(IAMT.EQ.2)  GO TO 150                                          LYR 1280
C*****CALCULATE AMOUNTS                                                 LYR 1290
      DSDZ = DS/DH                                                      LYR 1300
      DHHP = DH/HP                                                      LYR 1310
      IF(DHHP .LE. BIGEXP) THEN                                         LYR 1320
           PB = PA*EXP(-DHHP )                                          LYR 1330
      ELSE                                                              LYR 1340
           PB = 0.                                                      LYR 1350
      ENDIF                                                             LYR 1360
      DHRH = DH/HRHO                                                    LYR 1370
      IF(DHRH .LE. BIGEXP) THEN                                         LYR 1380
           RHOB = RHOA*EXP(-DHRH   )                                    LYR 1390
      ELSE                                                              LYR 1400
           RHOB = 0.                                                    LYR 1410
      ENDIF                                                             LYR 1420
      IF((DH/HRHO).LT.EPSILN)  GO TO 120                                LYR 1430
      PPSUM(J) = PPSUM(J)+DSDZ*(HP/(1.0+HP/HRHO))*(PA*RHOA-PB*RHOB)     LYR 1440
      TPSUM(J) = TPSUM(J)+DSDZ*HP*(PA-PB)/GCAIR                         LYR 1450
      RHOPSM(J) = RHOPSM(J)+DSDZ*HRHO*(RHOA-RHOB)                       LYR 1460
      GO TO 125                                                         LYR 1470
  120 CONTINUE                                                          LYR 1480
      PPSUM(J) = PPSUM(J)+0.5*DS*(PA*RHOA+PB*RHOB)                      LYR 1490
      TPSUM(J) = TPSUM(J)+0.5*DS*(PA+PB)/GCAIR                          LYR 1500
      RHOPSM(J) = RHOPSM(J)+0.5*DS*(RHOA+RHOB)                          LYR 1510
  125 CONTINUE                                                          LYR 1520
      DO 140 K=1,KMAX                                                   LYR 1530
      IF(ABS(HDEN(K)).EQ.0.0)GO TO 130                                  LYR 1540
      IF((DH/HDEN(K)).LT.EPSILN) GO TO 130                              LYR 1550
C*****EXPONENTIAL INTERPOLATION                                         LYR 1560
      H3Z1 =  (H3-Z1)/HDEN(K)                                           LYR 1570
      IF(H3Z1 .LE.BIGEXP) THEN                                          LYR 1580
           DENB(K) = DENP(K,J)*EXP(- H3Z1 )                             LYR 1590
      ELSE                                                              LYR 1600
           DENB(K) = 0.                                                 LYR 1610
      ENDIF                                                             LYR 1620
      AMTP(K,J) = AMTP(K,J)+DSDZ*HDEN(K)*(DENA(K)-DENB(K))              LYR 1630
      GO TO 140                                                         LYR 1640
  130 CONTINUE                                                          LYR 1650
C*****LINEAR INTERPOLATION                                              LYR 1660
      DENB(K) = DENP(K,J)+(DENP(K,J+1)-DENP(K,J))*(H3-Z1)/DZ            LYR 1670
      AMTP(K,J) = AMTP(K,J)+0.5*(DENA(K)+DENB(K))*DS                    LYR 1680
  140 CONTINUE                                                          LYR 1690
      PA = PB                                                           LYR 1700
      RHOA = RHOB                                                       LYR 1710
      DO 145 K=1,KMAX                                                   LYR 1720
  145 DENA(K) = DENB(K)                                                 LYR 1730
  150 CONTINUE                                                          LYR 1740
      IF(H3.GE.Z2) GO TO 160                                            LYR 1750
      H1 = H3                                                           LYR 1760
      R1 = R3                                                           LYR 1770
      SINAI1 = SINAI3                                                   LYR 1780
      RATIO1 = RATIO3                                                   LYR 1790
      Y1=Y3                                                             LYR 1800
      COSAI1 = COSAI3                                                   LYR 1810
      X1 = X3                                                           LYR 1820
      DSDX1 = DSDX3                                                     LYR 1830
      DBNDX1 = DBNDX3                                                   LYR 1840
      GO TO 115                                                         LYR 1850
  160 CONTINUE                                                          LYR 1860
      SINAI = SINAI3                                                    LYR 1870
      COSAI = COSAI3                                                    LYR 1880
      SP(J) = S                                                         LYR 1890
      RETURN                                                            LYR 1900
      END                                                               LYR 1910
