C                                                                       TRA 7620
C                                                                       TRA 7630
C   INITIALIZE OMEGA0 FOR NO MULTIPLE SCATTERING CASES                  TRA 7640
C                                                                       TRA 7650
CCC   IF(IMULT .EQ. 0) THEN                                             TRA*7660
CCC   IF(IK. LE. ML)OMEGA0(IK)=0.0                                      TRA*7670
CCC   ENDIF                                                             TRA*7680
      IF(ISCTTR.EQ.0) GO TO 209                                         TRA 7690
      IF(IMULT. EQ. 1.AND.INIT.EQ.0) GO TO 207                          TRA 7700
 206  CONTINUE                                                          TRA 7710
C     TEST AGAINST ML1 INSERTED  FOR ERRATA TO WORK                     TRA 7720
      IF(IK .LE. ML1 .AND .IMULT .EQ. 1)                                TRA*7730
     X     CALL SSRAD(IPH,IK,ITZERO,IPATH,V,SUMSSR)                     TRA*7740
      IF(IMULT .EQ. 0)                                                  TRA*7745
     X     CALL SSRAD(IPH,IK,ITZERO,IPATH,V,SUMSSR)                     TRA*7750
207   IF(IPATH.EQ.3) GO TO 208                                          TRA 7760
      IPATH=IPATH+1                                                     TRA 7770
      GO TO 111                                                         TRA 7780
208   IPATH=2                                                           TRA 7790
209   IF (IV.GE.13000) TX(3)=TX(8)                                      TRA 7800
C     TO AVOID THE DIFFICULTY FOR WAVENUMBER BEING ZERO                 TRA 7810
      ALAM=9999.998                                                     TRA 7820
      IF(V.GT.0.)ALAM=1.0E+04/V                                         TRA 7830
      IF (IEMSCT.EQ.0 .OR. IEMSCT.EQ.3) GO TO 220                       TRA 7840
C                                                                       TRA 7850
C   FOR NO MULTIPLE SCATTERING, CALCULATE BLACK BODY FUNCTION,          TRA 7860
C   AS WELL AS RADIANCE ADDING FOR THERMAL                              TRA 7870
C                                                                       TRA 7880
      BBIK=BBFN(TBBY(IK),V)                                             TRA 7890
C     TLNEW=(TX(9)*TX(10))/(TX(7)*TX(6))                                TRA 7900
C     TSNEW=(TX(7)*TX(6))/TX(10)                                        TRA 7910
      TLNEW= TX(9)                                                      TRA 7920
      DTAU=TLOLD-TLNEW                                                  TRA 7930
      IF(IMULT.EQ.1) DTAU = TOTAL - TX(9)                               TRA 7940
      TOTAL = TX(9)                                                     TRA 7950
      IF(DTAU.LE.0.) DTAU=0.                                            TRA 7960
      IF(IMULT .EQ. 0) THEN                                             TRA 7970
           IF(NOPRNT. EQ. -1) THEN                                      TRA 7980
              WRITE(IPR1,1210) V,AHT(IK),AHT(IK+1),BBIK,DTAU,TLNEW      TRA 7990
 1210         FORMAT(F10.0,2F7.2,1P2E12.5,0PF12.9)                      TRA 8000
           ENDIF                                                        TRA 8010
      IF (IEMSCT.LT.2.AND.DTAU.LT.1.0E-5.AND.TLNEW.LT.1.0E-5) THEN      TRA*8020
           IF(NOPRNT. EQ. -1) WRITE(IPR1,1220)                          TRA 8030
 1220      FORMAT('     -999.',T63,'  DTAU   EXIT LOOP ')               TRA 8040
           GO TO 220                                                    TRA 8050
      ENDIF                                                             TRA 8060
      SUMV=SUMV+BBIK*DTAU                                               TRA 8070
C     RADIANCE / CONSERVATIVE SCATTERING                                TRA 8080
      ELSE                                                              TRA 8090
      IF(INIT.EQ.0) GO TO 210                                           TRA 8100
      N1 = ML - IMAP(IK) + IOFF                                         TRA 8110
      IF(N1.GE.ML ) N1 = ML - 1                                         TRA*8122
           IF(NOPRNT. EQ. -1) THEN                                      TRA 8130
               N = N1 + 1                                               TRA 8140
               WRITE(IPR1,1230) V,AHT(IK+1),UMB(N),UMBS(N),DMB(N),      TRA 8150
     X         DMBS(N),SUN*TX(9),TX(9)                                  TRA 8160
           ENDIF                                                        TRA 8170
      IF (DTAU.LT.1.0E-5.AND.TX(9).LT.1.0E-5) THEN                      TRA 8180
           IF(NOPRNT. EQ. -1) WRITE(IPR1,1220)                          TRA 8190
           GO TO 220                                                    TRA 8200
      ENDIF                                                             TRA 8210
      IF(QTHETA(IK). LT. (PI/2.)) THEN                                  TRA 8220
C     UP                                                                TRA 8230
C                                                                       TRA 8240
C   SU & SD ARE THERMAL UPWARD & DOWNWARD SOURCE FUNCTIONS              TRA 8250
C   SUS & SDS ARE SOLAR UPWARD & DOWNWARD SOURCE FUNCTIONS              TRA 8260
C                                                                       TRA 8270
      SMS     =BBIK    *(1.-OMEGA0(N1))+OMEGA0(N1)/PI*                  TRA 8280
     1   (DMF(N1)*(1.-BET(IK)) +UMF(N1)*BET(IK))                        TRA 8290
      SMSS     =                                                        TRA 8300
     2  OMEGA0(N1)/PI*(DMFS(N1)*(1.-BET(IK))+UMFS(N1)*BET(IK))          TRA 8310
      ELSE                                                              TRA 8320
C     DOWN                                                              TRA 8330
      SMS       =BBIK      *(1.-OMEGA0(N1  ))+OMEGA0(N1  )/PI*          TRA 8340
     2 (UMF(N1  )* (1.-BET(IK  ))+DMF(N1)*BET(IK  ))                    TRA 8350
      SMSS       =    OMEGA0(N1  )/PI*                                  TRA 8360
     2 (UMFS(N1  )*(1.-BET(IK  ))+DMFS(N1  )*BET(IK  ))                 TRA 8370
      ENDIF                                                             TRA 8380
      SUMV = SUMV + SMS  * DTAU                                         TRA 8390
      SUMMS= SUMMS+ SMSS * DTAU                                         TRA 8400
      ENDIF                                                             TRA 8410
      TLOLD=TLNEW                                                       TRA 8420
  210 CONTINUE                                                          TRA 8430
C*****END OF LAYER LOOP                                                 TRA 8440
  220 CONTINUE                                                          TRA 8450
C                                                                       TRA 8460
C   THE FOLLOWING ROUTINE IS CALLED FOR MULTIPLE SCATTERING CASES ONLY  TRA 8470
C                                                                       TRA 8480
C   MSRAD RETURNS SOLAR AND THERMAL CONTRIBUTIONS TO RADIANCE           TRA 8490
C   AND THERMAL (FDNTRT) DIFFUSE FLUX FOR SURFACE REFLECTION.           TRA 8500
C   (THESE QUANTITIES IN COMMON /MSRD/)                                 TRA 8510
C                                                                       TRA 8520
      IF(IMULT .EQ. 0) GO TO 266                                        TRA 8530
      IF(INIT  .NE. 0) GO TO 266                                        TRA 8540
      CALL MSRAD(IV,V,ISOURC,IDAY,ANGLEM,SS,                            TRA 8550
     2     DMF,DMFS,UMF,UMFS)                                           TRA 8560
C                                                                       TRA 8570
      IF(NOPRNT. EQ. -1) THEN                                           TRA 8580
          WRITE(IPR1,1230) V,AHT(1),UMB(1),UMBS(1),DMB(1),              TRA 8590
     X    DMBS(1),SUN,1.                                                TRA 8600
1230      FORMAT(F10.0,F7.2,1P5E12.5,0PF10.5)                           TRA 8610
      ENDIF                                                             TRA 8620
C                                                                       TRA 8630
C   GO BACK AND CALCULATE RADIANCE FOR NON-MULTIPLE SCATTERING CASE     TRA 8640
C                                                                       TRA 8650
C                                                                       TRA 8660
C     IKMAX AND IKMAXP NEED BE DEFINED FOR VEW TO SPACE                 TRA 8670
C                                                                       TRA 8680
      IKMAX1 = IKMAX - 1                                                TRA 8690
      IKMAXP = IKMAX + 1                                                TRA 8700
      COSBAR(IKMAX) = COSBAR(IKMAX1)                                    TRA 8710
      OMEGA0(IKMAX)  = OMEGA0(IKMAX1)                                   TRA 8720
      UMF(IKMAX)   = UMF(IKMAX1)                                        TRA 8730
      UMFS(IKMAX)   = UMFS(IKMAX1)                                      TRA 8740
      DMFS(IKMAX)  =DMFS(IKMAX1)                                        TRA 8750
      COSBAR(IKMAXP)= COSBAR(IKMAX1)                                    TRA 8760
      OMEGA0(IKMAXP) = OMEGA0(IKMAX1)                                   TRA 8770
      UMF(IKMAXP)  = UMF(IKMAX1)                                        TRA 8780
      UMFS(IKMAXP)  = UMFS(IKMAX1)                                      TRA 8790
      DMFS(IKMAXP) =DMFS(IKMAX1)                                        TRA 8800
C                                                                       TRA 8810
      IKMAX = IKMX                                                      TRA 8820
      INIT = INIT + 1                                                   TRA 8830
      DO 60 IK=1,IKMAX                                                  TRA 8840
      N  = ML - IMAP(IK) + IOFF                                         TRA 8850
C                                                                       TRA 8860
C   BET(N)     - BACKSCATTER FRACTION ALONG OPTICAL PATH AT LAYER N     TRA 8870
C                                                                       TRA 8880
      COZEN=ABS(COS(QTHETA(IK  )))                                      TRA 8890
C                                                                       TRA 8900
C    CALCULATE BACKSCATTER PARAMETERS                                   TRA 8910
C                                                                       TRA 8920
      IF(COSBAR(N) .EQ. 0.0) THEN                                       TRA 8930
      BET(IK)=.5                                                        TRA 8940
      ELSE                                                              TRA 8950
      BET(IK)=BETABS(COZEN,COSBAR(N))                                   TRA 8960
      ENDIF                                                             TRA 8970
60    CONTINUE                                                          TRA 8980
      BET(IKMAX+1)= .5                                                  TRA 8990
C                                                                       TRA 9000
      GOTO 109                                                          TRA 9010
266   CONTINUE                                                          TRA 9020
      IF (IV.GT.IV1) FACTOR=1.0                                         TRA 9030
      IF (IV.GE.IV2) FACTOR=0.5                                         TRA 9040
      SUMA=SUMA+FACTOR*DV*(1.0-TX(9))                                   TRA 9050
      IIII = IEMSCT+1                                                   TRA 9060
      ALTX9 = BIGNUM                                                    TRA 9070
      IF(TX(9) . GT. 0.)  ALTX9 = -ALOG(TX(9))                          TRA 9080
      GO TO (300,400,400,600),IIII                                      TRA 9090
  300 CONTINUE                                                          TRA 9100
C*****TRANSMITTANCE ONLY                                                TRA 9110
      TX(10)=1.-TX(10)                                                  TRA 9120
      TX(7) = TX(7) * TX(16)                                            TRA 9130
      WRITE(IPR,906) V,ALAM,TX(9),TX(17),UNIF,TX(31),TRACE,             TRA 9140
     + TX(4),TX(5),TX(6),TX(7),TX(11),TX(10),SUMA                       TRA 9150
      TX(50) = TX(50) * TX(58)                                          TRA 9160
      WRITE(IPR1,907)      V,TX(17),TX(31),TX(36),TX(44),TX(46),        TRA 9170
     + TX(47),TX(50),TX(52),TX(54),TX(55),TX(56)                        TRA 9180
      WRITE(IPU,907)      V,TX(9),TX(17),UNIF,TX(31),TRACE,             TRA 9190
     + TX(4),TX(5),TX(6),TX(7),TX(11),TX(10),ALTX9                      TRA 9200
  907 FORMAT(F7.0,11F8.4,1PE10.3)                                       TRA 9210
      GO TO 700                                                         TRA 9220
  400 CONTINUE                                                          TRA 9230
C*****ATMOSPHERIC RADIANCE ONLY                                         TRA 9240
C*****INCLUDE EMISSION OF BOUNDARY ATTENUATED BY TOTAL TRANSMISSION     TRA 9250
C                                                                       TRA 9260
C    CALCULATE THERMAL RADIANCE CONTRIBUTION OF BOUNDARY                TRA 9270
C                                                                       TRA 9280
C    SURDTR IS THE SCATTERED CONTRIBUTION TO THE THERMAL RADIANCE       TRA 9290
C    BUT IS ONLY ADDED IF THE PATH INTERSECTS THE SURFACE               TRA 9300
C                                                                       TRA 9310
C                                                                       TRA 9320
      SURDTR=0.0                                                        TRA 9330
      IF (IMLT .EQ. 1 .AND. H2 .EQ. 0.0) THEN                           TRA 9340
      SURDTR=SALB*FDNTRT*TX(9)/PI                                       TRA 9350
      END IF                                                            TRA 9360
      IF (TBOUND .LE. 0.0) THEN                                         TRA 9370
      BBG=0.0                                                           TRA 9380
      ELSE                                                              TRA 9390
      BBG=BBFN(TBOUND,V)*TX(9)*EMISS+SURDTR                             TRA 9400
      ENDIF                                                             TRA 9410
C                                                                       TRA 9420
C   ADD THERMAL BOUNDARY AND MULTIPLE SCATTERED RADIANCE                TRA 9430
C                                                                       TRA 9440
      SUMV = SUMV + BBG                                                 TRA 9450
      SUMVV=SUMV                                                        TRA 9460
      IF(V. GT. 0.)SUMV=(1.0E+04/V**2)*SUMV                             TRA 9470
      IF(IEMSCT.EQ.2) GO TO 500                                         TRA 9480
      RADSUM=RADSUM+DV*FACTOR*SUMV                                      TRA 9490
      WRITE(IPR,916) V,ALAM ,SUMV,SUMVV,RADSUM,TX(9)                    TRA 9500
      WRITE(IPU,917) V,TX(9),SUMV,ALTX9                                 TRA 9510
  917 FORMAT(F7.0,F8.4,1PE9.2,T96,E10.3)                                TRA 9520
      SUMT=SUMV                                                         TRA 9530
      SUMTT=SUMVV                                                       TRA 9540
      GO TO 700                                                         TRA 9550
  500 CONTINUE                                                          TRA 9560
C*****SOLAR SCATTERED RADIANCE                                          TRA 9570
C*****MULTIPLY SUMSSR BY THE EXTRATERRESTRIAL SOURCE STRENGTH SS        TRA 9580
      CALL SOURCE(V,ISOURC,IDAY,ANGLEM,SS)                              TRA 9590
C                                                                       TRA 9600
C   CALCULATE TOTAL SINGLE SCATTERED + MULTIPLE SCATTERED SOLAR         TRA 9610
C   RADIANCE FOR EACH FREQUENCY                                         TRA 9620
C                                                                       TRA 9630
      SUMSSS=SUMSSR*SS                                                  TRA 9640
      SUMSSR=SUMSSS+SUMMS                                               TRA 9650
C*****SUMSSR IS THE SCATTERED RADIANCE IN (W/CM2-STER-MICROMETER)       TRA 9660
      SUMS = 0.                                                         TRA 9662
      SUMSS = 0.                                                        TRA 9664
      IF(V. GT. 0.)SUMS=(1.0E4/V**2)*SUMSSR                             TRA 9670
      IF(V. GT. 0.)SUMSSS=(1.0E4/V**2)*SUMSSS                           TRA 9680
C*****RFLSOL IS GROUND-REFLECTED DIRECT SOURCE RADIANCE                 TRA 9690
      RFLSOL=0.                                                         TRA 9700
      RFLS=0.                                                           TRA 9710
      RFLSS=0.                                                          TRA 9720
C                                                                       TRA 9730
C   SURDSR IS SURFACE REFLECTION OF DOWNWARD SCATTERED RADIANCE         TRA 9740
C                                                                       TRA 9750
      SURDSR=0.                                                         TRA 9760
      IF(H2.GT.ZM(1)) GO TO 510                                         TRA*9770
      IF(TEB1.LE.0.)  GO TO 510                                         TRA 9780
      IF(ANGSUN.GE.0.) RFLFAC=SALB*COS(ANGSUN*CA)/PI                    TRA 9790
C                                                                       TRA 9800
C     FOR MULTIPLE SCATTERING CASES:                                    TRA 9810
C                                                                       TRA 9820
      IF (IMLT .EQ. 1) THEN                                             TRA 9830
      SURDSR=SALB*FDNSRT*TX(9)/PI                                       TRA 9840
      END IF                                                            TRA 9850
      RFLSSS=RFLFAC*TEB1*SS                                             TRA 9860
      RFLSOL=RFLSSS+SURDSR                                              TRA 9870
      RFLS = 0.                                                         TRA 9872
      RFLSS = 0.                                                        TRA 9874
      IF(V. GT. 0.)RFLS = 1.0E4/V**2*RFLSOL                             TRA 9880
      IF(V. GT. 0.)RFLSS = 1.0E4/V**2*RFLSSS                            TRA 9890
  510 CONTINUE                                                          TRA 9900
      SUMT=SUMV+SUMS+RFLS                                               TRA 9910
      SUMTT=SUMVV+SUMSSR+RFLSOL                                         TRA 9920
      RADSUM=RADSUM+DV*FACTOR*SUMT                                      TRA 9930
      IF (IMLT .NE. 1) THEN                                             TRA 9940
      WRITE(IPR,926) V,ALAM,SUMV,SUMVV,SUMS,SUMSSR,RFLS,RFLSOL,         TRA 9950
     X    SUMT,SUMTT,RADSUM,TX(9)                                       TRA 9960
      ELSE                                                              TRA 9970
      WRITE(IPR,928) V,ALAM,SUMV,SUMVV,SUMS,SUMSSR,SUMSSS,RFLS,RFLSOL,  TRA 9980
     X    RFLSS,SUMT,SUMTT,RADSUM,TX(9)                                 TRA 9990
      END IF                                                            TRA10000
      WRITE(IPU,927) V,TX(9),SUMV,SUMS,SUMSSS,RFLS,                     TRA10010
     X    RFLSS,SUMT,TEB1,TEB2SV,ALTX9                                  TRA10020
  927 FORMAT(F7.0,F8.4,1P6E9.2,0P2F8.4,T96,1PE10.3)                     TRA10030
      GO TO 700                                                         TRA10040
  600 CONTINUE                                                          TRA10050
C*****DIRECTLY TRANSMITTED SOLAR IRRADIANCE                             TRA10060
C*****SOLIL IS SOLAR IRRADIANCE IN WATTS/(CM2 MICROMETER)               TRA10070
      CALL SOURCE(V,ISOURC,IDAY,ANGLEM,SOLIL)                           TRA10080
      SOLIV = 0.                                                        TRA10082
      IF(V. GT. 0.)SOLIV = SOLIL*1.0E+4/V**2                            TRA10090
      TSOLIV = SOLIV*TX(9)                                              TRA10100
      TSOLIL = SOLIL*TX(9)                                              TRA10110
      STSOL = STSOL+TSOLIV*DV*FACTOR                                    TRA10120
      SSOL = SSOL+SOLIV*DV*FACTOR                                       TRA10130
      WRITE(IPR,936) V,ALAM,TSOLIV,TSOLIL,SOLIV,SOLIL,STSOL,SSOL,TX(9)  TRA10140
      WRITE(IPU,937) V,TX(9),TSOLIV,SOLIV,ALTX9                         TRA10150
  937 FORMAT(F7.0,F8.4,1P2E9.2,T96,E10.3)                               TRA10160
      SUMT = TSOLIV                                                     TRA10170
      RADSUM = STSOL                                                    TRA10180
C*****                                                                  TRA10190
  700 CONTINUE                                                          TRA10200
      IF(IEMSCT.EQ.0) GO TO 710                                         TRA10210
      IF(SUMT.GE.RADMAX) VRMAX = V                                      TRA10220
      IF(SUMT.GE.RADMAX) RADMAX= SUMT                                   TRA10230
      IF(SUMT.LE.RADMIN) VRMIN=V                                        TRA10240
      IF(SUMT.LE.RADMIN) RADMIN=SUMT                                    TRA10250
  710 CONTINUE                                                          TRA10260
      IMULT=IMLT                                                        TRA10270
      IF (IV.LT.IV2) GO TO 5                                            TRA10280
C*****END OF FREQUENCY LOOP                                             TRA10290
      AB=1.0-SUMA/FLOAT(IV-IV1)                                         TRA10300
      WRITE(IPR,740) IV1,IV,SUMA,AB                                     TRA10310
      IF(IEMSCT.EQ.0) GO TO 770                                         TRA10320
      IF(IEMSCT .EQ. 3) THEN                                            TRA10322
           WRITE(IPR,755) RADSUM,RADMIN,VRMIN,RADMAX,VRMAX              TRA10324
      ELSE                                                              TRA10326
           WRITE(IPR,750) RADSUM,RADMIN,VRMIN,RADMAX,VRMAX              TRA10330
           WRITE(IPR,760) TBOUND,EMISS                                  TRA10350
      ENDIF                                                             TRA10355
  770 CONTINUE                                                          TRA10360
      RETURN                                                            TRA10370
