      SUBROUTINE FUDGE(V,SUMY)                                          FDG* 100
C                                                                       FDG* 110
C     TO CALCULATE H2O FAR WING CONTINUUM USING THE SUMS OF EXPONENTIALSFDG* 120
C                                                                       FDG* 130
C     THIS FUNCTION IS WITHIN 5% OF THE ORIGINAL "FUDGE" BETWEEN 0 AND  FDG* 140
C     3000CM-1, PRESERVING THAT VALIDATION.                             FDG* 150
C     THE NEW FUNCTION IS 0.01 OF THE ORIGINAL NEAR 10000CM-1 (1.06NM), FDG* 160
C     IN ACCORDANCE WITH THE MEASUREMENTS OF JAYCOR, FUNDED BY SDIO.    FDG* 170
C                                                                       FDG* 180
C     Y0(V)=EXP(ALOG(3.159E-8)-(2.75E-4)*V)                             FDG* 190
      Y1(V)=EXP(ALOG(1.025*3.159E-8)-(2.75E-4)*V)                       FDG* 200
      Y2(V)=EXP(ALOG(8.97E-6)-(1.300E-3)*V)                             FDG* 210
C                                                                       FDG* 220
C     YO=Y0(V)                                                          FDG* 230
      YA=Y1(V)                                                          FDG* 240
      YB=Y2(V)                                                          FDG* 250
      YAINV=1/YA                                                        FDG* 260
      YBINV=1/YB                                                        FDG* 270
      SUMY=1./(1.*YAINV+1.*YBINV)                                       FDG* 280
      RETURN                                                            FDG* 290
      END                                                               FDG* 300
      FUNCTION   ANDEX(H,SH,GAMMA)                                      FAN  100
C***********************************************************************FAN  110
C     COMPUTES THE INDEX OF REFRACTION AT HEIGHT H, SH IS THE           FAN  120
C     SCALE HEIGHT, GAMMA IS THE VALUE AT H=0 OF THE REFRACTIVITY =     FAN  130
C     INDEX-1                                                           FAN  140
C***********************************************************************FAN  150
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     FAN  160
      IF(SH.EQ.0.0) GO TO 10                                            FAN  170
      IF(H. LE. 0.) GO TO 10                                            FAN  180
      HSH = H/SH                                                        FAN  190
      IF(HSH .GT.BIGEXP) GO TO 20                                       FAN* 200
      ANDEX = 1.0+GAMMA*EXP(-HSH )                                      FAN  210
      RETURN                                                            FAN  220
   10 ANDEX = 1.0+GAMMA                                                 FAN  230
      RETURN                                                            FAN  240
  20  ANDEX = 1.                                                        FAN* 242
      RETURN                                                            FAN* 244
      END                                                               FAN  250
      FUNCTION   TNRAIN(RR,V,TM,RADFLD)                                 TNR  100
CCC                                                                     TNR  110
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     TNR  120
      COMMON /CARD3/ H1,H2,ANGLE,RANGE,BETA,RE,LEN                      TNR  130
CCC   CALCULATES TRANSMISSION DUE TO RAIN AS A FUNCTION OF              TNR  140
CCC   RR=RAIN RATE IN MM/HR                                             TNR  150
CCC   OR WITHIN 350CM-1 USES THE MICROWAVE TABLE ROUTINE TO             TNR  160
CCC   OBTAIN THE EXTINCTION DUE TO RAIN                                 TNR  170
CCC   RANGE=SLANT RANGE KM                                              TNR  180
CCC                                                                     TNR  190
CCC   ASSUMES A MARSHALL-PALMER RAIN DROP SIZE DISTRIBUTION             TNR  200
CCC   N(D)=NZERO*EXP(-A*D)                                              TNR  210
CCC   NZERO=8.E3 (MM-1)  (M-3)                                          TNR  220
CCC   A=41.*RR**(-0.21)                                                 TNR  230
CCC   D=DROP DIAMETER (CM)                                              TNR  240
CCC                                                                     TNR  250
      REAL NZERO                                                        TNR  260
      DATA NZERO /8000./                                                TNR  270
CCC                                                                     TNR  280
      A=41./RR**0.21                                                    TNR  290
CCC                                                                     TNR  300
      IF(RR.LE.0)TNRAIN=1.                                              TNR  310
      IF(RR.LE.0)RETURN                                                 TNR  320
CCC                                                                     TNR  330
      IF(V.GE.350.0) THEN                                               TNR  340
       TNRAIN=PI*NZERO/A**3                                             TNR  350
      ELSE                                                              TNR  360
       TNRAIN=GMRAIN(V,TM,RR)                                           TNR  370
       TNRAIN = TNRAIN * RADFLD                                         TNR  380
      END IF                                                            TNR  390
      RETURN                                                            TNR  400
      END                                                               TNR  410
      SUBROUTINE RNSCAT(V,R,TT,PHASE,DIST,IK,CSSA,ASYMR,IENT)           RNS  100
C********************************************************************** RNS  110
      INTEGER PHASE,DIST                                                RNS  120
      DIMENSION SC(3,4)                                                 RNS  130
C                                                                       RNS  140
C          ARGUMENTS:                                                   RNS  150
C                                                                       RNS  160
C          F = FREQUENCY (GHZ)                                          RNS  170
C          R = RAINFALL RATE (MM/HR)                                    RNS  180
C          T = TEMPERATURE (DEGREES CELSIUS)                            RNS  190
C          PHASE = PHASE PARAMETER (1=WATER, 2=ICE)                     RNS  200
C          DIST = DROP SIZE DISTRIBUTION PARAMETER                      RNS  210
C                     (1=MARSHALL-PALMER, 2=BEST)                       RNS  220
C                                                                       RNS  230
C          RESULTS:                                                     RNS  240
C                                                                       RNS  250
C          SC(1) = ABSORPTION COEFFICIENT (1/KM)                        RNS  260
C          SC(2) = EXTINCTION COEFFICIENT (1/KM)                        RNS  270
C          SC(I),I=3,NSC = LEGENDRE COEFFICIENTS #I-3  (NSC=10)         RNS  280
C          ERR = ERROR RETURN CODE: 0=NO ERROR, 1=BAD FREQUENCY,        RNS  290
C                2=BAD RAINFALL RATE, 3=BAD TEMPERATURE,                RNS  300
C                4=BAD PHASE PARAMETER, 5=BAD DROP SIZE DISTRIBUTION    RNS  310
C                                                                       RNS  320
C          THE INTERNAL DATA:                                           RNS  330
C                                                                       RNS  340
      DIMENSION FR(9),TEMP(3)                                           RNS  350
C                                                                       RNS  360
C          FR(I),I=1,NF = TABULATED FREQUENCIES (GHZ)  (NF=9)           RNS  370
C          TEMP(I),I=1,NT = TABULATED TEMPERATURES  (NT=3)              RNS  380
C                                                                       RNS  390
C          THE BLOCK-DATA SECTION                                       RNS  400
C                                                                       RNS  410
      DATA RMIN,RMAX/0.,50./,NF/9/,NT/3/,NSC/4/,MAXI/3/                 RNS  420
      DATA TK/273.15/,CMT0/1.0/,C7500/0.5/,G0/0.0/,G7500/0.85/          RNS  430
      DATA (TEMP(I),I=1,3)/-10.,0.,10./                                 RNS  440
      DATA (FR(I),I=1,9)/19.35,37.,50.3,89.5,100.,118.,130.,183.,231./  RNS  450
C                                                                       RNS  460
C      THIS SUBROUTINE REQUIRES FREQUENCIES IN GHZ                      RNS  470
      NOPR = 0                                                          RNS  480
      IF(IK .EQ. 1) NOPR = 1                                            RNS  490
      IF(IENT .GT.1) NOPR = 0                                           RNS  500
      F= V *29.97925                                                    RNS  510
      FSAV=F                                                            RNS  520
      RSAV=R                                                            RNS  530
      TSAV=T                                                            RNS  540
      INT=0                                                             RNS  550
C      CONVERT TEMP TO DEGREES CELSIUS                                  RNS  560
      T=TT-TK                                                           RNS  570
C      FREQ RANGE OF DATA 19.35-231 GHZ IF LESS THAN 19.35              RNS  580
C      SET UP PARAMETERS FOR INTERPOLATION                              RNS  590
      IF(F.LT.FR(1)) THEN                                               RNS  600
        FL=0.0                                                          RNS  610
        FM=FR(1)                                                        RNS  620
        INT=1                                                           RNS  630
        IF(NOPR .GT. 0) WRITE (6,801)                                   RNS  640
      END IF                                                            RNS  650
C      IF MORE THAN 231 GHZ SET UP PARAMETERS FOR EXTRAPOLATION         RNS  660
       IF(F.GT.FR(NF)) THEN                                             RNS  670
         FL=FR(NF)                                                      RNS  680
         FM=7500.                                                       RNS  690
         INT=2                                                          RNS  700
         IF(NOPR .GT. 0) WRITE (6,801)                                  RNS  710
      END IF                                                            RNS  720
C      TEMP RANGE OF DATA IS -10 TO +10 DEGREES CELCIUS                 RNS  730
C      IF BELOW OR ABOVE EXTREME SET AND DO CALCULATIONS AT EXTREME     RNS  740
      IF (T.LT.TEMP(1)) THEN                                            RNS  750
        T=TEMP(1)                                                       RNS  760
        IF(NOPR .GT. 0) WRITE (6,802)                                   RNS  770
      END IF                                                            RNS  780
C                                                                       RNS  790
      IF (T.GT.TEMP(3)) THEN                                            RNS  800
        T=TEMP(3)                                                       RNS  810
        IF(NOPR .GT. 0) WRITE (6,802)                                   RNS  820
      END IF                                                            RNS  830
C                                                                       RNS  840
C      RAIN RATE OF DATA IS FOR 0-50 MM/HR                              RNS  850
C      IF GT 50 TREAT CALCULATIONS AS IF 50 MM/HR WAS INPUT             RNS  860
      IF(R.GT.50) THEN                                                  RNS  870
        R=50.                                                           RNS  880
        IF(NOPR .GT. 0) WRITE (6,803)                                   RNS  890
      END IF                                                            RNS  900
C                                                                       RNS  910
      KI=1                                                              RNS  920
C             FIGURE OUT THE SECOND INDEX                               RNS  930
   10 J=PHASE+2*DIST                                                    RNS  940
C                                                                       RNS  950
C                                                                       RNS  960
C             GET THE TEMPERATURE INTERPOLATION PARAMETER ST            RNS  970
C             IF NEEDED AND AMEND THE SECOND INDEX                      RNS  980
      CALL BS(J,T,TEMP,NT,ST)                                           RNS  990
C                                                                       RNS 1000
C             FIGURE OUT THE THIRD INDEX AND THE FREQUENCY INTERPOLATIONRNS 1010
C             PARAMETER SF                                              RNS 1020
      CALL BS(K,F,FR,NF,SF)                                             RNS 1030
C                                                                       RNS 1040
C             INITIALIZE SC                                             RNS 1050
      DO 11 I=1,NSC                                                     RNS 1060
      SC(KI,I)=0.                                                       RNS 1070
   11 CONTINUE                                                          RNS 1080
      SC(KI,3)=1.                                                       RNS 1090
C                                                                       RNS 1100
C             NOW DO THE CALCULATIONS                                   RNS 1110
C                                                                       RNS 1120
C             THE WATER CONTENT IS                                      RNS 1130
      IF(DIST.EQ.1) THEN                                                RNS 1140
      WC=.0889*R**.84                                                   RNS 1150
      ELSE                                                              RNS 1160
      WC=.067*R**.846                                                   RNS 1170
      END IF                                                            RNS 1180
C                                                                       RNS 1190
C             FOR A TEMPERATURE DEPENDENT CASE, I.E.                    RNS 1200
      IF(J.LT.3) THEN                                                   RNS 1210
      S1=(1.-SF)*(1.-ST)                                                RNS 1220
      S2=(1.-SF)*ST                                                     RNS 1230
      S3=SF*(1.-ST)                                                     RNS 1240
      S4=SF*ST                                                          RNS 1250
      DO 14 I=1,MAXI                                                    RNS 1260
      IF(I.LE.2) THEN                                                   RNS 1270
      ISC=I                                                             RNS 1280
      ELSE                                                              RNS 1290
      ISC=I+1                                                           RNS 1300
      END IF                                                            RNS 1310
      SC(KI,ISC)=S1*TAB(I,J,K,WC)+S2*TAB(I,J+1,K,WC)+                   RNS 1320
     *             S3*TAB(I,J,K+1,WC)+S4*TAB(I,J+1,K+1,WC)              RNS 1330
   14 CONTINUE                                                          RNS 1340
C                                                                       RNS 1350
C             FOR A TEMPERATURE INDEPENDENT CASE                        RNS 1360
      ELSE                                                              RNS 1370
      S1=1.-SF                                                          RNS 1380
      S2=SF                                                             RNS 1390
      DO 17 I=1,MAXI                                                    RNS 1400
      IF(I.LE.2) THEN                                                   RNS 1410
      ISC=I                                                             RNS 1420
      ELSE                                                              RNS 1430
      ISC=I+1                                                           RNS 1440
      END IF                                                            RNS 1450
      SC(KI,ISC)=S1*TAB(I,J,K,WC)+S2*TAB(I,J,K+1,WC)                    RNS 1460
   17 CONTINUE                                                          RNS 1470
      END IF                                                            RNS 1480
      F=FSAV                                                            RNS 1490
      IF(INT.EQ.3) GO TO 20                                             RNS 1500
      IF(INT.EQ.4) GO TO 30                                             RNS 1510
      IF(INT.EQ.0) THEN                                                 RNS 1520
        CSSA=SC(KI,1)/SC(KI,2)                                          RNS 1530
        IF(CSSA.GT.1.0) CSSA=1.0                                        RNS 1540
        ASYMR=SC(KI,4)/3.0                                              RNS 1550
        F=FSAV                                                          RNS 1560
        R=RSAV                                                          RNS 1570
        T=TSAV                                                          RNS 1580
      RETURN                                                            RNS 1590
      END IF                                                            RNS 1600
      IF(INT.EQ.1) THEN                                                 RNS 1610
        INT=3                                                           RNS 1620
        F=FM                                                            RNS 1630
        KI=2                                                            RNS 1640
      END IF                                                            RNS 1650
      IF(INT.EQ.2) THEN                                                 RNS 1660
        INT=4                                                           RNS 1670
        F=FL                                                            RNS 1680
        KI=3                                                            RNS 1690
      END IF                                                            RNS 1700
      GO TO 10                                                          RNS 1710
   20 CONTINUE                                                          RNS 1720
      FDIF=FM-F                                                         RNS 1730
      FTOT=FM-FL                                                        RNS 1740
      CM=SC(KI,1)/SC(KI,2)                                              RNS 1750
      IF(CM.GT.1.0) CM=1.0                                              RNS 1760
      CL=CMT0                                                           RNS 1770
      AM=SC(KI,4)/3.0                                                   RNS 1780
      AL=G0                                                             RNS 1790
      GO TO 40                                                          RNS 1800
   30 CONTINUE                                                          RNS 1810
      FDIF=FM-F                                                         RNS 1820
      FTOT=FM-FL                                                        RNS 1830
      CM=C7500                                                          RNS 1840
      CL=SC(KI,1)/SC(KI,2)                                              RNS 1850
      IF(CL.GT.1.0) CL=1.0                                              RNS 1860
      AM=G7500                                                          RNS 1870
      AL=SC(KI,4)/3.0                                                   RNS 1880
   40 CTOT=CM-CL                                                        RNS 1890
      CAMT=FDIF*CTOT/FTOT                                               RNS 1900
      CSSA=CM-CAMT                                                      RNS 1910
      ATOT=AM-AL                                                        RNS 1920
      AAMT=FDIF*ATOT/FTOT                                               RNS 1930
      ASYMR=AM-AAMT                                                     RNS 1940
      F=FSAV                                                            RNS 1950
      R=RSAV                                                            RNS 1960
      T=TSAV                                                            RNS 1970
      RETURN                                                            RNS 1980
801   FORMAT(2X,'***  THE ASYMMETRY PARAMETER DUE TO RAIN IS BASED ON', RNS 1990
     1 'DATA BETWEEN 19 AND 231 GHZ',                                   RNS 2000
     2 /2X,'***  EXTRAPOLATION IS USED FOR FREQUENCIES LOWER AND',      RNS 2010
     3 'HIGHER THAN THIS RANGE')                                        RNS 2020
802   FORMAT(2X,'***  TEMPERATURE RANGE OF DATA IS -10 TO +10 ',        RNS 2030
     1'DEGREES CELSIUS',/2X,'***  BEYOND THESE VALUES IT IS ',          RNS 2040
     2'TREATED AS IF AT THE EXTREMES')                                  RNS 2050
803   FORMAT(2X,'***  RAIN RATES BETWEEN 0 AND 50 MM/HR ARE',           RNS 2060
     1'WITHIN THIS DATA RANGE',/2X,'***  ABOVE THAT THE ASYMMETRY',     RNS 2070
     2' PARAMETER IS CALCULATED FOR 50 MM/HR')                          RNS 2080
      END                                                               RNS 2090
      SUBROUTINE BS(I,A,B,N,S)                                          BS   100
C********************************************************************** BS   110
      DIMENSION B(9)                                                    BS   120
C                                                                       BS   130
C             THIS SUBROUTINE DOES THE BINARY SEARCH FOR THE INDEX I    BS   140
C             SUCH THAT A IS IN BETWEEN B(I) AND B(I+1)                 BS   150
C             AND CALCULATES THE INTERPOLATION PARAMETER S              BS   160
C             SUCH THAT A=S*B(I+1)+(1.-S)*B(I)                          BS   170
C                                                                       BS   180
      I=1                                                               BS   190
      J=N                                                               BS   200
   10 M=(I+J)/2                                                         BS   210
      IF(A.LE.B(M)) THEN                                                BS   220
      J=M                                                               BS   230
      ELSE                                                              BS   240
      I=M                                                               BS   250
      END IF                                                            BS   260
      IF(J.GT.I+1) GO TO 10                                             BS   270
      S=(A-B(I))/(B(I+1)-B(I))                                          BS   280
      RETURN                                                            BS   290
      END                                                               BS   300
