           BB(IMOL) = BBNH3(IBAND)                                      ABC 1870
           CC(IMOL) = CCNH3(IBAND)                                      ABC 1880
      ENDIF                                                             ABC 1890
C  ---NO                                                                ABC 1900
      IMOL = 8                                                          ABC 1910
      IW = -1                                                           ABC 1920
      IF (IV .GE.  1700 .AND. IV .LE.  2005) IW  = 54                   ABC 1930
      IBAND = IW - 53                                                   ABC 1940
      IBND(IMOL) = IW                                                   ABC 1950
      IF(IW .GT.  0) THEN                                               ABC 1960
           A(IMOL)  =  ANO                                              ABC 1970
           AA(IMOL) = AANO                                              ABC 1980
           BB(IMOL) = BBNO                                              ABC 1990
           CC(IMOL) = CCNO                                              ABC 2000
      ENDIF                                                             ABC 2010
C  ---NO2                                                               ABC 2020
      IW = -1                                                           ABC 2030
      IMOL = 10                                                         ABC 2040
      IF((IV .GE.   580 .AND. IV .LE.   925) .OR.                       ABC 2050
     *   (IV .GE.  1515 .AND. IV .LE.  1695) .OR.                       ABC 2060
     *   (IV .GE.  2800 .AND. IV .LE.  2970)) IW = 55                   ABC 2070
      IBAND = IW - 54                                                   ABC 2080
      IBND(IMOL) = IW                                                   ABC 2090
      IF(IW .GT.  0) THEN                                               ABC 2100
           A(IMOL)  =  ANO2(IBAND)                                      ABC 2110
           AA(IMOL) = AANO2(IBAND)                                      ABC 2120
           BB(IMOL) = BBNO2(IBAND)                                      ABC 2130
           CC(IMOL) = CCNO2(IBAND)                                      ABC 2140
      ENDIF                                                             ABC 2150
C  ---SO2                                                               ABC 2160
      IMOL = 9                                                          ABC 2170
      IW = -1                                                           ABC 2180
      IF (IV .GE.     0 .AND. IV .LE.   185)  IW = 56                   ABC 2190
      IF((IV .GE.   400 .AND. IV .LE.   650) .OR.                       ABC 2200
     *   (IV .GE.   950 .AND. IV .LE.  1460) .OR.                       ABC 2210
     *   (IV .GE.  2415 .AND. IV .LE.  2580)) IW = 57                   ABC 2220
      IBAND = IW - 55                                                   ABC 2230
      IBND(IMOL) = IW                                                   ABC 2240
      IF(IW .GT.  0) THEN                                               ABC 2250
           A(IMOL)  =  ASO2(IBAND)                                      ABC 2260
           AA(IMOL) = AASO2(IBAND)                                      ABC 2270
           BB(IMOL) = BBSO2(IBAND)                                      ABC 2280
           CC(IMOL) = CCSO2(IBAND)                                      ABC 2290
      ENDIF                                                             ABC 2300
      RETURN                                                            ABC 2310
      END                                                               ABC 2320
      SUBROUTINE CXDTA (CPRIME,V,IWL,IWH,CP,IND)                        CXD  100
C     THIS SUBROUTINE FINDS THE CPRIME FOR THE WAVENUMBER V.            CXD  110
C     INPUT:         V --- WAVENUMBER                                   CXD  120
C            (IWL,IWH) --- WAVENUMBER PAIR SPECIFIES THE ABSORPTION     CXD  130
C                          REGION. BOTH ARE ARRAYS AND TERMINATED       CXD  140
C                          WITH THE VALUE -999                          CXD  150
C                   CP --- ARRAY CONTAINS THE CPRIMES                   CXD  160
C     OUTPUT:   CPRIME --- THE CPRIME CORRESPONDING TO V                CXD  170
C     I/O:         IND --- INDICATOR INDICATES THE ABSORPTION REGION    CXD  180
C                          WHERE THE WAVENUMBER IS EXPECTED TO BE IN    CXD  190
C                          OR NEARBY (IT SERVES FOR THE PURPOSE         CXD  200
C                          TO SPEED UP THE SEARCHING PROCESS)           CXD  210
      DIMENSION IWL(*),IWH(*),CP(*)                                     CXD  220
      IV=V                                                              CXD  230
      CPRIME=-20.0                                                      CXD  240
      IF (IWL(IND+1) .EQ. -999 .AND. IV .GT. IWH(IND)) RETURN           CXD  250
      IF (IV .LT. IWL(1)) RETURN                                        CXD  260
      IC=0                                                              CXD  270
  100 IF (IV .GE. IWL(IND) .AND. IV .LE. IWH(IND)) GO TO 200            CXD  280
      IF (IV .GT. IWH(IND) .AND. IV .LT. IWL(IND+1)) RETURN             CXD  290
      IND=IND+1                                                         CXD  300
      IF (IWL(IND) .NE. -999) GO TO 100                                 CXD  310
      IND=IND-1                                                         CXD  320
      IF (IV .GT. IWH(IND)) RETURN                                      CXD  330
      IND=1                                                             CXD  340
      GO TO 100                                                         CXD  350
  200 IF (IND .EQ. 1) GO TO 400                                         CXD  360
      INDM1=IND-1                                                       CXD  370
      DO 300 I=1,INDM1                                                  CXD  380
        IC=IC+(IWH(I)-IWL(I))/5+1                                       CXD  390
  300 CONTINUE                                                          CXD  400
  400 IC=IC+(IV-IWL(IND))/5+1                                           CXD  410
      CPRIME=CP(IC)                                                     CXD  420
      RETURN                                                            CXD  430
      END                                                               CXD  440
      FUNCTION   DBLTX(W,CPRIME,QA)                                     DBL  100
      DBLTX=1.0                                                         DBL  110
      IF (W .LT. 1.0E-20) RETURN                                        DBL  120
      IF (CPRIME .LT. -20) RETURN                                       DBL  130
      WS=ALOG10(W)+CPRIME                                               DBL  140
      QAWS=QA*WS                                                        DBL  150
      IF (QAWS .LT. 2.0) GO TO 100                                      DBL  160
      DBLTX=0.0                                                         DBL  170
      RETURN                                                            DBL  180
  100 IF (QAWS .GT. -6.0) GO TO 200                                     DBL  190
      DBLTX=1.0                                                         DBL  200
      RETURN                                                            DBL  210
  200 DBLTX=EXP(-10**QAWS)                                              DBL  220
      RETURN                                                            DBL  230
      END                                                               DBL  240
      SUBROUTINE CLDPRF (K,ICLD,IHA1,IC1)                               CLD  100
C***********************************************************************CLD  110
C     WILL COMPUTE DENSITY    PROFILES FOR CLOUDS                       CLD  120
C***********************************************************************CLD  130
      REAL MDLWC                                                        CLD  140
      COMMON RELHUM(34),WHNO3(34),ICH(4),VH(17),TX(63),W(63)            CLD  150
      COMMON             WPATH(68, 63),TBBY(68)                         CLD  160
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         CLD  170
      COMMON/MODEL/ ZMDL(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),     CLD  180
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         CLD  190
      DIMENSION RHZONE(4)                                               CLD  200
      DIMENSION ELWCR(4),ELWCU(4),ELWCM(4),ELWCT(4)                     CLD  210
      DATA RHZONE/0.,70.,80.,99./                                       CLD  220
      DATA ELWCR/3.517E-04,3.740E-04,4.439E-04,9.529E-04/               CLD  230
      DATA ELWCM/4.675E-04,6.543E-04,1.166E-03,3.154E-03/               CLD  240
      DATA ELWCU/3.102E-04,3.802E-04,4.463E-04,9.745E-04/               CLD  250
      DATA ELWCT/1.735E-04,1.820E-04,2.020E-04,2.408E-04/               CLD  260
      DATA AFLWC/1.295E-02/,RFLWC/1.804E-03/,CULWC/7.683E-03/           CLD  270
      DATA ASLWC/4.509E-03/,STLWC/5.272E-03/,SCLWC/4.177E-03/           CLD  280
      DATA SNLWC/7.518E-03/,BSLWC/1.567E-04/,FVLWC/5.922E-04/           CLD  290
      DATA AVLWC/1.675E-04/,MDLWC/4.775E-04/                            CLD  300
      DATA TNLWC/3.446E-3/ ,TKLWC/5.811E-2/                             CLD  310
      IF(CLDAMT(K).LE.0.) GO TO 15                                      CLD  320
      IH=ICLD                                                           CLD  330
      IF(IH .EQ. 0) GO TO 200                                           CLD  340
      IF(ICLD .EQ. 18) THEN                                             CLD  350
           HAZEC(K) = CLDAMT(K)/TNLWC                                   CLD  360
           RETURN                                                       CLD  370
      ENDIF                                                             CLD  380
      IF(ICLD .EQ. 19) THEN                                             CLD  390
           HAZEC(K) = CLDAMT(K)/TKLWC                                   CLD  400
           RETURN                                                       CLD  410
      ENDIF                                                             CLD  420
      IF(ICLD .EQ. 20) THEN                                             CLD  430
           RETURN                                                       CLD  440
      ENDIF                                                             CLD  450
      GO TO(114,115,116,117,118,116,118,118,114,114,114),IH             CLD  460
114   HAZEC(K)=CLDAMT(K)/CULWC                                          CLD  470
      RETURN                                                            CLD  480
115   HAZEC(K)=CLDAMT(K)/ASLWC                                          CLD  490
      RETURN                                                            CLD  500
116   HAZEC(K)=CLDAMT(K)/STLWC                                          CLD  510
      RETURN                                                            CLD  520
117   HAZEC(K)=CLDAMT(K)/SCLWC                                          CLD  530
      RETURN                                                            CLD  540
118   HAZEC(K)=CLDAMT(K)/SNLWC                                          CLD  550
 15   RETURN                                                            CLD  560
200   IF(IHA1 .GT. 0) GO TO 205                                         CLD  570
      PRINT*,' WARNING ICLD NOT SET '                                   CLD  580
      RETURN                                                            CLD  590
205   CONTINUE                                                          CLD  600
      WRH = RELHUM(K)                                                   CLD  610
C                                                                       CLD  620
      M = IC1                                                           CLD  630
      IF (ICH(M).EQ.6.AND.M.NE.1) WRH=70.                               CLD  640
C     THIS CODING  DOES NOT ALLOW TROP RH DEPENDENT  ABOVE EH(7,I)      CLD  650
C     DEFAULTS TO TROPOSPHERIC AT 70. PERCENT                           CLD  660
      DO 210 I=2,4                                                      CLD  670
      IF (WRH.LT.RHZONE(I)) GO TO 215                                   CLD  680
  210 CONTINUE                                                          CLD  690
      I=4                                                               CLD  700
  215 II=I-1                                                            CLD  710
      IF(WRH.GT.0.0.AND.WRH.LT.99.)X=ALOG(100.0-WRH)                    CLD  720
      X1=ALOG(100.0-RHZONE(II))                                         CLD  730
      X2=ALOG(100.0-RHZONE(I))                                          CLD  740
      IF (WRH.GE.99.0) X=X2                                             CLD  750
      IF (WRH.LE.0.0) X=X1                                              CLD  760
      ITA=ICH(M)                                                        CLD  770
      IF(ITA.EQ.3. AND. M.EQ.1) GO TO 218                               CLD  780
      IF(ITA.GT.6) GO TO 245                                            CLD  790
CC                                                                      CLD  800
CC    MICROWAVE                                                         CLD  810
      N = 41                                                            CLD  820
CC                                                                      CLD  830
218   IF(N.GE.41. AND. ITA.EQ.3) ITA = 4                                CLD  840
C     RH DEPENDENT AEROSOLS                                             CLD  850
      GO TO (220,220,222,225,230,235), ITA                              CLD  860
 220  E2=ALOG(ELWCR(I))                                                 CLD  870
      E1=ALOG(ELWCR(II))                                                CLD  880
      GO TO 240                                                         CLD  890
 222  IF(M.GT.1) GO TO 225                                              CLD  900
      E2=ALOG(ELWCM(I))                                                 CLD  910
      E1=ALOG(ELWCM(II))                                                CLD  920
      GO TO 240                                                         CLD  930
  225 E2=ALOG(ELWCM(I))                                                 CLD  940
      E1=ALOG(ELWCM(II))                                                CLD  950
      GO TO 240                                                         CLD  960
  230 E2=ALOG(ELWCU(I))                                                 CLD  970
      E1=ALOG(ELWCU(II))                                                CLD  980
      GO TO 240                                                         CLD  990
  235 E2=ALOG(ELWCT(I))                                                 CLD 1000
      E1=ALOG(ELWCT(II))                                                CLD 1010
  240 EC=E1+(E2-E1)*(X-X1)/(X2-X1)                                      CLD 1020
      CON=EXP(EC)                                                       CLD 1030
      HAZEC(K) = CLDAMT(K)/CON                                          CLD 1040
      RETURN                                                            CLD 1050
  245 IF (ITA.GT.19) GO TO 275                                          CLD 1060
      ITC=ICH(M)-7                                                      CLD 1070
      IF (ITC.LT.1) RETURN                                              CLD 1080
      GO TO (250,255,280,260,265,270,265,270,260,260,270,275), ITC      CLD 1090
250   CON=AFLWC                                                         CLD 1100
      GO TO 280                                                         CLD 1110
255   CON=RFLWC                                                         CLD 1120
      GO TO 280                                                         CLD 1130
260   CON=BSLWC                                                         CLD 1140
      GO TO 280                                                         CLD 1150
265   CON=AVLWC                                                         CLD 1160
      GO TO 280                                                         CLD 1170
270   CON=FVLWC                                                         CLD 1180
      GO TO 280                                                         CLD 1190
275   CON=MDLWC                                                         CLD 1200
280   CONTINUE                                                          CLD 1210
      HAZEC(K) = CLDAMT(K)/CON                                          CLD 1220
      RETURN                                                            CLD 1230
      END                                                               CLD 1240
      FUNCTION   BBFN(T,V)                                              BBF  100
C***********************************************************************BBF  110
C   PLANCK BLACK BODY FUNCTION IN UNITS OF WATTS/(CM2 STER MICROMETER)  BBF  120
C***********************************************************************BBF  130
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     BBF  140
      BBFN = 0.0                                                        BBF  150
      IF(V. LE. 0.) RETURN                                              BBF  160
      X = 1.43879*V/T                                                   BBF  170
C*****PROTECT AGAINST EXPONENTIAL OVERLOW                               BBF  180
      IF(X.GT.BIGEXP) RETURN                                            BBF  190
      BBFN = 1.190956E-16*V**5/(EXP(X)-1.0)                             BBF  200
      RETURN                                                            BBF  210
      END                                                               BBF  220
      SUBROUTINE MSRAD(IV,V,ISOURC,IDAY,ANGLEM,SS,                      MSR  100
     2     DMF,DMFS,UMF,UMFS)                                           MSR  110
C                                                                       MSR  120
C         (1) SETS UP OPTICAL PROPERTIES PROFILES FOR VERTICAL          MSR  130
C             PATH                                                      MSR  140
C         (2) CALLS FLXADD WHICH RETURNS MS SOURCE FUNCTION, J,         MSR  150
C             FOR PATH                                                  MSR  160
C         (3) EVALUATES PATH INTEGRAL OF SOURCE FUNCTION, I.E.          MSR  170
C             MS RADIANCE CONTRIBUTION                                  MSR  180
C                                                                       MSR  190
C                                 A.E.R. 1986                           MSR  200
C                                                                       MSR  210
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  MSR  220
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   MSR  230
     1    RAINRT                                                        MSR  240
      COMMON /CNTRL/ KMAX,M,IKMAX,NL,ML,IKLO,ISSGEO,IMULT               MSR  250
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            MSR  260
      COMMON             WPATH(68, 63),TBBY(68)                         MSR  270
      COMMON ABSC(5,47),EXTC(5,47),ASYC(5,47),VX2(47),AWCCON(5)         MSR  280
      COMMON /AER/ XX1,XX2,XX3,XX4,XX5,                                 MSR  290
     X YY1,YY2,YY3,YY4,YY5,ZZ1,ZZ2,ZZ3,ZZ4,ZZ5                          MSR  300
      COMMON /SOLS/ AH1(68),ARH(68),                                    MSR  310
     X   WPATHS(68,63),PA(68),PR(68),ATHETA(35),ADBETA(35),LJ(69),JTURN,MSR  320
     X ANGSUN,CSZEN(68)                                                 MSR  330
C                                                                       MSR  340
      COMMON /TRAN/ABB(19),                                             MSR  350
     1                     BTOP(50),BMID(34),TMD(34),TMOLS(34),         MSR  360
     2 TAERS(34),TCONT(34),DCONT(34)                                    MSR  370
      COMMON /MNLT/TBBSS(68),TBBMS(34),WPMS(34,63),IMSMX,WPMSS(34,63)   MSR  380
      COMMON /MSRD/TLE(34),COSBAR(34),OMEGA0(34),UPF(10,34),DNF(10,34), MSR  390
     1 TAER(50),                                             DTX7(34),  MSR  400
     2 DAERS(34),ASYIK(34),ASYDM(34),                                   MSR  410
     3 STRN(0:34),DMOLS(34), DSTRN(0:34),FDNSRF    ,FDNSRT,IKMX,        MSR  420
     4               FDNTRF    ,FDNTRT                                  MSR  430
      DIMENSION         DMF(34),DMFS(34),UMF(34),UMFS(34)               MSR  440
C                                                                       MSR  450
      IKMAX = IKMAX + 1                                                 MSR  460
      IKM=IKMAX-1                                                       MSR  470
C                                                                       MSR  480
C  FOR H1 < H2, CALCULATE DOWNWARD RADIANCE                             MSR  490
C                                                                       MSR  500
C                                                                       MSR  510
      DO 222 IK=IKLO,IKM-1                                              MSR  520
C                                                                       MSR  530
C  AEROSOL PATH FOR EACH LAYER (NOT CUMULATIVE)                         MSR  540
C                                                                       MSR  550
C                                                                       MSR  560
C  SOLAR TRANSMITTANCE FROM TOP OF ATMOSPHERE                           MSR  570
C                                                                       MSR  580
      STRN(IK-1)=DSTRN(IKMAX-IK)                                        MSR  590
      IF(IHAZE .GT. 0) THEN                                             MSR  600
C                                                                       MSR  610
C  CALCULATE AEROSOL ASYMMETRY FACTOR FOR GIVEN LAYER                   MSR  620
C                                                                       MSR  630
      DENOM  =  ASYDM(IK)                                               MSR  640
      IF(DENOM .LE. 0.) THEN                                            MSR  650
      COSBAR(IKM-IK) = 0.                                               MSR  660
      ELSE                                                              MSR  670
      COSBAR(IKM-IK)= ASYIK(IK)/DENOM                                   MSR  680
      ENDIF                                                             MSR  690
      ENDIF                                                             MSR  700
C                                                                       MSR  710
C  TAER,TAERS, AND TMOLS ARE TOTAL AEROSOL, AEROSOL SCATTERING, AND     MSR  720
C  MOLECULAR SCATTERING OPTICAL THICKNESS FOR GIVEN LAYER               MSR  730
C                                                                       MSR  740
      TAER(IKM-IK)=DTX7(IK+1)-DTX7(IK)                                  MSR  750
      TAERS(IKM-IK)=DAERS(IK+1)-DAERS(IK)                               MSR  760
      TMOLS(IKM-IK)=DMOLS(IK+1)-DMOLS(IK)                               MSR  770
      IKMIK = IKM-IK                                                    MSR *772
      IF(TAER (IKMIK) .LT. 0)TAER (IKMIK) = 0.                          MSR *774
      IF(TAERS(IKMIK) .LT. 0)TAERS(IKMIK) = 0.                          MSR *776
      IF(TMOLS(IKMIK) .LT. 0)TMOLS(IKMIK) = 0.                          MSR *778
C                                                                       MSR  780
C  WEIGHT ASYMMETRY FACTOR                                              MSR  790
C                                                                       MSR  800
      IF(TAERS(IKM-IK) .LE.0.) THEN                                     MSR  810
            COSBAR(IKM-IK) = 0.                                         MSR  820
      ELSE                                                              MSR  830
      COSBAR(IKM-IK)=COSBAR(IKM-IK)*TAERS(IKM-IK)/(TAERS(IKM-IK)+       MSR  840
     1 TMOLS(IKM-IK))                                                   MSR  850
      ENDIF                                                             MSR  860
C                                                                       MSR  870
C  TCONT IS MOLECULAR CONTINUUM OPTICAL THICKNESS FOR GIVEN LAYER       MSR  880
C                                                                       MSR  890
222   TCONT(IKM-IK)=DCONT(IK+1)-DCONT(IK)                               MSR  900
      IF(IHAZE .GT. 0) THEN                                             MSR  910
C                                                                       MSR  920
C  CALCULATE ASYMMETRY FACTOR FOR BOTTOM OF ATMOSPHERE                  MSR  930
C                                                                       MSR  940
      DENOM =  ASYDM(1)                                                 MSR  950
      IF(DENOM .LE. 0.)  THEN                                           MSR  960
            COSBAR(IKM) = 0.                                            MSR  970
      ELSE                                                              MSR  980
      COSBAR(IKM)= ASYIK(IK)/DENOM                                      MSR  990
      ENDIF                                                             MSR 1000
      ENDIF                                                             MSR 1010
      STRN(IKM-1)=DSTRN(IKMAX-IKM)                                      MSR 1020
      STRN(IKM)=DSTRN(0)                                                MSR 1030
      TAER(IKM)=DTX7(1)                                                 MSR 1040
      TAERS(IKM)=DAERS(1)                                               MSR 1050
      TMOLS(IKM)=DMOLS(1)                                               MSR 1060
C                                                                       MSR 1070
C   WEIGHT ASYMMETRY FACTOR                                             MSR 1080
C                                                                       MSR 1090
      IF(TAERS(IKM) .LE.0.) THEN                                        MSR 1100
            COSBAR(IKM) = 0.                                            MSR 1110
      ELSE                                                              MSR 1120
      COSBAR(IKM)=COSBAR(IKM)*TAERS(IKM)/(TAERS(IKM)+TMOLS(IKM))        MSR 1130
      ENDIF                                                             MSR 1140
      TCONT(IKM)=DCONT(1)                                               MSR 1150
C                                                                       MSR 1160
C   FOR H1 > H2, CALCULATE UPWARD RADIANCE                              MSR 1170
C                                                                       MSR 1180
C                                                                       MSR 1190
C   CALCULATE SOLAR INTENSITY AT TOP OF ATMOSPHERE                      MSR 1200
C                                                                       MSR 1210
      S0 = 0.                                                           MSR 1220
      IF(IEMSCT .EQ. 2) CALL SOURCE(V,ISOURC,IDAY,ANGLEM,S0)            MSR 1230
C                                                                       MSR 1240
C                                                                       MSR 1250
C   CALL FLUX ADDING SUBROUTINE                                         MSR 1260
C                                                                       MSR 1270
      CALL FLXADD (TLE,COSBAR,TAER,OMEGA0,UPF,DNF,IV,     IKMAX,        MSR 1280
     2 CSZEN,S0,STRN,            FDNSRT,FDNTRT,                         MSR 1290
     3     DMF,DMFS,UMF,UMFS)                                           MSR 1300
      RETURN                                                            MSR 1310
      END                                                               MSR 1320
      SUBROUTINE FLXADD (TLE,COSBAR,TAER,OMEGA0,UPF,DNF,IWAVN,          FLX  100
     1 IKMAX,     CSZEN,S0,STRN,            FDNSRF,FDNTRF,              FLX  110
     2     DMF,DMFS,UMF,UMFS)                                           FLX  120
C                                                                       FLX  130
C         (1) CALCULATES UPWARD, F+ (UPF), AND DOWNWARD, F- (DNF),      FLX  140
C             FLUX PROFILES USING ADDING METHOD                         FLX  150
C         (2) MS SOURCE FUNCTION EVALUATED FROM LEVEL FLUXES VIA        FLX  160
C             STREAM APPROXIMATION (SEE EQN     , ISAACS ET AL, 1986)   FLX  170
C         (3) BACKSCATTER FOR SOURCE FUNCTION FOR GIVEN G FACTOR        FLX  180
C             AND ZENTIH ANGLE OBTAINED USING FUNCTION BETABS           FLX  190
C                                                                       FLX  200
C                                          A.E.R. 1986                  FLX  210
C                                                                       FLX  220
      DOUBLE PRECISION AER1,AER2,AERA,AERU,AERV,AERC,AERCX,EX1,EX2,     FLX* 222
     1                 DENO,DNMO,DNM1                                   FLX* 224
      COMMON /IFIL/IRD,IPR,IPU,NPR,IPR1                                 FLX  230
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            FLX  240
      COMMON             WPATH(68, 63),TBBY(68)                         FLX* 242
      COMMON ABSC(5,47),EXTC(5,47),ASYM(5,47),VX2(47),AWCCON(5)         FLX* 244
C                                                                       FLX  250
      COMMON /TRAN/ABB(19),                                             FLX  260
     1                     BTOP(50),BMID(34),TMD(34),TMOLS(34),         FLX  270
     1 TAERS(34),TCONT(34),DCONT(34)                                    FLX  280
      COMMON /AABBCC/ AA(11),BB(11),CC(11),IBND(11),A(11),CPS(11)       FLX  290
      COMMON/CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB   FLX  300
      COMMON/MODEL/ZM(34),PM(34),TM(34),DUM(34),DENSTY(63,34),          FLX  310
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         FLX  320
      COMMON /CNTRL/ KMAX,M7,JKMAX,NL,ML,JKLO,ISSGEO,IMULT              FLX  330
      COMMON /PATH/ PL(68),QTHETA(68),ITEST,H1S,H2S,AHT(68)             FLX  340
      COMMON /CNSTNS/PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                      FLX  350
      COMMON /DIRST/  SUN,UMB(34),DMB(34),UMBS(34),DMBS(34)             FLX  360
      DIMENSION COSBAR(34),DNF(10,34),                                  FLX  370
     1 EDN(50),EUP(50),EUPC(50),TAUM(3,50),                             FLX  380
     2                        TDF(50),TLE(34),OMEGA0(34),               FLX  390
     3 UPF(10,34),                  RUPC(50),TAER(50),REF(50),          FLX  400
     4 EUPS(34),EDNS(34),UPFS(15,34),DNFS(15,34),EUPCS(34),             FLX  410
     5 CSZEN(68),STRN(0:34),BETS(34),           TWGP(3,34),RUPCS(34),   FLX  420
     6 UMF(34),DMF(34),UMFS(34),DMFS(34),REFS(34),TDFS(34)              FLX  430
      DIMENSION OMEGAK(34),TAUT(34)                                     FLX  440
      DIMENSION FAC(3),GKWJ(3,11),DPWJ(3,11)                            FLX  450
      DIMENSION CP1S(11),DPJ(3,34)                                      FLX  460
C***********************************************************************FLX  470
C                                                                       FLX  480
C   INPUT PARAMETERS:                                                   FLX  490
C   ----------------                                                    FLX  500
C   TLE(N)     - TEMPERATURE (KELVIN) OF UPPER EDGE OF LAYER N          FLX  510
C   COSBAR(N)  - ASYMMETRY FACTOR FOR LAYER N                           FLX  520
C   TAER(N)    - TOTAL AEROSOL OPTICAL THICKNESS OF LAYER N             FLX  530
C   TAERS(N)   - AEROSOL SCATTERING OPTICAL THICKNESS OF LAYER N        FLX  540
C   TCONT(N)   - MOLECULAR CONTINUUM OPTICAL THICKNESS FOR LAYER N      FLX  550
C   TMOLS(N)   - RAYLEIGH SCATTERING OPTICAL THICKNESS FOR LAYER N      FLX  560
C   CSZEN(N)   - COSINE OF SOLAR ZENITH ANGLE FOR LAYER N               FLX  570
C   IWAVN      - FREQUENCY (WAVENUMBER)                                 FLX  580
C   S0         - SOLAR INTENSITY AT TOP OF ATMOSPHERE                   FLX  590
C   H1         - ALTITUDE OF OBSERVER (KM)                              FLX  600
C   H2         - ALTITUDE OF TARGET (KM)                                FLX  610
C   STRN(N)    - SOLAR TRANSMISSION TO BOTTOM OF LAYER N                FLX  620
C                                                                       FLX  630
C   OUTPUT PARAMETERS:                                                  FLX  640
C   -----------------                                                   FLX  650
C   OMEGAK  (N)- SINGLE SCATTERING ALBEDO OF LAYER N FOR VALUE OF K     FLX  660
C   TAUM(K,N)  - MOLECULAR OPTICAL THICKNESS OF LAYER N FOR VALUE OF K  FLX  670
C   BTOP(N)    - BLACK BODY FLUX FOR UPPER EDGE TEMPERATURE OF LAYER N  FLX  680
C   BMID(N)    - BLACK BODY FLUX FOR AVERAGE TEMPERATURE OF LAYER N     FLX  690
C                                                                       FLX  700
C   INTERNAL PARAMETERS:                                                FLX  710
C   -------------------                                                 FLX  720
C   TWGP(K,N)  - SUM OF OPTICAL DEPTH * PROBABILITY BY MOLECULE         FLX  730
C                                                                       FLX  740
C   UPF(K,N)   - UPWARD THERMAL FLUX AT UPPER EDGE OF LAYER N FOR       FLX  750
C                VALUE OF K                                             FLX  760
C   UPFS(K,N)  - UPWARD SOLAR FLUX AT UPPER EDGE OF LAYER N FOR         FLX  770
C                VALUE OF K                                             FLX  780
C   DNF(K,N)   - DOWNWARD THERMAL FLUX AT UPPER EDGE OF LAYER N FOR     FLX  790
C                VALUE OF K                                             FLX  800
C   DNFS(K,N)  - DOWNWARD SOLAR FLUX AT UPPER EDGE OF LAYER N FOR       FLX  810
C                VALUE OF K                                             FLX  820
C   UMF(N)     - MEAN UPWARD THERMAL FLUX OF LAYER N FOR VALUE OF K     FLX  830
C   UMFS(N)    - MEAN UPWARD SOLAR FLUX OF LAYER N FOR VALUE OF K       FLX  840
C   DMF(N)     - MEAN DOWNWARD THERMAL FLUX OF LAYER N FOR VALUE OF K   FLX  850
C   DMFS(N)    - MEAN DOWNWARD SOLAR FLUX OF LAYER N FOR VALUE OF K     FLX  860
C   SU(K,N)    - UPWARD THERMAL RADIANCE SOURCE FUNCTION FOR N,K        FLX  870
C   SUS(K,N)   - UPWARD SOLAR RADIANCE SOURCE FUNCTION FOR N,K          FLX  880
C   SD(K,N)    - DOWNWARD THERMAL RADIANCE SOURCE FUNCTION FOR N,K      FLX  890
C   SDS(K,N)   - DOWNWARD SOLAR RADIANCE SOURCE FUNCTION FOR N,K        FLX  900
C   BETS(N)    - BACKSCATTER FRACTION ALONG SOLAR PATH AT LAYER N       FLX  910
C                                                                       FLX  920
C***********************************************************************FLX  930
C                                                                       FLX  940
C   NEW VARABLES          11 MOLECULES  BY JOSEPH H PIERLUISSI          FLX  950
C   DPWJ  PROBABILITY FOR EACH MOLECULE  FIT DOUBLE EXPODENTIAL         FLX  960
C   GKWJ  BAND DEPENDENT  SCALING OF DENSITYS TO GET K  AMOUNT          FLX  970
C   CPS IS THE STORED VALUES OF PIERLUISSI BAND MODEL COEFFIECNTS       FLX  980
C   CP1S = 10**CPS                                                      FLX  990
C   IBND MAPS BANDS TO MOLECULES                                        FLX 1000
C   DPJ REPLACES DPO  IS THE  EFFECTIVE PROBILITY BY LAYER              FLX 1010
C   TAUM IS DEFINED AS THE SUM OF THE OPTICAL DEPTHS BY MOLECULE        FLX 1020
C                                                                       FLX 1030
      DATA RUPC/50*0.0/,RUPCS/34*0.0/                                   FLX 1040
      DATA EUPS/34*0.0/,EDNS/34*0.0/                                    FLX 1050
      DATA REFS/34*0.0/,TDFS/34*0.0/                                    FLX 1060
      DATA UPFS/510*0.0/,DNFS/510*0.0/                                  FLX 1070
      DATA FAC /1.0,0.09,0.015/                                         FLX 1080
320   FORMAT(5E14.6)                                                    FLX 1090
300   FORMAT(10F7.2)                                                    FLX 1100
310   FORMAT(10F7.4)                                                    FLX 1110
      DPC = 1./3.                                                       FLX 1120
      WAVN=FLOAT(IWAVN)                                                 FLX 1130
      V = WAVN                                                          FLX 1140
CC    PRINT 1900                                                        FLX 1150
1900  FORMAT('      V    FLXDN     FLXDNS    FLXUP     FLXUPS')         FLX 1160
      NLAYRS = ML - 1                                                   FLX 1170
      NG=NLAYRS+1                                                       FLX 1180
      NG1=NG+1                                                          FLX 1190
      DO  1 M = 1,11                                                    FLX 1200
1     CP1S(M)= 10.**CPS(M)                                              FLX 1210
C                                                                       FLX 1220
      DO  4  MOL = 1,11                                                 FLX 1230
      DO  5    K = 1,3                                                  FLX 1240
      IW = IBND(MOL)                                                    FLX 1250
      GKWJ(K,MOL) = 0.                                                  FLX 1260
      DPWJ(K,MOL) = 0.                                                  FLX 1270
      IF(IW. LE. 0) GO TO 5                                             FLX 1280
      GKWJ(K,MOL) = FAC(K) * CC(MOL)                                    FLX 1290
      IF(K .EQ. 1) DPWJ(K,MOL) = AA(MOL)                                FLX 1300
      IF(K .EQ. 2) DPWJ(K,MOL) = BB(MOL)                                FLX 1310
      IF(K .EQ. 3) DPWJ(K,MOL) = 1. - AA(MOL) - BB(MOL)                 FLX 1320
      IF(K. GT. 1) GO TO 5                                              FLX 1330
  5   CONTINUE                                                          FLX 1340
 4    CONTINUE                                                          FLX 1350
      TLE(NG1)=TLE(NG)                                                  FLX 1360
      DO 98 N=1,NG                                                      FLX 1370
C                                                                       FLX 1380
C   BLACK BODY FLUX FOR LAYER EDGE TEMPERATURE                          FLX 1390
C                                                                       FLX 1400
      BTOP(N)=BBFN(TLE(N),V)*PI                                         FLX 1410
C                                                                       FLX 1420
C   BLACK BODY RADIANCE FOR LAYER MIDDLE TEMPERATURE                    FLX 1430
C                                                                       FLX 1440
      BMID(N)=BBFN(TMD(N),V)                                            FLX 1450
      IF(N .LT. NG) THEN                                                FLX 1460
C                                                                       FLX 1470
C    CALCULATE COSINE OF VIEWING PATH ZENITH ANGLE                      FLX 1480
C                                                                       FLX 1490
C                                                                       FLX 1500
C    CALCULATE BACKSCATTER PARAMETERS                                   FLX 1510
C                                                                       FLX 1520
      IF(COSBAR(N) .EQ. 0.0) THEN                                       FLX 1530
      BETS(N)=.5                                                        FLX 1540
      ELSE                                                              FLX 1550
      IF((IEMSCT .EQ. 2) .OR. (IEMSCT .EQ. 3)) THEN                     FLX 1560
      BETS(N)=BETABS(CSZEN(N),COSBAR(N))                                FLX 1570
      ENDIF                                                             FLX 1580
      ENDIF                                                             FLX 1590
      ENDIF                                                             FLX 1600
   98 CONTINUE                                                          FLX 1610
C                                                                       FLX 1620
C   ABSORPTION COEFFICIENTS FOR WATER VAPOR, UNIFORMLY MIXED GASES      FLX 1630
C   AND OZONE                                                           FLX 1640
C                                                                       FLX 1650
C                                                                       FLX 1660
C   EVALUATE THE WEIGHTED K DISTRIBUTION QUANTITIES FOR                 FLX 1670
C   WATER VAPOR AND THE UNIFORMLY MIXED GASES                           FLX 1680
C                                                                       FLX 1690
      DO 70 K=1, 3                                                      FLX 1700
      DO 70 N=1,NLAYRS                                                  FLX 1710
C                                                                       FLX 1720
      TAUM(K,N)  = 0.                                                   FLX 1730
      TWGP(K,N)  = 0.                                                   FLX 1740
      DO 60 MOL = 1,11                                                  FLX 1750
      IB = IBND(MOL)                                                    FLX 1760
      IF(IB .LT.0) GO TO 60                                             FLX 1770
      W(IB) = DENSTY(IB,IKMAX-N)*PL(IKMAX-N)*GKWJ(K,MOL)                FLX 1780
      TAUM(K,N)= TAUM(K,N) + W(IB) * CP1S(MOL)                          FLX 1790
      TWGP(K,N)= TWGP(K,N) + W(IB) * CP1S(MOL) * DPWJ(K,MOL)            FLX 1800
60    CONTINUE                                                          FLX 1810
      DPJ(K,N) = DPC                                                    FLX 1820
      IF(TAUM(K,N) .NE .0) THEN                                         FLX 1830
         DPJ(K,N) = TWGP(K,N) / TAUM(K,N)                               FLX 1840
      ENDIF                                                             FLX 1850
C                                                                       FLX 1860
C     EFFECTIVE PROBILITY BY LAYER DPJ IS BASED ON MOLECULAR            FLX 1870
C     PROBILITY WEIGHTED BY OPTICAL DEPTH                               FLX 1880
C                                                                       FLX 1890
  70  CONTINUE                                                          FLX 1900
      DO 80 N=1,NLAYRS                                                  FLX 1910
      SMDPJ = DPJ(1,N) + DPJ(2,N) + DPJ(3,N)                            FLX 1920
      DO 80 K=1, 3                                                      FLX 1930
      DPJ(K,N) = DPJ(K,N)/SMDPJ                                         FLX 1940
  80  CONTINUE                                                          FLX 1950
C                                                                       FLX 1960
C     EFFECTIVE PROBILITY BY LAYER DPJ NORMALIZED                       FLX 1970
C     FOR CURRENT VALUES OF GKWJ  NORMALIZATION NOT REQUIRED            FLX 1980
C     PROTECTION FOR ARBITARY CHOICE OF GKWJ                            FLX 1990
C                                                                       FLX 2000
C***********************************************************************FLX 2010
C*    PROBABILITY INTEGRATION LOOP                                     *FLX 2020
C***********************************************************************FLX 2030
      DO 100 K=1,3                                                      FLX 2040
C**** COMPOSITE DOWNWARDS REFLECTION                                    FLX 2050
      RDNCN=0.                                                          FLX 2060
      RDNCNS=0.0                                                        FLX 2070
C***********************************************************************FLX 2080
C*    ADDING GROUND LAYER                                              *FLX 2090
C***********************************************************************FLX 2100
C                                                                       FLX 2110
C   DEFINE INITIAL UPWARD COMPOSITE SURFACE REFLECTANCE                 FLX 2120
C                                                                       FLX 2130
      RUPCN=SALB                                                        FLX 2140
      RUPCNS=SALB                                                       FLX 2150
C                                                                       FLX 2160
C   SURFACE THERMAL EMISSION                                            FLX 2170
C                                                                       FLX 2180
      BTOP(NG1)=BBFN(TLE(NG1),V)*PI                                     FLX 2190
C                                                                       FLX 2200
C   SURFACE EMISSION                                                    FLX 2210
C                                                                       FLX 2220
      EUPCN=(1.0-RUPCN)*BTOP(NG1)                                       FLX 2230
      EUP(NG)=EUPCN                                                     FLX 2240
      EUPC(NG)=EUPCN                                                    FLX 2250
      IF(IEMSCT .EQ. 2) THEN                                            FLX 2260
      MF = ML - 1                                                       FLX 2270
      EUPCNS=RUPCNS*CSZEN(MF)*STRN(MF)*S0                               FLX 2280
      ELSE                                                              FLX 2290
      EUPCNS=0.0                                                        FLX 2300
      ENDIF                                                             FLX 2310
      EUPS(NG)=EUPCNS                                                   FLX 2320
      EUPCS(NG)=EUPCNS                                                  FLX 2330
C                                                                       FLX 2340
C***********************************************************************FLX 2350
C*      UPWARD ADDING LOOP(1-N)                                        *FLX 2360
C***********************************************************************FLX 2370
      DO 101 N=NLAYRS,1,-1                                              FLX 2380
      M=N-1                                                             FLX 2390
C                                                                       FLX 2400
C    CALCULATE MOLECULAR OPTICAL THICKNESS OF EACH LAYER FOR EACH K     FLX 2410
C    NOTE: TOP LAYER IS LAYER 1                                         FLX 2420
C                                                                       FLX 2430
C                                                                       FLX 2440
C      EUP AND EDN ARE UPWARD AND DOWNWARD THERMAL FLUX                 FLX 2450
C      FOR AN ISOLATED LAYER                                            FLX 2460
C      TDF AND REF ARE THE THERMAL TRANSMISSION AND REFLECTANCE         FLX 2470
C      FOR AN ISOLATED LAYER                                            FLX 2480
      IF(K.EQ.1) THEN                                                   FLX 2490
        TAUT(N) = TAUM(K,N) * DPJ(K,N)                                  FLX 2500
      ELSE                                                              FLX 2510
        TAUT(N) = TAUM(K,N) * DPJ(K,N) + TAUT(N)                        FLX 2520
        DENOM =  (TAUT(N)+TAER(N)+TCONT(N)+TMOLS(N))                    FLX 2530
        IF(DENOM .LE. 0.) DENOM = 1.                                    FLX 2540
        IF(K.EQ.3) OMEGA0(N) = (TAERS(N) + TMOLS(N)) /                  FLX 2550
     X  DENOM                                                           FLX 2560
      ENDIF                                                             FLX 2570
C                                                                       FLX 2580
C    XP IS THE PATH OPTICAL THICKNESS FOR A GIVEN LAYER OF PATH         FLX 2590
C    LENGTH PL                                                          FLX 2600
C                                                                       FLX 2610
      XP=TAUM(K,N)+TAER(N)+TCONT(N)+TMOLS(N)                            FLX 2620
C                                                                       FLX 2630
C    X IS THE NORMAL PATH OPTICAL THICKNESS FOR A GIVEN LAYER           FLX 2640
C                                                                       FLX 2650
      X=XP*(ZM(IKMAX-N+1)-ZM(IKMAX-N))/PL(IKMAX-N)                      FLX 2660
      OMEGAK(N)=(TAERS(N)+TMOLS(N))/XP                                  FLX 2670
C                                                                       FLX 2680
C   USE TWO STREAM APPROXIMATION FOR THERMAL                            FLX 2690
C                                                                       FLX 2700
160   CONTINUE                                                          FLX 2710
      IF(OMEGAK(N).GT.0.99999) GO TO 115                                FLX 2720
      AER1=1.0-OMEGAK(N)                                                FLX 2730
      AER2=1.0-OMEGAK(N)*COSBAR(N)                                      FLX 2740
      AERA=SQRT(AER1/AER2)                                              FLX 2750
      AERU=(1.0-AERA)/2.0                                               FLX 2760
      AERV=(1.0+AERA)/2.0                                               FLX 2770
      AERC=SQRT(3.0*AER1*AER2)                                          FLX 2780
      AERCX = AERC*X                                                    FLX 2790
      IF(AERCX.LT.BIGEXP) THEN                                          FLX 2800
           EX1=EXP(-AERCX)                                              FLX 2810
      ELSE                                                              FLX 2820
           EX1 = 1./BIGNUM                                              FLX 2830
      ENDIF                                                             FLX 2840
      EX2=EX1*EX1                                                       FLX 2850
      DENO=(AERV*AERV-AERU*AERU*EX2)                                    FLX 2860
      DNMO=(BTOP(N)-BTOP(N+1))/X/AERC*(AERV-AERU*EX2-AERA*EX1)          FLX 2870
      DNM1=AERV+AERU*EX2                                                FLX 2880
      EUP(N)=(BTOP(N)*DNM1-DNMO-BTOP(N+1)*EX1)/DENO*AERA                FLX 2890
      EDN(N)=(BTOP(N+1)*DNM1+DNMO-BTOP(N)*EX1)/DENO*AERA                FLX 2900
      IF(EUP(N) .LT. 0)EUP(N) = 0.                                      FLX*2902
      IF(EDN(N) .LT. 0)EDN(N) = 0.                                      FLX*2904
      REF(N)=AERU*AERV*(1.0-EX2)/DENO                                   FLX 2910
      TDF(N)=(AERV-AERU)/DENO*EX1                                       FLX 2920
      GOTO 114                                                          FLX 2930
115    TDF(N)=1./(1.+SQRT(3.)*(1.-COSBAR(N))/2.*X)                      FLX 2940
      REF(N)=1.-TDF(N)                                                  FLX 2950
      EUP(N)=0.0                                                        FLX 2960
      EDN(N)=0.0                                                        FLX 2970
114    CONTINUE                                                         FLX 2980
      IF(IEMSCT .EQ. 2)THEN                                             FLX 2990
C                                                                       FLX 3000
C     CALCULATE PARAMETERS FOR SOLAR HYBRID MODIFIED                    FLX 3010
C     DELTA EDDINGTON 2-STREAM APPROXIMATION                            FLX 3020
C                                                                       FLX 3030
      SMU=CSZEN(N)                                                      FLX 3040
      B0=BETS(N)                                                        FLX 3050
      OME0=OMEGAK(N)                                                    FLX 3060
      IF(OME0 .GE. .999999) OME0 = .999999                              FLX 3070
C                                                                       FLX 3080
C      OME0 TEST MAY BE MACHINE DEPENDENT                               FLX 3090
C                                                                       FLX 3100
C                                                                       FLX 3110
C      RECODED AS BS  FC AND AS*EXP(SK*X)  FOR IMPROVED                 FLX 3120
C      ACCURACY IN SOLAR FLUX CALCULATIONS FOR LARGE X                  FLX 3130
C      ( NON MACHINE DEPENDANT )                                        FLX 3140
C                                                                       FLX 3150
      G=COSBAR(N)                                                       FLX 3160
      Q=4.0*(1.0-G**2.*(1.0-SMU))                                       FLX 3170
      GM1=(7.0-3.0*G**2.-OME0*(4.0+3.0*G)+OME0*G**2.*(4.0*B0+3.0*G))/Q  FLX 3180
      GM2=-(1.0-G**2.-OME0*(4.0-3.0*G)-OME0*G**2.*(4.0*B0+3.0*G-4.0))/Q FLX 3190
      IF(GM2  .EQ. 0.0) THEN                                            FLX 3200
          GM2=1.0E-10                                                   FLX 3210
      ENDIF                                                             FLX 3220
      SK=SQRT(GM1*GM1-GM2*GM2)                                          FLX 3230
      CS=           OME0*(BETS(N)/SMU-GM1*BETS(N)-GM2*                  FLX 3240
     2 (1.-BETS(N)))*(SMU*SMU/(1.-SK*SK*SMU*SMU))                       FLX 3250
      YS=CS*(GM1+1./SMU)-           OME0*BETS(N)                        FLX 3260
CC    E1=YS/(GM1-SK)                                                    FLX 3270
CC    E2=-CS                                                            FLX 3280
CC    E3=(GM1+SK)/(SK-GM1)                                              FLX 3290
CC    E4=1.                                                             FLX 3300
CCC   BS=(E1*EXP(SK*X)+E2*EXP(-X/SMU))/(E3*EXP(SK*X)+E4*EXP(-SK*X))     FLX 3310
CCC   AS=(BS*(GM1+SK)+YS)/(SK-GM1)                                      FLX 3320
C                                                                       FLX 3330
C    CALCULATE UPWARD & DOWNWARD FLUX, EACH LAYER ALONE                 FLX 3340
C                                                                       FLX 3350
CCC   EUPS(N)=AS+BS+CS                                                  FLX 3360
CCC   EDNS(N)=(AS*(GM1-SK)*EXP(SK*X)+BS*(GM1+SK)*EXP(-SK*X)+            FLX 3370
CCC  2 YS*EXP(-X/SMU))/GM2                                              FLX 3380
C                                                                       FLX 3390
C     NEW SECTION                                                       FLX 3400
C                                                                       FLX 3410
      EKTL = SK * X                                                     FLX 3420
      IF(EKTL .LT.BIGEXP) THEN                                          FLX 3430
           EKT = EXP(-EKTL)                                             FLX 3440
      ELSE                                                              FLX 3450
           EKT = 1./BIGNUM                                              FLX 3460
      ENDIF                                                             FLX 3470
      EKT2 = EKT * EKT                                                  FLX 3480
      ETMUL = X / SMU                                                   FLX 3490
      IF(ETMUL .LT. BIGEXP) THEN                                        FLX 3500
           ETMU = EXP(-ETMUL)                                           FLX 3510
      ELSE                                                              FLX 3520
           ETMU = 1./BIGNUM                                             FLX 3530
      ENDIF                                                             FLX 3540
CC    ASXP =-1.* ((GM1+SK)*E2*ETMU+YS*E4*EKT)/(E3+E4*EKT2)              FLX 3550
      GMPSK = GM1+SK                                                    FLX 3560
      GMMSK = GM1-SK                                                    FLX 3570
      DENOM = (EKT2*GMMSK)-GMPSK                                        FLX 3580
      ASXP =-GMMSK*(YS*EKT-GMPSK*CS*ETMU)/DENOM                         FLX 3590
CC    ASNEW=-1.* ((GM1+SK)*E2*ETMU*EKT+YS*E4*EKT2)/(E3+E4*EKT2)         FLX 3600
      ASNEW=-1.* (-GMPSK*CS*ETMU*EKT+YS*EKT2)/DENOM                     FLX 3610
CC    BSNEW = (E1 + E2 * ETMU * EKT)/(E3 + E4*EKT2)                     FLX 3620
      BSNEW = (YS -(CS * ETMU * EKT*GMMSK))/DENOM                       FLX 3630
      EUPS(N)=ASNEW+BSNEW+CS                                            FLX 3640
CC    EDNS(N)=(ASXP+BSNEW*(GM1+SK)*EXP(-SK*X)+                          FLX 3650
CC   2 YS*EXP(-X/SMU))/GM2                                              FLX 3660
      EDNS(N)=(ASXP+BSNEW*(GMPSK)*EKT+YS*ETMU)/GM2                      FLX 3670
      FC=1./(CSZEN(N))                                                  FLX 3680
C                                                                       FLX 3690
C   REFS & TDFS ARE REFLECT & TRANSMITT FOR INDIVIDUAL LAYERS,SOLAR     FLX 3700
C                                                                       FLX 3710
           REFS(N)=EUPS(N)*FC                                           FLX 3720
           XCSZEN = X/CSZEN(N)                                          FLX 3730
           IF(XCSZEN.LT.BIGEXP) THEN                                    FLX 3740
                TDFS(N)=EDNS(N)*FC+EXP(-XCSZEN)                         FLX 3750
      ELSE                                                              FLX 3760
                TDFS(N)=EDNS(N)*FC                                      FLX 3770
      ENDIF                                                             FLX 3780
           EUPS(N) = EUPS(N) * S0 * STRN(M)                             FLX 3790
           EDNS(N) = EDNS(N) * S0 * STRN(M)                             FLX 3800
      ENDIF                                                             FLX 3810
      DENO=1.0-RUPCN*REF(N)                                             FLX 3820
      DENOS=1.0-RUPCNS*REFS(N)                                          FLX 3830
C                                                                       FLX 3840
C    CALCULATE COMPOSITE FLUXES AND REFLECTANCES                        FLX 3850
C                                                                       FLX 3860
125   EUPCN=EUP(N)+(EUPCN+EDN(N)*RUPCN)*TDF(N)/DENO                     FLX 3870
      EUPCNS=EUPS(N)+(EUPCNS+EDNS(N)*RUPCNS)*TDFS(N)/DENOS              FLX 3880
      RUPCNS=REFS(N)+TDFS(N)*RUPCNS*TDFS(N)/DENOS                       FLX 3890
140   RUPCN=REF(N)+TDF(N)*RUPCN*TDF(N)/DENO                             FLX 3900
      EUPC(N)=EUPCN                                                     FLX 3910
      EUPCS(N)=EUPCNS                                                   FLX 3920
      RUPC(N)=RUPCN                                                     FLX 3930
      RUPCS(N)=RUPCNS                                                   FLX 3940
101   CONTINUE                                                          FLX 3950
C***********************************************************************FLX 3960
C*    NOW ADD DOWNWARD FROM TOP LAYER (N=1)                            *FLX 3970
C***********************************************************************FLX 3980
      EDNCN=0.0                                                         FLX 3990
      EDNCNS=0.0                                                        FLX 4000
      DNF(K,1)=EDNCN                                                    FLX 4010
      UPF(K,1)=EUPC(1)                                                  FLX 4020
      IF(IEMSCT .EQ. 2) THEN                                            FLX 4030
      DNFS(K,1)=EDNCNS                                                  FLX 4040
      UPFS(K,1)=EUPCS(1)                                                FLX 4050
      ENDIF                                                             FLX 4060
      RDNCN=0.                                                          FLX 4070
      RDNCNS=0.                                                         FLX 4080
      DO 130 N=2,NG                                                     FLX 4090
      M=N-1                                                             FLX 4100
      DENO=1.0-RDNCN*REF(M)                                             FLX 4110
      DENOS=1.0-RDNCNS*REFS(M)                                          FLX 4120
121   CONTINUE                                                          FLX 4130
      EDNCN=EDN(M)+(EDNCN+EUP(M)*RDNCN)*TDF(M)/DENO                     FLX 4140
      RDNCN=REF(M)+TDF(M)*TDF(M)*RDNCN/DENO                             FLX 4150
132   CONTINUE                                                          FLX 4160
      PEFUP =(EUPC(N)+EDNCN*RUPC(N))/DENO                               FLX 4170
      PEFDN =(EDNCN+EUPC(N)*RDNCN)/DENO                                 FLX 4180
      IF(IEMSCT .EQ. 2) THEN                                            FLX 4190
      EDNCNS=EDNS(M)+(EDNCNS+EUPS(M)*RDNCNS)*TDFS(M)/DENOS              FLX 4200
      RDNCNS=REFS(M)+TDFS(M)*TDFS(M)*RDNCNS/DENOS                       FLX 4210
      PEFUPS=(EUPCS(N)+EDNCNS*RUPCS(N))/DENOS                           FLX 4220
      PEFDNS=(EDNCNS+EUPCS(N)*RDNCNS)/DENOS                             FLX 4230
      UPFS(K,N)=PEFUPS                                                  FLX 4240
      DNFS(K,N)=PEFDNS                                                  FLX 4250
      ENDIF                                                             FLX 4260
 120  UPF(K,N)=PEFUP                                                    FLX 4270
      DNF(K,N)=PEFDN                                                    FLX 4280
 130  CONTINUE                                                          FLX 4290
      DPJ(K,NG) = DPJ(K,NG-1)                                           FLX 4300
      DO 117 N=2,NG                                                     FLX 4310
      M=N-1                                                             FLX 4320
      IF(K.EQ.1) THEN                                                   FLX 4330
           UMF(M)=.5*(UPF(K,M)+UPF(K,N))*DPJ(K,M)                       FLX 4340
           UMFS(M)=.5*(UPFS(K,M)+UPFS(K,N))*DPJ(K,M)                    FLX 4350
           DMF(M)=.5*(DNF(K,M)+DNF(K,N))*DPJ(K,M)                       FLX 4360
           DMFS(M)=.5*(DNFS(K,M)+DNFS(K,N))*DPJ(K,M)                    FLX 4370
           UMB(N)=    UPF(K,N)*DPJ(K,N)                                 FLX 4380
           UMBS(N)=   UPFS(K,N)*DPJ(K,N)                                FLX 4390
           DMB(M)=    DNF(K,M)*DPJ(K,M)                                 FLX 4400
           DMBS(M)=   DNFS(K,M)*DPJ(K,M)                                FLX 4410
      ELSE                                                              FLX 4420
           UMF(M)=UMF(M)+.5*(UPF(K,M)+UPF(K,N))*DPJ(K,M)                FLX 4430
           UMFS(M)=UMFS(M)+.5*(UPFS(K,M)+UPFS(K,N))*DPJ(K,M)            FLX 4440
           DMF(M)=DMF(M)+.5*(DNF(K,M)+DNF(K,N))*DPJ(K,M)                FLX 4450
           DMFS(M)=DMFS(M)+.5*(DNFS(K,M)+DNFS(K,N))*DPJ(K,M)            FLX 4460
           UMB(N)= UMB(N)   +  UPF(K,N)*DPJ(K,N)                        FLX 4470
           UMBS(N)=UMBS(N)  +  UPFS(K,N)*DPJ(K,N)                       FLX 4480
           DMB(M)= DMB(M)   +  DNF(K,M)*DPJ(K,M)                        FLX 4490
           DMBS(M)=DMBS(M)  +  DNFS(K,M)*DPJ(K,M)                       FLX 4500
      ENDIF                                                             FLX 4510
C                                                                       FLX 4520
C   TRNSD & TRNSU ARE CUMULATIVE DOWNWARD & UPWARD TRANSMISSION         FLX 4530
C                                                                       FLX 4540
 117    CONTINUE                                                        FLX 4550
C                                                                       FLX 4560
C   FDNSRF IS THE DOWNWARD SOLAR FLUX AT THE SURFACE                    FLX 4570
C   FDNTRF IS THE DOWNWARD THERMAL FLUX AT THE SURFACE                  FLX 4580
C                                                                       FLX 4590
      IF(K.EQ.1) THEN                                                   FLX 4600
           FDNSRF   =DNFS(K,NG)*DPJ(K,NLAYRS)                           FLX 4610
           FDNTRF   =DNF(K,NG) *DPJ(K,NLAYRS)                           FLX 4620
           FUPSRF   =UPFS(K, 1)*DPJ(K, 1)                               FLX 4630
           FUPTRF   =UPF(K, 1) *DPJ(K, 1)                               FLX 4640
      ELSE                                                              FLX 4650
           FDNSRF   =FDNSRF + DNFS(K,NG)*DPJ(K,NLAYRS)                  FLX 4660
           FDNTRF   =FDNTRF + DNF(K,NG) *DPJ(K,NLAYRS)                  FLX 4670
           FUPSRF   =FUPSRF + UPFS(K, 1)*DPJ(K, 1)                      FLX 4680
           FUPTRF   =FUPTRF + UPF(K, 1) *DPJ(K, 1)                      FLX 4690
      ENDIF                                                             FLX 4700
CC    IF(K.EQ.3)PRINT 1910,V,FDNSRF,FDNTRF,FUPSRF,FUPTRF                FLX 4710
1910  FORMAT(F10.0,1P4E10.3)                                            FLX 4720
CCC   IF(K.EQ.3)WRITE(6,1920)FDNSRF,FDNTRF,FUPSRF,FUPTRF                FLX 4730
1920  FORMAT(30X,' FLXDNS=',1PE10.3,' FLXDN =',E10.3,' FLXUPS =',E10.3, FLX 4740
     X ' FLXUP ',E10.3)                                                 FLX 4750
100   CONTINUE                                                          FLX 4760
      UMB(1) = FUPTRF                                                   FLX 4770
      UMBS(1)= FUPSRF                                                   FLX 4780
      DMB(NG)= FDNTRF                                                   FLX 4790
      DMBS(NG)=FDNSRF                                                   FLX 4800
      SUN = S0                                                          FLX 4810
      RETURN                                                            FLX 4820
      END                                                               FLX 4830
