      SUBROUTINE C6DTA(C6L,V)                                           C6D  100
C     CALCULATES MOLECULAR RAYLEIGH SCATTERING COEFFICIENT              C6D* 110
C     USES APPROXIMATION OF SHETTLE ET AL., 1980 (APPL OPT,2873-4)      C6D* 120
C     WITH THE DEPOLARIZATION = 0.0279 INSTED OF 0.035                  CD6* 130
C     INPUT: V = FREQUENCY IN WAVENUMBERS (CM-1)                        C6D* 140
C     OUTPUT: C6L = MOLECULAR SCATTERING COEFFICIENT (KM-1)             C6D* 150
C                   FOR TEMPERATURE 273K & PRESSURE = 1 ATM.            C6D* 160
C                                                                       C6D* 170
      C6L=V**4/(9.38076E+18-1.08426E+09*V**2)                           C6D* 180
      RETURN                                                            C6D  190
      END                                                               C6D  200
      SUBROUTINE C8DTA (C8L,V)                                          C8D  100
C **  OZONE U.V + VISIBLE                                               C8D  110
      COMMON /C4C8/ C4(133),C8(102)                                     C8D  120
      C8L=0.                                                            C8D  130
      IF(V.LT.13000.) RETURN                                            C8D  140
      IF(V.GT.50000.) RETURN                                            C8D  150
      IV=V                                                              C8D  160
      IF(IV.GT.24200.AND.IV.LT.27500) RETURN                            C8D  170
      XI=(V-13000.0)/200.0+1.                                           C8D  180
      IF(IV.GE.27500) XI=(V-27500.0)/500.+57.                           C8D  190
      N=XI+1.001                                                        C8D  200
      XD=XI-FLOAT(N)                                                    C8D  210
      C8L=C8(N)+XD*(C8(N)-C8(N-1))                                      C8D  220
      RETURN                                                            C8D  230
      END                                                               C8D  240
      SUBROUTINE HERTDA(HERZ,V)                                         HER  100
C                                                                       HER  110
C     HERZBERG O2 ABSORPTION                                            HER  120
C     HALL,1987 PRIVATE COMMUNICATION, BASED ON:                        HER  130
C                                                                       HER  140
C     REF. JOHNSTON ET.AL, JGR,89,11661-11665,1984                      HER  150
C         NICOLET, 1987 (RECENT STUDIES IN ATOMIC & MOLECULAR PROCESSES,HER  160
C                        PLEMUN PUBLISHING CORP, NY 1987)               HER  170
C     AND YOSHINO, ET.AL., 1988 (PREPRINT OF "IMPROVED ABSORPTION       HER  180
C         CROSS SECTIONS OF OXYGEN IN THE WAVELENGTH REGION 205-240NM   HER  190
C         OF THE HERZBERG CONTINUUM")                                   HER  200
C                                                                       HER  210
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     HER  220
C                                                                       HER  230
      HERZ=0.0                                                          HER  240
      IF(V.LE.36000.00) RETURN                                          HER  250
C                                                                       HER  260
C     EXTRAPOLATE SMOOTHLY THROUGH THE HERZBERG BAND REGION             HER  270
C     NOTE: HERZBERG BANDS ARE NOT CORRECTLY INCLUDED                   HER  280
C                                                                       HER  290
      CORR=0.                                                           HER  300
      IF(V.LE.40000.)CORR=((40000.-V)/4000.)*7.917E-27                  HER  310
C                                                                       HER  320
C     CONVERSION TO ATM-CM /KM                                          HER  330
C                                                                       HER  340
      RLOSCH = 2.6868 E24 * 1.0E-5                                      HER  350
C                                                                       HER  360
C     HALL'S NEW HERZBERG  (LEAST SQRS FIT, LN(P))                      HER  370
C                                                                       HER  380
C     YRATIO=2048.7/WL(I)  ****IN ANGSTOMS****                          HER  390
C           =.20487/WN(I)     IN MICRONS                                HER  400
C           =WCM(I)/48811.0   IN CM-1                                   HER  410
C                                                                       HER  420
      YRATIO= V    /48811.0                                             HER  430
      HERZ=6.884E-24*(YRATIO)*EXP(-69.738*(ALOG(YRATIO))**2)-CORR       HER  440
      HERZ = HERZ * RLOSCH                                              HER  450
      RETURN                                                            HER  460
      END                                                               HER  470
