     *-1.9981,  -1.0852,  -1.4468,  -0.9222,  -0.1746,   0.3789,        MDA 1990
     *-0.1326,  -0.8516,  -0.4270,  -0.5021,  -0.5774,   0.1072,        MDA 2000
     * 0.0779,  -0.1890,  -0.3060,  -0.3330,  -0.3362,  -0.3320,        MDA 2010
     *-0.3290,  -0.3248,  -0.3123,  -0.2275,  -0.1469,  -0.0421,        MDA 2020
     * 0.1192,   0.2136,   0.2149,   0.2122,   0.2019,   0.1760,        MDA 2030
     * 0.0989,   0.0190,  -0.0836,  -0.1437/                            MDA 2040
      DATA F1/                                                          MDA 2050
     *-0.5486,  -0.6082,  -0.5956,  -0.5356,  -0.4402,  -0.0871,        MDA 2060
     * 0.4527,   1.0366,   1.1096,   1.3655,   1.7101,   1.8903,        MDA 2070
     * 1.6543,   1.2291,   1.4722,   1.4553,   1.4742,   1.8427,        MDA 2080
     * 1.8260,   1.6925,   1.6714,   1.6561,   1.6818,   1.7408,        MDA 2090
     * 1.7604,   1.7735,   1.6870,   1.6975,   1.7266,   1.7732,        MDA 2100
     * 1.8476,   1.8953,   1.8977,   1.9100,   1.9121,   1.9074,        MDA 2110
     * 1.8820,   1.8553,   1.8167,   1.8034/                            MDA 2120
      DATA F2/                                                          MDA 2130
     *-0.4081,  -0.4784,  -0.4660,  -0.4117,  -0.3046,   0.0831,        MDA 2140
     * 0.7409,   1.4609,   1.3780,   1.7134,   2.1471,   2.2808,        MDA 2150
     * 2.1315,   1.6742,   1.9804,   1.9449,   1.9238,   2.2748,        MDA 2160
     * 2.2689,   2.1587,   2.1154,   2.1037,   2.1124,   2.1387,        MDA 2170
     * 2.1490,   2.1552,   2.1238,   2.1535,   2.1840,   2.2226,        MDA 2180
     * 2.2790,   2.3247,   2.3268,   2.3387,   2.3422,   2.3439,        MDA 2190
     * 2.3339,   2.3198,   2.2926,   2.2751/                            MDA 2200
      DATA F3/                                                          MDA 2210
     *-0.2242,  -0.3289,  -0.3406,  -0.2786,  -0.1532,   0.3414,        MDA 2220
     * 1.1618,   1.9783,   1.7412,   2.1629,   2.6182,   2.6999,        MDA 2230
     * 2.6101,   2.1844,   2.4931,   2.4589,   2.4253,   2.7204,        MDA 2240
     * 2.7182,   2.6391,   2.5975,   2.5896,   2.5918,   2.6017,        MDA 2250
     * 2.6055,   2.6086,   2.6049,   2.6334,   2.6574,   2.6843,        MDA 2260
     * 2.7225,   2.7601,   2.7620,   2.7734,   2.7780,   2.7832,        MDA 2270
     * 2.7839,   2.7809,   2.7677,   2.7571/                            MDA 2280
      DATA F4/                                                          MDA 2290
     *-0.0119,  -0.2110,  -0.2063,  -0.1444,  -0.0667,   0.6542,        MDA 2300
     * 1.5923,   2.4405,   2.1326,   2.5924,   3.0247,   3.0696,        MDA 2310
     * 3.0154,   2.6365,   2.9257,   2.8957,   2.8634,   3.1026,        MDA 2320
     * 3.1009,   3.0465,   3.0135,   3.0090,   3.0097,   3.0147,        MDA 2330
     * 3.0167,   3.0193,   3.0233,   3.0464,   3.0635,   3.0808,        MDA 2340
     * 3.1047,   3.1337,   3.1354,   3.1458,   3.1506,   3.1572,        MDA 2350
     * 3.1639,   3.1680,   3.1651,   3.1624/                            MDA 2360
      END                                                               MDA 2370
      SUBROUTINE VSA(IHAZE,VIS,CEILHT,DEPTH,ZINVHT,Z,RH,AHAZE,IH)       VSA  100
C                                                                       VSA  110
C     VERTICAL STRUCTURE ALGORITHM                                      VSA  120
C                                                                       VSA  130
C     FROM U.S. ARMY ATMOSPHERIC SCIENCES LAB                           VSA  140
C     WHITE SANDS MISSILE RANGE, NM                                     VSA  150
C                                                                       VSA  160
C     CREATES A PROFILE OF AEROSOL DENSITY NEAR THE GROUND,INCLUDING    VSA  170
C     CLOUDS AND FOG                                                    VSA  180
C                                                                       VSA  190
C     THESE PROFILES ARE AT 9 HEIGHTS BETWEEN 0 KM AND 2 KM             VSA  200
C                                                                       VSA  210
C                                                                       VSA  220
C  ***VISIBILITY IS ASSUMED TO BE THE SURFACE VISIBILITY***             VSA  230
C                                                                       VSA  240
C     IHAZE  = THE TYPE OF AEROSOL                                      VSA  250
C     VIS    = VISIBILITY IN KM AT THE SURFACE                          VSA  260
C     CEILHT = THE CLOUD CEILING HEIGHT IN KM                           VSA  270
C     DEPTH  = THE CLOUD/FOG DEPTH IN KM                                VSA  280
C     ZINVHT = THE HEIGHT OF INVERSION OR BOUNDARY LAYER IN KM          VSA  290
C                                                                       VSA  300
C     VARIABLES USED IN VSA                                             VSA  310
C                                                                       VSA  320
C     ZC     = CLOUD CEILING HEIGHT IN M                                VSA  330
C     ZT     = CLOUD DEPTH IN M                                         VSA  340
C     ZINV   = INVERSION HEIGHT IN M                                    VSA  350
C           SEE BELOW FOR MORE INFORMATION ABOUT ZC, ZT, AND ZINV       VSA  360
C     D      = INITIAL EXTINCTION AT THE SURFACE (D=3.912/VIS-0.012)    VSA  370
C     ZALGO  = THE DEPTH OF THE LAYER FOR THE ALGORITHM                 VSA  380
C                                                                       VSA  390
C     OUTPUT FROM VSA:                                                  VSA  400
C                                                                       VSA  410
C     Z      = HEIGHT IN KM                                             VSA  420
C     RH     = RELATIVE HUMIDITY AT HEIGHT Z IN PERCENT                 VSA  430
C     AHAZE  = EXTINCTION AT HEIGHT Z IN KM**-1                         VSA  440
C     IH     = AEROSAL TYPE FOR HEIGHT Z                                VSA  450
C     HMAX   = MAXIMUM HEIGHT IN KM USED IN VSA, NOT NECESSARILY 2.0 KM VSA  460
C                                                                       VSA  470
C                                                                       VSA  480
C     THE SLANT PATH CALCULATION USES THE FOLLOWING FUNCTION:           VSA  490
C                                                                       VSA  500
C                 EXT55=A*EXP(B*EXP(C*Z))                               VSA  510
C                                                                       VSA  520
C     WHERE 'Z' IS THE HEIGHT IN KILOMETERS,                            VSA  530
C           'A' IS A FUNCTION OF EXT55 AT Z=0.0 AND IS ALWAYS POSITIVE, VSA  540
C           'B' AND 'C' ARE FUNCTIONS OF CLOUD CONDITIONS AND SURFACE   VSA  550
C               VISIBILITY (EITHER A OR B CAN BE POSITIVE OR NEGATIVE), VSA  560
C           'EXT55' IS THE VISIBILE EXTINCTION COEFFICIENT IN KM**-1.   VSA  570
C                                                                       VSA  580
C     THEREFORE, THERE ARE 4 CASES DEPENDING ON THE SIGNS OF 'B' AND 'C'VSA  590
C     CEILHT AND ZINVHT ARE USED AS SWITCHES TO DETERMINE WHICH CASE    VSA  600
C     TO USE.  THE SURFACE EXTINCTION 'D' IS CALCULATED FROM THE        VSA  610
C     VISIBILITY USING  D=3.912/VIS-0.012 AS FOLLOWS-                   VSA  620
C                                                                       VSA  630
C         CASE=1  FOG/CLOUD CONDITIONS                                  VSA  640
C                 'B' LT 0.0, 'C' LT 0.0                                VSA  650
C                 'D' GE 7.0   KM**-1                                   VSA  660
C                 FOR A CLOUD 7.    KM**-1 IS THE BOUNDARY VALUE AT     VSA  670
C                 THE CLOUD BASE AND 'Z' IS THE VERTICAL DISTANCE       VSA  680
C                 INTO THE CLOUD.                                       VSA  690
C                 VARIABLE USED:   DEPTH                                VSA  700
C                 ** DEFAULT:  DEPTH OF FOG/CLOUD IS 0.2 KM WHEN        VSA  710
C                              'DEPTH' IS 0.0                           VSA  720
C                                                                       VSA  730
C             =2  CLOUD CEILING PRESENT                                 VSA  740
C                 'B' GT 0.0, 'C' GT 0.0                                VSA  750
C                 VARIABLE USED:   CEILHT (MUST BE GE 0.0)              VSA  800
C                 ** DEFAULTS:  CASE 2 - CEILHT IS CALCULATED FROM      VSA  810
C                               SURFACE EXTINCTION                      VSA  820
C                                                                       VSA  850
C             =3  RADIATION FOG OR INVERSION OR BOUNDARY LAYER PRESENT  VSA  860
C                 'B' LT 0.0, 'C' GT 0.0                                VSA  870
C                 VIS LE 2.0 KM DEFAULTS TO A RADIATION FOG AT THE      VSA  880
C                     GROUND AND OVERRIDES INPUT BOUNDARY AEROSOL TYPE  VSA  890
C                 VIS GT 2.0 KM FOR AN INVERSION OR BOUNDARY LAYER      VSA  900
C                     WITH INPUT BOUNDARY AEROSOL TYPE                  VSA  910
C                 ** IHAZE=9 (RADIATION FOG) ALWAYS DEFAULTS TO A       VSA  920
C                    RADIATION FOG NO MATTER WHAT THE VISIBILITY IS.    VSA  930
C                 SWITCH VARIABLE: CEILHT (MUST BE LT 0.0)              VSA  940
C                 VARIABLE USED:   ZINVHT (MUST BE GE 0.0)              VSA  950
C                 ** CEILHT MUST BE LT 0.0 FOR ZINVHT TO BE USED **     VSA  960
C                    HOWEVER, IF DEPTH IS GT 0.0 AND ZINVHT IS EQ 0.0,  VSA  970
C                    THE PROGRAM WILL SUBSTITUTE DEPTH FOR ZINVHT.      VSA  980
C                 ** DEFAULT:  FOR A RADIATION FOG ZINVHT IS 0.05 K     VSA  990
C                              FOR AN INVERSION LAYER ZINVHT IS 2.0 KM  VSA 1000
C
C           NOTE: IF IHAZE = 9, BUT VIS GT 2.0 KM RECOMEND
C           THAT IHAZE DEFAULT TO RURAL AEROSOL
C                                                                       VSA 1010
C             =4  NO CLOUD CEILING, INVERSION LAYER, OR BOUNDARY        VSA 1020
C                 LAYER PRESENT, I.E. CLEAR SKIES                       VSA 1030
C                 EXTINCTION PROFILE CONSTANT WITH HEIGHT A SHORT       VSA 1040
C                 DISTANCE ABOVE THE SURFACE
C                                                                       VSA 1050
      COMMON /IFIL/IRD,IPR,IPU,NPR,IPR1                                 VSA 1060
      DIMENSION Z(10),RH(10),AHAZE(10),IH(10)                           VSA 1070
      DIMENSION AA(2),CC(3),EE(4),A(2),B(2),C(2),FAC1(9),FAC2(9)        VSA 1080
      REAL KMTOM                                                        VSA 1090
      DATA AA/92.1,0.3981/,CC/-0.014,0.0125,-0.03 /,KMTOM/1000.0/       VSA 1100
C     THE LAST 3 VALUES OF EE BELOW ARE EXTINCTIONS FOR VISIBILITIES    VSA 1110
C     EQUAL TO 5.0, 23.0, AND 50.0 KM, RESPECTIVELY.                    VSA 1120
      DATA EE/7.0  ,0.7824,0.17009,0.012  /                             VSA 1130
      DATA FAC1/0.0,0.03,0.05,0.075,0.1,0.18,0.3,0.45,1.0/              VSA 1140
      DATA FAC2/0.0,0.03,0.1,0.18,0.3,0.45,0.6,0.78,1.0/                VSA 1150
      WRITE(IPR,599)                                                    VSA 1160
C                                                                       VSA 1170
C     UPPER LIMIT ON VERTICAL DISTANCE - 2 KM                           VSA 1180
      ZHIGH=2000.                                                       VSA 1190
      HMAX=ZHIGH                                                        VSA 1200
      IF(VIS.GT.0.0)GO TO 5                                             VSA 1210
C     DEFAULT FOR VISIBILITY DEPENDS ON THE VALUE OF IHAZE.             VSA 1220
      IF(IHAZE.EQ.8)VIS=0.2                                             VSA 1230
      IF(IHAZE.EQ.9)VIS=0.5                                             VSA 1240
      IF(IHAZE.EQ.2.OR.IHAZE.EQ.5)VIS=5.0                               VSA 1250
      IF(IHAZE.EQ.1.OR.IHAZE.EQ.4.OR.IHAZE.EQ.7)VIS=23.0                VSA 1260
      IF(IHAZE.EQ.6)VIS=50.0                                            VSA 1270
C     IF(IHAZE.EQ.3)VIS= OR IHAZE = 10 VIS IS DETERMINED ELSEWHERE      VSA 1280
   5  D=3.912/VIS-0.012                                                 VSA 1290
C                                                                       VSA 1300
      ZC=CEILHT*KMTOM                                                   VSA 1310
      ZT=DEPTH*KMTOM                                                    VSA 1320
      ZINV=ZINVHT*KMTOM                                                 VSA 1330
C     IHAZE=9 (RADIATION FOG) IS ALWAYS CALCULATED AS A RADIATION FOG.  VSA 1340
      IF(IHAZE.EQ.9)ZC=-1.0                                             VSA 1350
C     ALSO, CHECK TO SEE IF THE FOG DEPTH FOR A RADIATION FOG           VSA 1360
C     WAS INPUT TO DEPTH INSTEAD OF THE CORRECT VARIABLE ZINVHT.        VSA 1370
      IF(IHAZE.EQ.9.AND.ZT.GT.0.0.AND.ZINV.EQ.0.0)ZINV=ZT               VSA 1380
C                                                                       VSA 1390
C     'IC' DEFINES WHICH CASE TO USE.                                   VSA 1400
      IC=2                                                              VSA 1410
      IF(D.GE.EE(1).AND.ZC.GE.0.0)IC=1                                  VSA 1420
C                                                                       VSA 1430
      IF(ZC.LT.0.0.AND.IC.EQ.2)IC=3                                     VSA 1440
      IF(ZINV.LT.0.0.AND.IC.EQ.3)IC=4                                   VSA 1450
      K=1                                                               VSA 1500
      GO TO (10,20,40,50),IC                                            VSA 1520
C                                                                       VSA 1530
C     CASE 1:  DEPTH FOG/CLOUD; INCREASING EXTINCTION WITH HEIGHT FROM  VSA 1540
C              CLOUD/FOG BASE TO CLOUD/FOG TOP.                         VSA 1550
 10   CONTINUE                                                          VSA 1560
      IF(ZC.LT.HMAX.AND.IC.EQ.2)K=2                                     VSA 1570
C     IC=-1 WHEN A CLOUD IS PRESENT AND THE PATH GOES INTO IT.          VSA 1580
C     USE CASE 2 OR 2' BELOW CLOUD AND CASE 1 INSIDE IT.                VSA 1590
      IF(K.EQ.2)IC=(-1)                                                 VSA 1600
C     THE BASE OF THE CLOUD HAS AN EXTINCTION COEFFICIENT OF 7.0   KM-1.VSA 1610
      IF(K.EQ.2)D=EE(1)                                                 VSA 1620
      A(K)=AA(1)                                                        VSA 1630
C     IF THE SURFACE EXTINCTION IS GREATER THAN THE UPPER LIMIT OF 92.1 VSA 1640
C     KM**-1, RUN THE ALGORITHM WITH AN UPPER LIMIT OF 'D+10'.          VSA 1650
      IF(D.GE.AA(1))A(K)=D+10.0                                         VSA 1660
      C(K)=CC(1)                                                        VSA 1670
      IF(ZT.LE.0.0)WRITE(IPR  ,603)                                     VSA 1680
      IF(ZT.LE.0.0)WRITE(IPR  ,604)                                     VSA 1690
      IF(ZT.GT.0.0)WRITE(IPR  ,611)ZT                                   VSA 1700
C     IF THE DISTANCE FROM THE GROUND TO THE CLOUD/FOG TOP IS LESS      VSA 1710
C     THAN 2.0 KM, VSA WILL ONLY CALCULATE UP TO THE CLOUD TOP.         VSA 1720
      IF(ZT.LE.0.0)ZT=200.                                              VSA 1730
      HMAX=AMIN1(ZT+ZC,HMAX)                                            VSA 1740
      GO TO 60                                                          VSA 1750
C                                                                       VSA 1760
C     CASE 2:  CLEAR/HAZY/LIGHTLY FOGGY; INCREASING EXTINCTION WITH HEIGHT    VSA 1770
C              UP TO THE CLOUD BASE.                                    VSA 1780
 20   A(K)=AA(2)                                                        VSA 1790
      E=EE(1)                                                           VSA 1800
      IF(ZC.EQ.0.0)WRITE(IPR  ,600)                                     VSA 1810
      IF(ZC.EQ.0.0)THEN                                                 VSA 1820
        EAK =  ALOG(E/A(K) )
        DAK =  ALOG(D/A(K) )
        ANUM = EAK / DAK
        IF(ANUM . GT. 0) THEN
              CEIL = ALOG(ANUM)/CC(2)
         ELSE
               CEIL = 2000.
          ENDIF
      ENDIF
CC    IF(ZC.EQ.0.0)CEIL=ALOG(ALOG(E/A(K))/(ALOG(D/A(K))))/CC(2)         VSA 1820
      IF(ZC.EQ.0.0)WRITE(IPR  ,602)CEIL                                 VSA 1830
      IF(ZC.GT.0.0)WRITE(IPR  ,610)ZC                                   VSA 1840
      IF(ZC.EQ.0.0)ZC=CEIL                                              VSA 1850
      F = (VIS * ZC/350.0)**2
C
C     F IS A SCALING FACTOR USED IN CASE 2
C
      DOF = D/F
      IF(DOF .GE. 1.0E-4)THEN
           A(K) = F*(1.0 - EXP(-D/F))
      ELSE
           A(K) = D*(1.-.5*DOF)
      ENDIF
C
C     THE COEFFICIENT A IS RECALCULATED BASED UPON THE SCALING FACTOR
C
      GO TO 60                                                          VSA 1860
C                                                                       VSA 1870
C                                                                       VSA 1970
C     CASE 3:  NO CLOUD CEILING BUT A RADIATION FOG OR AN INVERSION     VSA 1980
C              OR BOUNDARY LAYER PRESENT; DECREASING EXTINCTION WITH    VSA 1990
C              HEIGHT UP TO THE HEIGHT OF THE FOG OR LAYER.             VSA 2000
 40   A(K)=D*1.1                                                        VSA 2010
      E=EE(3)                                                           VSA 2020
      IF(IHAZE.EQ.2.OR.IHAZE.EQ.5)E=EE(2)                               VSA 2030
      IF(IHAZE.EQ.6.OR.(VIS.GT.2.0.AND.IHAZE.NE.9))E=EE(4)              VSA 2040
      IF(E.GT.D)E=D*0.99999                                             VSA 2050
      IF(ZT.GT.0.0.AND.ZINV.EQ.0.0.AND.VIS.LE.2.0)ZINV=ZT               VSA 2060
      IF(ZINV.EQ.0.0.AND.VIS.GT.2.0.AND.IHAZE.NE.9)WRITE(IPR  ,601)     VSA 2070
      IF(ZINV.EQ.0.0.AND.(VIS.LE.2.0.OR.IHAZE.EQ.9))WRITE(IPR  ,605)    VSA 2080
      IF(ZINV.EQ.0.0.AND.(VIS.LE.2.0.OR.IHAZE.EQ.9))WRITE(IPR  ,604)    VSA 2090
      IF(ZINV.GT.0.0.AND.VIS.GT.2.0.AND.IHAZE.NE.9)WRITE(IPR  ,612)ZINV VSA 2100
      IF(ZINV.GT.0.0.AND.(VIS.LE.2.0.OR.IHAZE.EQ.9))WRITE(IPR,614)ZINV  VSA 2110
      IF(ZINV.EQ.0.0.AND.VIS.GT.2.0.AND.IHAZE.NE.9)ZINV=2000            VSA 2120
      IF(ZINV.EQ.0.0.AND.(VIS.LE.2.0.OR.IHAZE.EQ.9))ZINV= 50            VSA 2130
      HMAX=AMIN1(ZINV,HMAX)                                             VSA 2140
      ZC=0.0                                                            VSA 2150
      GO TO 60
C                                                                       VSA 2160
C     CASE 4:  NO CLOUD CEILING OR INVERSION LAYER;                     VSA 2170
C              CONSTANT EXTINCTION WITH HEIGHT.                         VSA 2180
C
50     A(K) = EE(4)
       C(K) = CC(3)
C                                                                       VSA 2190
60               B(K)=ALOG(D/A(K))                                      VSA 2200
      IF(IC.EQ.2)C(K)=ALOG(ALOG(E/A(K))/B(K))/ZC                        VSA 2220
      IF(IC.EQ.3)C(K)=ALOG(ALOG(E/A(K))/B(K))/ZINV                      VSA 2230
      IF(ZC.LT.HMAX.AND.K.EQ.1.AND.IC.EQ.2)GO TO 10                     VSA 2240
      IF(IC.EQ.2)HMAX=AMIN1(ZC,HMAX)                                    VSA 2250
      ZALGO=HMAX                                                        VSA 2260
      IF(IC.LT.0)ZALGO=ZC                                               VSA 2270
      WRITE(IPR  ,619)                                                  VSA 2280
      IF(IC.LT.0)K=1                                                    VSA 2290
C                                                                       VSA 2300
      DO 70 I=1,9                                                       VSA 2310
      IF(IC.LT.0.AND.I.EQ.5)K=2                                         VSA 2320
      IF(IC.LT.0.AND.I.EQ.5)ZALGO=HMAX-ZC                               VSA 2330
      Z(I)=ZALGO*(1.0-FAC2(10-I))                                       VSA 2340
      IF(IC.EQ.1)Z(I)=ZALGO*FAC1(I)                                     VSA 2350
      IF(IC.EQ.4)Z(I)=ZALGO*FLOAT(I-1)/8.0                              VSA 2360
      IF(IC.LT.0.AND.I.LT.5)Z(I)=ZALGO*(1.0-FAC2(11-2*I))               VSA 2370
      IF(IC.LT.0.AND.I.GE.5)Z(I)=ZALGO*FAC1(2*I-9)                      VSA 2380
C     IF(IC.LT.0.AND.(I.EQ.7.OR.I.EQ.8))Z(I)=ZALGO*FAC1(2*I-10)         VSA 2390
                 AHAZE(I)=A(K)*EXP(B(K)*EXP(C(K)*Z(I)))                 VSA 2400
      IF(IC.LE.0.AND.I.GE.5)Z(I)=Z(I)+ZC                                VSA 2420
      Z(I)=Z(I)/KMTOM                                                   VSA 2430
      RH(I)=6.953*ALOG(AHAZE(I))+86.407                                 VSA 2440
      IF(AHAZE(I).GE.EE(1))RH(I)=100.0                                  VSA 2450
      VISIB=3.912/(AHAZE(I)+0.012)                                      VSA 2460
      IH(I)=IHAZE                                                       VSA 2470
C     IF A RADIATION FOG IS PRESENT (I.E. VIS<=2.0 KM AND IC=3),        VSA 2480
C     IH IS SET TO 9 FOR ALL LEVELS.                                    VSA 2490
      IF(VISIB.LE.2.0.AND.IC.EQ.3)IH(I)=9                               VSA 2500
C     FOR A DEPTH FOG/CLOUD CASE, IH=8 DENOTING AN ADVECTION FOG.       VSA 2510
      IF(IC.EQ.1.OR.(IC.LT.0.AND.I.GE.5))IH(I)=8                        VSA 2520
      WRITE(IPR  ,620)Z(I),RH(I),AHAZE(I),VISIB,IH(I)                   VSA 2530
   70 CONTINUE                                                          VSA 2540
      HMAX=HMAX/KMTOM                                                   VSA 2550
      RETURN                                                            VSA 2560
C                                                                       VSA 2570
599   FORMAT('0 VERTICAL STRUCTURE ALGORITHM (VSA) USED')               VSA 2580
600   FORMAT(1H ,50X,28HCLOUD CEILING HEIGHT UNKNOWN)                   VSA 2590
601   FORMAT(1H ,50X,42HINVERSION OR BOUNDARY LAYER HEIGHT UNKNOWN,/,   VSA 2600
     1  1H ,50X,39HVSA WILL USE A DEFAULT OF 2000.0 METERS,/)           VSA 2610
605   FORMAT(1H ,50X,27HRADIATION FOG DEPTH UNKNOWN)                    VSA 2620
619   FORMAT(5X,10HHEIGHT(KM),5X,7HR.H.(%),5X,16HEXTINCTION(KM-1),      VSA 2640
     1   5X,15HVIS(3.912/EXTN),5X,5HIHAZE,/)                            VSA 2650
620   FORMAT(7X,F7.4,7X,F5.1,8X,E12.4,11X,F7.4,10X,I2)                  VSA 2660
602   FORMAT(1H ,39X,35HVSA WILL USE A CALCULATED VALUE OF ,F7.1,       VSA 2670
     1       7H METERS,/)                                               VSA 2680
603   FORMAT(1H ,50X,19HCLOUD DEPTH UNKNOWN)                            VSA 2690
604   FORMAT(1H ,50X,38HVSA WILL USE A DEFAULT OF 200.0 METERS,/)       VSA 2700
610   FORMAT(1H ,50X,24HCLOUD CEILING HEIGHT IS ,F9.1,7H METERS,/)      VSA 2710
611   FORMAT(1H ,50X,15HCLOUD DEPTH IS ,F14.1,7H METERS,/)              VSA 2720
612   FORMAT(1H ,50X,38HINVERSION OR BOUNDARY LAYER HEIGHT IS ,F7.1,    VSA 2730
     1 7H METERS,/)                                                     VSA 2740
614   FORMAT(1H ,50X,26HDEPTH OF RADIATION FOG IS ,F7.1,7H METERS,/)    VSA 2750
613   FORMAT(1H ,50X,43HTHERE IS NO INVERSION OR BOUNDARY LAYER OR ,    VSA 2760
     1 13HCLOUD PRESENT,/)                                              VSA 2770
      END                                                               VSA 2780
      SUBROUTINE HNO3 (V,HABS)                                          HNO  100
C                                                                       HNO  110
C     HNO3  STATISTICAL BAND PARAMETERS                                 HNO  120
C                                                                       HNO  130
      DIMENSION H1(15), H2(16), H3(13)                                  HNO  140
C     ARRAY H1 CONTAINS HNO3 ABS, COEF(CM-1ATM-1) FROM  850 TO 920 CM-1 HNO  150
      DATA H1/2.197,3.911,6.154,8.150,9.217,9.461,11.56,11.10,11.17,12.4HNO  160
     10,10.49,7.509,6.136,4.899,2.866/                                  HNO  170
C     ARRAY H2 CONTAINS HNO3 ABS, COEF(CM-1ATM-1) FROM 1275 TO1350 CM-1 HNO  180
      DATA H2/2.828,4.611,6.755,8.759,10.51,13.74,18.00,21.51,23.09,21.6HNO  190
     18,21.32,16.82,16.42,17.87,14.86,8.716/                            HNO  200
C     ARRAY H3 CONTAINS HNO3 ABS, COEF(CM-1ATM-1) FROM 1675 TO1735 CM-1 HNO  210
      DATA H3/5.003,8.803,14.12,19.83,23.31,23.58,23.22,21.09,26.99,25.8HNO  220
     14,24.79,17.68,9.420/                                              HNO  230
      HABS=0.                                                           HNO  240
      IF (V.GE.850.0.AND.V.LE.920.0) GO TO 5                            HNO  250
      IF (V.GE.1275.0.AND.V.LE.1350.0) GO TO 10                         HNO  260
      IF (V.GE.1675.0.AND.V.LE.1735.0) GO TO 15                         HNO  270
      RETURN                                                            HNO  280
    5 I=(V-845.)/5.                                                     HNO  290
      HABS=H1(I)                                                        HNO  300
      RETURN                                                            HNO  310
   10 I=(V-1270.)/5.                                                    HNO  320
      HABS=H2(I)                                                        HNO  330
      RETURN                                                            HNO  340
   15 I=(V-1670.)/5.                                                    HNO  350
      HABS=H3(I)                                                        HNO  360
      RETURN                                                            HNO  370
      END                                                               HNO  380
      SUBROUTINE SSGEO(IERROR,IPH,IPARM,PARM1,PARM2,PARM3,PARM4,PSIPO,G,SSG  100
     X MAXGEO)                                                          SSG  110
C                                                                       SSG  120
C     THIS ROUTINE DRIVES THE LOWTRAN GEOMETRY ROUTINES REPEATEDLY      SSG  130
C     TO OBTAIN THE ABSORBER AMOUNTS FROM THE SCATTERING POINTS ON      SSG  140
C     THE OPTICAL PATH TO THE EXTRATERRESTRIAL SOURCE, NECESSARY        SSG  150
C     TO DO THE LAYER BY LAYER SINGLE SCATTERING RADIANCE CALCULATION.  SSG  160
C                                                                       SSG  170
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            SSG  180
      COMMON             WPATH(68, 63),TBBY(68)                         SSG  190
      COMMON ABSC(5,47),EXTC(5,47),ASYC(5,47),VX2(47),AWCCON(5)         SSG  200
      COMMON /SOLS/ AH1(68),ARH(68),                                    SSG  210
     X   WPATHS(68,63),PA(68),PR(68),ATHETA(35),ADBETA(35),LJ(69),JTURN,SSG  220
     X ANGSUN,CSZEN(68)                                                 SSG  230
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                SSG  240
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  SSG  250
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   SSG  260
     1    RAINRT                                                        SSG  270
      COMMON /CARD3/ H1,H2,ANGLE,RANGE,BETA,REE,LEN                     SSG  280
      COMMON /CARD4/ V1,V2,DV                                           SSG  290
      COMMON /CNTRL/ KMAX,MM,IKMAX,NL,ML,IKLO,ISSGEO,IMULT              SSG  300
      COMMON /MODEL/ ZM(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),      SSG  310
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         SSG  320
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              SSG  330
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     SSG  340
      COMMON /RFRPTH/ ZP(35),PP(35),TP(35),RFNDXP(35),SP(35),           SSG  350
     1    PPSUM(35),TPSUM(35),RHOPSM(35),DENP(63,35),AMTP(63,35)        SSG* 360
      COMMON /USRDTA/ NANGLS,ANGF(50),F(4,50)                           SSG  370
      DIMENSION WPDUM(68,63),TBDUM(68),LJD(68),AZ(35),RHD(35)           SSG  380
C     MOLECULAR AND HENYEY-GREENSTEIN PHASE FUNCTIONS                   SSG  390
C     NOTE; UNITS ARE (STER-1), X=COS(SCATTERING ANGLE)                 SSG  400
C     PFMOL(X) = (3/16*PI)*((1+DPL)+(1-DPL)*X**2)*2/(2+DPL)             SSG* 402
C     WHERE: DPL (THE DEPOLARIZATION RATIO) = 0.0279                    SSG* 406
      PFMOL(X)=.06050402+.0572197*X**2                                  SSG* 410
      PFHG(GG,X)=(1.0-GG**2)/(4.*PI*(1.0+GG**2-2.0*GG*X)**1.5)          SSG  420
      IKLO=1                                                            SSG  430
      NPR = 1                                                           SSG  440
      ISSGEO=1                                                          SSG  450
C     SPECIFY THE GEOMETRICAL CONFIGURATION                             SSG  460
      IF(IPARM.EQ.2) GO TO 1                                            SSG  470
      THETAO=PARM1                                                      SSG  480
      PHIO=PARM2                                                        SSG  490
      THETAS=PARM3                                                      SSG  500
      PHIS=PARM4                                                        SSG  510
      GO TO 2                                                           SSG  520
1     CONTINUE                                                          SSG  530
      PSIO=PARM1                                                        SSG  540
      DELO=PARM2                                                        SSG  550
2     IF(IPARM.NE.0) GO TO 5                                            SSG  560
      IF(ABS(THETAO).LT.89.5) GO TO 5                                   SSG  570
      IF(THETAO.GT.0.0) GO TO 4                                         SSG  580
C     OBSERVER IS AT OR NEAR THE SOUTH POLE, REMAP TO EQUATOR           SSG  590
      WRITE(IPR,960)                                                    SSG  600
      PSIPO=PSIPO-PHIS                                                  SSG  610
      THETAO=0.0                                                        SSG  620
      PHIO=0.0                                                          SSG  630
      THETAS=0.0                                                        SSG  640
      PHIS=90.+THETAS                                                   SSG  650
      GO TO 5                                                           SSG  660
4     CONTINUE                                                          SSG  670
C     OBSERVER IS AT OR NEAR THE NORTH POLE, REMAP TO EQUATOR           SSG  680
      WRITE(IPR,961)                                                    SSG  690
      PSIPO=PHIS-PSIPO                                                  SSG  700
      THETAO=0.0                                                        SSG  710
      PHIO=0.0                                                          SSG  720
      THETAS=0.0                                                        SSG  730
      PHIS=90.-THETAS                                                   SSG  740
5     CONTINUE                                                          SSG  750
      WRITE(IPR,900)                                                    SSG  760
C                                                                       SSG  770
C     SAVE OPTICAL PATH PARAMETERS AND AMOUNTS                          SSG  780
C                                                                       SSG  790
      JTURND=JTURN                                                      SSG  800
      IKMAXD=IKMAX+1                                                    SSG  810
      H1D=H1                                                            SSG  820
      H2D=H2                                                            SSG  830
      ANGD=ANGLE                                                        SSG  840
      RNGD=RANGE                                                        SSG  850
      BETD=BETA                                                         SSG  860
      BETA=0.                                                           SSG  870
      LEND=LEN                                                          SSG  880
      ITD=ITYPE                                                         SSG  890
      IMAX=ML                                                           SSG  900
      DO 10 J=1,IMAX                                                    SSG  910
      AZ(J)=ZP(J)                                                       SSG  920
10    RHD(J)=RELHUM(J)                                                  SSG  930
      DO 11 J=1,IKMAXD                                                  SSG  940
      TBDUM(J)=TBBY(J)                                                  SSG  950
      LJD(J)=LJ(J)                                                      SSG  960
      IF(LJD(J).GT.IMAX)LJD(J)=IMAX                                     SSG  970
      DO 11 K=1,KMAX                                                    SSG  980
11    WPDUM(J,K)=WPATH(J,K)                                             SSG  990
      IMAX=IMAX-1                                                       SSG 1000
C                                                                       SSG 1010
C     ESTABLISH PSIO AND DELO                                           SSG 1020
      IARBO=0                                                           SSG 1030
      IF(ANGLE.LT.0.01.OR.ANGLE.GT.179.99) IARBO=1                      SSG 1040
C                                                                       SSG 1050
      IF(IPARM.NE.2)                                                    SSG 1060
     1CALL PSIDEL(THETAS,PHIS,THETAO,PHIO,PSIPO,PSIO,DELO,IARBO)        SSG 1070
C     INITIAL CONDITIONS AT THE OBSERVER                                SSG 1080
      IARB=IARBO                                                        SSG 1090
      BETAST=0.0                                                        SSG 1100
      IF(IARBO.EQ.0) PSIST=PSIO                                         SSG 1110
      ANGL0=DELO                                                        SSG 1120
C                                                                       SSG 1130
C     LOOP OVER THE POINT TO SUN PATHS TO OBTAIN AMOUNTS                SSG 1140
C                                                                       SSG 1150
      WRITE(IPR,950)                                                    SSG 1160
      WRITE(IPR,952)                                                    SSG 1170
950   FORMAT(' SCTTR SCTTR SUBTENDED  SOLAR   PATH  RELATIVE  SCTTR   MOSSG 1180
     1LECULAR  ')                                                       SSG 1190
952   FORMAT(' POINT  ALT    ANGLE   ZENITH  ZENITH AZIMUTH   ANGLE   PHSSG 1200
     1ASE F    '/)                                                      SSG 1210
      DO 130 L=IKLO,IKMAXD                                              SSG 1220
      IF(LEND.EQ.1.OR.JTURND.NE.0) GO TO 20                             SSG 1230
C     SHORT PATH, UP                                                    SSG 1240
      H1=AZ(L)                                                          SSG 1250
      RELH=RHD(L)                                                       SSG 1260
      THTST=ATHETA(L)                                                   SSG 1270
      IF(L.GE.2) BETAST=BETAST+ADBETA(L-1)                              SSG 1280
      GO TO 30                                                          SSG 1290
20    CONTINUE                                                          SSG 1300
C     LONG PATH, OR SHORT PATH DOWN                                     SSG 1310
      IF(L.GE.2) BETAST=BETAST+ADBETA(LJD(L-1))                         SSG 1320
      IF(L.GE.JTURND) GO TO 25                                          SSG 1330
      LJP1=LJD(L)+1                                                     SSG 1340
      H1=AZ(LJP1)                                                       SSG 1350
      RELH=RHD(LJP1)                                                    SSG 1360
      THTST=180.-ATHETA(LJP1)                                           SSG 1370
      GO TO 30                                                          SSG 1380
25    LJDL=LJD(L)                                                       SSG 1390
      H1=AZ(LJDL)                                                       SSG 1400
      RELH=RHD(LJDL)                                                    SSG 1410
      THTST=ATHETA(LJDL)                                                SSG 1420
      IF(L.EQ.JTURND)THTST=180.-ATHETA(LJDL)                            SSG 1430
30    CONTINUE                                                          SSG 1440
      AH1(L)=H1                                                         SSG 1450
      ARH(L)=RELH                                                       SSG 1460
      IF(L.LT.2) GO TO 35                                               SSG 1470
      PSIST=PSI(PSIO,DELO,BETAST,IARB,IARBO)                            SSG 1480
      ANGL0=DEL(PSIO,DELO,BETAST,IARBO)                                 SSG 1490
35    CORR=0.0                                                          SSG 1500
C     RANGE=UNKNOWN                                                     SSG 1510
      ITYPE=3                                                           SSG 1520
      DO 90 JITER=1,4                                                   SSG 1530
      H2 = 0.0                                                          SSG 1540
      ANGLE=ANGL0-CORR                                                  SSG 1550
      LEN=0                                                             SSG 1560
      IF(ANGLE.LE.90.0) GO TO 40                                        SSG 1570
      LEN=1                                                             SSG 1580
      WRITE(IPR,955) L                                                  SSG 1590
955   FORMAT('  SUN PATH ',I3,' PASSES THROUGH A TANGENT HEIGHT')       SSG 1600
40    CONTINUE                                                          SSG 1610
      HTOP=ZMAX                                                         SSG 1620
      IF(H1.LT.HTOP.OR.LEN.EQ.1) GO TO 60                               SSG 1630
C     SCATTERING POINT IS AT OR ABOVE HTOP AND LEN=0,                   SSG 1640
C     SET W(K)=0.0 AND CONTINUE                                         SSG 1650
      DO 50 K=1,KMAX                                                    SSG 1660
50    W(K)=0.0                                                          SSG 1670
      GO TO 100                                                         SSG 1680
C                                                                       SSG 1690
60    CALL GEO(IERROR,BENDNG,MAXGEO)                                    SSG 1700
C                                                                       SSG 1710
C     IERROR=-5 IF SCATTERING POINT IS IN THE SHADE, ALSO SET W(K)=-5.0 SSG 1720
      IF(IERROR.NE.-5) GO TO 80                                         SSG 1730
      WRITE(IPR,970) L                                                  SSG 1740
970   FORMAT('  SCATTERING POINT ',I3,' IS IN THE SHADE')               SSG 1750
      DO 70 K=1,KMAX                                                    SSG 1760
70    W(K)=-5.0                                                         SSG 1770
      IERROR=0                                                          SSG 1780
      GO TO 100                                                         SSG 1790
  80  CONTINUE                                                          SSG 1800
C     SOLAR ZENITH BENDING CORRECTION                                   SSG 1810
      IF(JITER.GT.1) WRITE(IPR,917) CORR                                SSG 1820
      IF(ABS(CORR-BENDNG).LT..1) GO TO 100                              SSG 1830
90    CORR=BENDNG                                                       SSG 1840
100   CONTINUE                                                          SSG 1850
      SANGLE=SCTANG(ANGLE,THTST,PSIST,IARB)                             SSG 1860
      COSANG=COS(CA*SANGLE)                                             SSG 1870
C     LOAD MOLECULAR PHASE FUNCTION ARRAY                               SSG 1880
      PR(L)=PFMOL(COSANG)                                               SSG 1890
C     LOAD AEROSOL PHASE FUNCTION ARRAY                                 SSG 1900
C     HENYEY-GREENSTEIN                                                 SSG 1910
      IF(IPH.NE.0) GO TO 105                                            SSG 1920
      PA(L)=PFHG(G,COSANG)                                              SSG 1930
      GO TO 115                                                         SSG 1940
105   CONTINUE                                                          SSG 1950
      IF(IPH.NE.1) GO TO 110                                            SSG 1960
C     USER SUPPLIED PHASE FUNCTION                                      SSG 1970
C     DETERMINE ALTITUDE AND ANGLE INDICES                              SSG 1980
      M=4                                                               SSG 1990
      IF(H1.LE.30.) M=3                                                 SSG 2000
      IF(H1.LE.10.) M=2                                                 SSG 2010
      IF(H1.LE.2.) M=1                                                  SSG 2020
      DO 106 LL=1,NANGLS                                                SSG 2030
C                                                                       SSG 2040
C    INTERPOLATION CORRECTION FOR ANGLE = TO ANGLES READ IN             SSG 2050
C                                                                       SSG 2060
      IF(ANGF(LL).EQ.SANGLE) THEN                                       SSG 2070
      PA(L)=F(M,LL)                                                     SSG 2080
      GO TO 115                                                         SSG 2090
      END IF                                                            SSG 2100
      IF(ANGF(LL).GT.SANGLE) GO TO 107                                  SSG 2110
106   CONTINUE                                                          SSG 2120
107   LP1=LL                                                            SSG 2130
      LL=LL-1                                                           SSG 2140
      CALL INTERP(2,SANGLE,ANGF(LL),ANGF(LP1),PA(L),F(M,LL),F(M,LP1))   SSG 2150
      GO TO 115                                                         SSG 2160
110   CONTINUE                                                          SSG 2170
C     V DEPENDENT MIE DATA BASE, SAVE SCATTERING ANGLE INSTEAD          SSG 2180
      PA(L)=SANGLE                                                      SSG 2190
115   CONTINUE                                                          SSG 2200
C     LOAD AMOUNTS FROM W(K) INTO WPATHS(L,K)                           SSG 2210
      DO 120 K=1,KMAX                                                   SSG 2220
120   WPATHS(L,K)=W(K)                                                  SSG 2230
C     REVERSE SIGN CONVENTION (TO + E OF N) FOR PRINTED OUTPUT          SSG 2240
      PSIST2=-PSIST                                                     SSG 2250
C                                                                       SSG 2260
C    CSZEN IS COSINE OF SOLAR ZENTIH FOR EACH LAYER                     SSG 2270
C                                                                       SSG 2280
      CSZEN(L)=COS(ANGLE*CA)                                            SSG 2290
      WRITE(IPR,951)L,H1,BETAST,ANGLE,THTST,PSIST2,SANGLE,PR(L)         SSG 2300
951   FORMAT(1X,I3,6(1X,F7.2), (1X,E10.3))                              SSG 2310
130   CONTINUE                                                          SSG 2320
C                                                                       SSG 2330
C     RESTORE OPTICAL PATH AMOUNTS                                      SSG 2340
C                                                                       SSG 2350
      IKMAX=IKMAXD-1                                                    SSG 2360
      H1=H1D                                                            SSG 2370
      H2=H2D                                                            SSG 2380
      ANGSUN=ANGLE                                                      SSG 2390
      ANGLE=ANGD                                                        SSG 2400
      RANGE=RNGD                                                        SSG 2410
      BETA=BETD                                                         SSG 2420
      LEN=LEND                                                          SSG 2430
      ITYPE=ITD                                                         SSG 2440
      DO 160 J=1,IKMAXD                                                 SSG 2450
      TBBY(J)=TBDUM(J)                                                  SSG 2460
      LJ(J)=LJD(J)                                                      SSG 2470
      DO 160 K=1,KMAX                                                   SSG 2480
160   WPATH(J,K)=WPDUM(J,K)                                             SSG 2490
      NPR = NOPRNT                                                      SSG 2500
C                                                                       SSG 2510
C     FORMATS                                                           SSG 2520
C                                                                       SSG 2530
900   FORMAT(2X,//,' SINGLE SCATTERING POINT TO SOURCE PATHS ')         SSG 2540
917   FORMAT('  SOLAR ZENITH CORRECTION FOR BENDING = ',F10.3)          SSG 2550
920   FORMAT(2X,'*** CUMMULATIVE POINT-TO-SOURCE AMOUNTS ***')          SSG 2560
925   FORMAT(/,2X,'L                 WPATHS(L,K) K=1,7')                SSG 2570
930   FORMAT(1X,I2,7(2X,E10.3))                                         SSG 2580
931   FORMAT(8E10.3)                                                    SSG 2590
940   FORMAT(2X,'L                 WPATHS(L,K) K=8,15')                 SSG 2600
960   FORMAT(2X,'THETAO < 89.5, OBSERVER ASSUMED TO BE AT THE SOUTH     SSG 2610
     1POLE, PROBLEM HAS BEEN REMAPPED TO THE EQUATOR')                  SSG 2620
961   FORMAT(2X,'THETAO > 89.5, OBSERVER ASSUMED TO BE AT THE NORTH     SSG 2630
     1POLE, PROBLEM HAS BEEN REMAPPED TO THE EQUATOR')                  SSG 2640
      RETURN                                                            SSG 2650
      END                                                               SSG 2660
      SUBROUTINE PSIDEL(THETAS,PHIS,THETAO,PHIO,PSIPO2,PSIO,DELO,IARBO) PSI  100
C                                                                       PSI  110
C     THIS ROUTINE CALCULATES PSIO (THE RELATIVE AZIMUTH BETWEEN THE    PSI  120
C     LINE OF SIGHT AND THE DIRECT SOLAR PATH, AT THE OBSERVER)         PSI  130
C     AND DELO (THE ANGLE SUBTENDED AT THE EARTH'S CENTER BY THE        PSI  140
C     OBSERVER AND THE SUBSOLAR POINT).                                 PSI  150
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              PSI  160
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     PSI  170
      DATA  EPSILN/1.0E-5/                                              PSI  180
C                                                                       PSI  190
C     CHANGE CONVENTION FOR PSIPO FROM (EAST OF NORTH) TO (NORTH OF EASTPSI  200
C     FOR COMPUTATIONAL PURPOSES. RANGE IS -180 TO +180.                PSI  210
      PSIPO=90.0-PSIPO2                                                 PSI  220
      IF(PSIPO.GT.180.0) PSIPO=PSIPO-360.0                              PSI  230
C                                                                       PSI  240
      DPHI=PHIS-PHIO                                                    PSI  250
      DTHT=THETAS-THETAO                                                PSI  260
      THTS=THETAS/DEG                                                   PSI  270
      THTO=THETAO/DEG                                                   PSI  280
      DPH=DPHI/DEG                                                      PSI  290
      STS=SIN(THTS)                                                     PSI  300
      STO=SIN(THTO)                                                     PSI  310
      CTS=COS(THTS)                                                     PSI  320
      CTO=COS(THTO)                                                     PSI  330
      SDPHI=SIN(DPH)                                                    PSI  340
      CDPHI=COS(DPH)                                                    PSI  350
      TTS=TAN(THTS)                                                     PSI  360
      TTO=TAN(THTO)                                                     PSI  370
C                                                                       PSI  380
C     CALCULATE DELO,  CHECK FOR SPECIAL CASES%                         PSI  390
      IF(ABS(DPHI).GT.EPSILN) GO TO 10                                  PSI  400
C     COLONGITUDE                                                       PSI  410
      DELO=ABS(DTHT)                                                    PSI  420
      GO TO 30                                                          PSI  430
10    IF(ABS(DTHT).GT.EPSILN) GO TO 20                                  PSI  440
C     COLATITUDE                                                        PSI  450
      DELO=ABS(DPHI)                                                    PSI  460
      GO TO 30                                                          PSI  470
C     GENERAL CASE                                                      PSI  480
20    DELO=DEG*ACOS(CTS*CTO*CDPHI+STS*STO)                              PSI  490
30    IF(DELO.LE.EPSILN) DELO=0.0                                       PSI  500
C     CALCULATE PSIO (=PSISO-PSIPO)                                     PSI  510
C     CHECK FOR SPECIAL CASES WHERE EITHER PSISO OR PSIPO ARE ARBITRARY PSI  520
C                                                                       PSI  530
C     VERTICAL OPTICAL PATH, PSIPO IS ARBITRARY, THIS CASE WAS FLAGGED  PSI  540
C     IN SSGEO BY SETTING IARBO=1                                       PSI  550
C                                                                       PSI  560
C     OBSERVER AND SUBSOLAR POINT COINCIDE, PSISO IS ARBITRARY          PSI  570
      IF(DELO.EQ.0.0) IARBO=IARBO+2                                     PSI  580
      IF(IARBO.EQ.0) GO TO 40                                           PSI  590
C     IARBO=0,1,2,3 FOR% PSIO DEFINITE, PSIPO ARBITRARY, PSISO ARBITRARYPSI  600
C                        BOTH PSIPO AND PSISO ARBITRARY, RESPECTIVELY.  PSI  610
      RETURN                                                            PSI  620
40    ANUMER=TTO*CDPHI-TTS                                              PSI  630
      DENOM=(1.+TTO**2)*CTO*SDPHI                                       PSI  640
      IF(ABS(ANUMER).GT.EPSILN) GO TO 50                                PSI  650
C     NUMERATOR=0.0, GREAT CIRCLE CONTAINING THE OBSERVER AND THE SUBSOLPSI  660
C     POINT IS DUE EAST-WEST AT THE OBSERVER                            PSI  670
      PSISO=0.0                                                         PSI  680
      IF(DPHI.GT.0.0) PSISO=180.0                                       PSI  690
      GO TO 90                                                          PSI  700
50    IF(ABS(DENOM).GT.EPSILN) GO TO 60                                 PSI  710
C     DENOMINATOR =0.0, ATAN(INFINITY)=+90 OR -90                       PSI  720
      PSISO=90.0                                                        PSI  730
      IF(DTHT.LT.0.0) PSISO=-90.0                                       PSI  740
      GO TO 90                                                          PSI  750
C     GENERAL CASE                                                      PSI  760
60    PSISO=DEG*ATAN(ANUMER/DENOM)                                      PSI  770
C     ATAN RETURNS ARGUMENTS BETWEEN -90 AND 90, MAY NEED TO CORRECT    PSI  780
C     NO CORRECTION NECESSARY                                           PSI  790
      IF(DPHI.LT.0.0) GO TO 90                                          PSI  800
C     CORRECTION NECESSARY                                              PSI  810
      IF(DPHI.GT.0.0) GO TO 70                                          PSI  820
C     DPHI=0.0                                                          PSI  830
      PSISO=90.0                                                        PSI  840
      IF(DTHT.LT.0.0) PSISO=-90.0                                       PSI  850
      GO TO 90                                                          PSI  860
C     DPHI.GT.0.0                                                       PSI  870
70    IF(PSISO.GT.0.0) GO TO 80                                         PSI  880
      PSISO=PSISO+180.0                                                 PSI  890
      GO TO 90                                                          PSI  900
80    PSISO=PSISO-180.0                                                 PSI  910
90    CONTINUE                                                          PSI  920
      IF(PHIS .GT. 180.)THEN                                            PSI* 922
           PSIPO = PSIPO + 180.                                         PSI* 923
           IF(PHIS .GT. 360.)PSIPO = PSIPO - 360.                       PSI* 924
           IF(PSISO.LT.   0.)PSISO = 180.  - PSISO                      PSI* 925
      ENDIF                                                             PSI* 926
      PSIO=PSISO-PSIPO                                                  PSI  930
C     RANGE OF PSIO IS -180.0 TO 180.0, CORRECT IF NECESSARY            PSI  940
      IF(PSIO.LT.-180.0) PSIO=PSIO+360.0                                PSI  950
      IF(PSIO.GT. 180.0) PSIO=PSIO-360.0                                PSI  960
C                                                                       PSI  970
      RETURN                                                            PSI  980
      END                                                               PSI  990
      FUNCTION   SCTANG(ANGLST,THTST,PSIST,IARB)                        SCT  100
C                                                                       SCT  110
C     FUNCTION SCTANG RETURNS THE SCATTERING ANGLE (THAT IS, THE        SCT  120
C     ANGLE BETWEEN THE SUN'S RAYS AND THE LINE OF SIGHT) AT ANY        SCT  130
C     POINT ALONG THE OPTICAL PATH.                                     SCT  140
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              SCT  150
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     SCT  160
C                                                                       SCT  170
      SUNZEN=ANGLST/DEG                                                 SCT  180
      PTHZEN=THTST/DEG                                                  SCT  190
      IF(IARB.EQ.0) GO TO 10                                            SCT  200
C     SPECIAL CASES IF PSI IS ARBITRARY                                 SCT  210
      SCTANG=DEG*ACOS(COS(SUNZEN)*COS(PTHZEN))                          SCT  220
      RETURN                                                            SCT  230
10    CONTINUE                                                          SCT  240
      PSI=PSIST/DEG                                                     SCT  250
C     GENERAL CASE                                                      SCT  260
      X=SIN(SUNZEN)*SIN(PTHZEN)*COS(PSI)+COS(SUNZEN)*COS(PTHZEN)        SCT  270
      SCTANG=DEG*ACOS(X)                                                SCT  280
      RETURN                                                            SCT  290
      END                                                               SCT  300
      FUNCTION   PSI(PSIO,DELO,BETA,IARB,IARBO)                         FSI  100
C                                                                       FSI  110
C     FUNCTION PSI RETURNS THE VALUE OF SOLAR AZIMUTH RELATIVE TO       FSI  120
C     THE LINE OF SIGHT, AT THE CURRENT SCATTERING LOCATION             FSI  130
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              FSI  140
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     FSI  150
      DATA  EPSILN/1.0E-5/                                              FSI  160
      DELOR=DELO/DEG                                                    FSI  170
      BETAR=BETA/DEG                                                    FSI  180
      IF(IARBO.EQ.0) GO TO 5                                            FSI  190
C     SPECIAL CASES WHEN PSIO IS ARBITRARY                              FSI  200
      IARB=IARBO                                                        FSI  210
      IF(IARBO.EQ.1.OR.IARBO.EQ.3) RETURN                               FSI  220
      IF(BETA.LE.EPSILN) RETURN                                         FSI  230
C     PSI=180.0 (MOVED OUT FROM UNDER THE SUN)                          FSI  240
      IARB=0                                                            FSI  250
      PSI=180.0                                                         FSI  260
      RETURN                                                            FSI  270
5     CONTINUE                                                          FSI  280
C     GENERAL CASE                                                      FSI  290
      PSIOR=PSIO/DEG                                                    FSI  300
      IARB=0                                                            FSI  310
      ANUMER=SIN(DELOR)*SIN(PSIOR)                                      FSI  320
      DENOM=COS(BETAR)*SIN(DELOR)*COS(PSIOR)-SIN(BETAR)*COS(DELOR)      FSI  330
C     SPECIAL CASES                                                     FSI  340
C     NUMERATOR GOES TO ZERO IN THE FOLLOWING 3 CASES                   FSI  350
C 1)  DELO=0.0                                                          FSI  360
      IF(DELO.GT.EPSILN) GO TO 20                                       FSI  370
      IF(BETA.GT.EPSILN) GO TO 10                                       FSI  380
      IARB=2                                                            FSI  390
      RETURN                                                            FSI  400
10    PSI=180.0                                                         FSI  410
      RETURN                                                            FSI  420
C 2)  PSIO=0.0                                                          FSI  430
20    IF(ABS(PSIO).GT.EPSILN) GO TO 40                                  FSI  440
      IF(ABS(BETA-DELO).GE.EPSILN) GO TO 30                             FSI  450
C     SCATTERING POINT IS DIRECTLY UNDER THE SUN                        FSI  460
      IARB=2                                                            FSI  470
      RETURN                                                            FSI  480
30    IF(BETA.LT.DELO) PSI=0.0                                          FSI  490
      IF(BETA.GT.DELO) PSI=180.0                                        FSI  500
      RETURN                                                            FSI  510
C 3)  PSIO=180.0                                                        FSI  520
40    IF(ABS(PSIO).LT.(180.0-EPSILN)) GO TO 60                          FSI  530
      PSI=180.0                                                         FSI  540
      RETURN                                                            FSI  550
60    CONTINUE                                                          FSI  560
C     DENOMINATOR CAN GO TO ZERO FOR THE FOLLOWING 2 CASES              FSI  570
C 1)  BETA=DELO AND PSIO=0.0                                            FSI  580
C     THIS CASE WAS HANDLED EARLIER                                     FSI  590
C 2)  GENERAL CASE                                                      FSI  600
      IF(ABS(DENOM).GT.EPSILN) GO TO 80                                 FSI  610
      IF(PSIO.LT.0.0) PSI=-90.0                                         FSI  620
      IF(PSIO.GT.0.0) PSI=90.0                                          FSI  630
      RETURN                                                            FSI  640
80    CONTINUE                                                          FSI  650
      PSI=DEG*ATAN(ANUMER/DENOM)                                        FSI  660
C     NOTE ATAN RETURNS ARGUMENTS BETWEEN -90 AND 90, PSI               FSI  670
C     AND PSIO SHOULD BE OF THE SAME SIGN.                              FSI  680
      IF(PSIO.GT.0.0.AND.PSI.LT.0.0) PSI=PSI+180.                       FSI  690
      IF(PSIO.LT.0.0.AND.PSI.GT.0.0) PSI=PSI-180.                       FSI  700
      RETURN                                                            FSI  710
      END                                                               FSI  720
      FUNCTION   DEL(PSIO,DELO,BETA,IARBO)                              DEL  100
C                                                                       DEL  110
C     FUNCTION DEL RETURNS THE VALUE OF THE SUN'S ZENITH ANGLE          DEL  120
C     AT ANY POINT ALONG THE OPTICAL PATH BASED UPON STRAIGHT           DEL  130
C     LINE GEOMETRY (NO REFRACTION). THIS ANGLE IS USED TO SPECIFY      DEL  140
C     THE SCATTERING POINT TO SUN PATHS. THE BENDING DUE TO REFRACTION  DEL  150
C     ALONG THIS PATH IS DETERMINED BY THE GEO ROUTINES. IF THE BENDING DEL  160
C     IS GREATER THAN ONE DEGREE THE ZENITH ANGLE IS CORRECTED ACCORDINGDEL  170
C     AND THE PATH CALCULATION IS REPEATED.                             DEL  180
      COMMON /PARMTR/ RE,DELTAS,ZMAX,IMAX,IMOD,IBMAX,IPATH              DEL  190
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     DEL  200
      DATA  EPSILN/1.0E-5/                                              DEL  210
C                                                                       DEL  220
      IF(IARBO.EQ.0) GO TO 10                                           DEL  230
C     SPECIAL CASES IF PSIO IS ARBITRARY                                DEL  240
      IF(IARBO.EQ.1) DEL=DELO                                           DEL  250
      IF(IARBO.EQ.2) DEL=BETA                                           DEL  260
      IF(IARBO.EQ.3) DEL=0.0                                            DEL  270
      RETURN                                                            DEL  280
10    CONTINUE                                                          DEL  290
      PSIOR=PSIO/DEG                                                    DEL  300
      DELOR=DELO/DEG                                                    DEL  310
      BETAR=BETA/DEG                                                    DEL  320
C     GENERAL CASE                                                      DEL  330
      X=COS(DELOR)*COS(BETAR)+SIN(DELOR)*SIN(BETAR)*COS(PSIOR)          DEL  340
      DEL=DEG*ACOS(X)                                                   DEL  350
      RETURN                                                            DEL  360
      END                                                               DEL  370
      SUBROUTINE SSRAD(IPH,IK,ITZERO,IPATH,V,SUMSSR)                    SSR  100
C                                                                       SSR  110
C     SUBROUTINE SSRAD PERFORMS THE LAYER BY LAYER SINGLE SCATTERING    SSR  120
C     RADIANCE SUM.                                                     SSR  130
C                                                                       SSR  140
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  SSR  150
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   SSR  160
     1    RAINRT                                                        SSR  170
      COMMON /CARD3/ H1,H2,ANGLE,RANGE,BETA,RE,LEN                      SSR  180
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            SSR  190
      COMMON             WPATH(68, 63),TBBY(68)                         SSR  200
      COMMON ABSC(5,47),EXTC(5,47),ASYC(5,47),VX2(47),AWCCON(5)         SSR  210
      COMMON /SOLS/ AH1(68),ARH(68),                                    SSR  220
     X   WPATHS(68,63),PA(68),PR(68),ATHETA(35),ADBETA(35),LJ(69),JTURN,SSR  230
     X ANGSUN,CSZEN(68)                                                 SSR  240
      COMMON /SRAD/ TEB1,TEB2,TASP1,TASP2,TMSP1,TMSP2,TEB2SV            SSR  250
      COMMON /SRADS/ PMOL1,PMOL2,PAER1,PAER2                            SSR* 252
      SAVE /SRADS/                                                      SSR* 254
      IF(ITZERO.EQ.1) GO TO 60                                          SSR  260
      TX6=TX(6)                                                         SSR  270
      TX7=TX(7)                                                         SSR  280
      TX9=TX(9)                                                         SSR  290
      TX10=TX(10)                                                       SSR  300
      IF(IPATH-2) 10,20,30                                              SSR  310
10    CONTINUE                                                          SSR  320
CC    ISKIP = 0                                                         SSR  330
C     INITIAL CONDITIONS                                                SSR  340
C     SINGLE SCATTERING RADIANCE SUM                                    SSR  350
      SUMSSR=0.0                                                        SSR  360
C     OPTICAL PATH TRANSMITTANCES                                       SSR  370
      TASP2=1.0                                                         SSR  380
      TMSP2=1.0                                                         SSR  390
C     L PATH TRANSMITTANCE                                              SSR  400
      TEB2=TX9                                                          SSR  410
C     MOLECULAR AND AEROSOL PHASE FUNCTIONS                             SSR  420
      PMOL2=PR(IK)                                                      SSR  430
      PAER2=PA(IK)                                                      SSR  440
      IF(IPH.EQ.2)                                                      SSR  450
     1 CALL PHASEF(V,IK,AH1(IK),PA(IK),ARH(IK),PAER2)                   SSR  460
      RETURN                                                            SSR  470
20    CONTINUE                                                          SSR  480
C     CURRENT L PATH TRANSMITTANCE                                      SSR  490
      TEB1=TX9                                                          SSR  500
C     CURRENT MOLECULAR AND AEROSOL PHASE FUNCTIONS                     SSR  510
      PMOL1=PR(IK+1)                                                    SSR  520
      PAER1=PA(IK+1)                                                    SSR  530
      IKK = IK + 1                                                      SSR  540
      IF(IPH.EQ.2)                                                      SSR  550
     1 CALL PHASEF(V,IKK,AH1(IKK),PA(IKK),ARH(IKK),PAER1)               SSR  560
      RETURN                                                            SSR  570
30    CONTINUE                                                          SSR  580
C     CURRENT OPTICAL PATH TRANSMITTANCES                               SSR  590
      TASP1=TX7/TX10                                                    SSR  600
      TMSP1=TX6                                                         SSR  610
C     SINGLE SCATTERING RADIANCE CALCULATION                            SSR  620
      IF(TASP1.EQ.0.0 .OR. TMSP1.EQ.0.0 .OR.TMSP2.EQ.0.0 .OR.           SSR  630
     1    TASP2.EQ.0.0) GO TO 50                                        SSR  640
      XA1=PAER1*TEB1/TASP1                                              SSR  650
      XA2=PAER2*TEB2/TASP2                                              SSR  660
      XM1=PMOL1*TEB1/TMSP1                                              SSR  670
      XM2=PMOL2*TEB2/TMSP2                                              SSR  680
      DTASP=TASP2-TASP1                                                 SSR  690
      DTMSP=TMSP2-TMSP1                                                 SSR  700
C     COULD ADD A CHECK FOR SMALL DTMSP,DTASP AND BYPASS CALCULATION    SSR  710
      SUMSSR=SUMSSR+.5*(DTASP*(XA1+XA2)+DTMSP*(XM1+XM2))                SSR  720
C     RESET L AND OPTICAL PATH  TRANSMITTANCES                          SSR  730
   50 CONTINUE                                                          SSR  740
      TASP2=TASP1                                                       SSR  750
      TEB2SV=TEB2                                                       SSR  760
      TMSP2=TMSP1                                                       SSR  770
      TEB2=TEB1                                                         SSR  780
C     RESET PHASE FUNTION VALUES                                        SSR  790
      PAER2=PAER1                                                       SSR  800
      PMOL2=PMOL1                                                       SSR  810
60    CONTINUE                                                          SSR  820
      RETURN                                                            SSR  830
      END                                                               SSR  840
      SUBROUTINE SOURCE(VV,ISOURC,IDAY,ANGLE,SS)                        SOR  100
      COMMON /ICLL/ ICALL,FPHS,FALB,FORBIT                              SOR  110
C     SUBROUITNE SOURCE CONTAINS THE SOLAR INTENSITY DATA AS A          SOR  120
C     FUNTION OF WAVELENGTH. THIS ROUTINE IS ALSO CAPABLE OF CALCULATINGSOR  130
C     LUNAR INTENSITY BASED ON THE PHASE ANGLE BETWEEN THE SUN, MOON ANDSOR  140
C     EARTH. CORRECTIONS ARE MADE FOR THE SUN'S ELLIPTIC ORBIT.         SOR  150
C                                                                       SOR  160
      DIMENSION NDAY(13),RAT(13),PHS(17),ALB(29)                        SOR  170
      DATA NDAY/1,32,60,91,121,152,182,213,244,274,305,335,366/         SOR  180
      DATA RAT/1.034,1.030,1.019,1.001,.985,.972,.967,.971,.982,        SOR  190
     1    .998,1.015,1.029,1.034/                                       SOR  200
      DATA PHS/100.,73.2,57.8,42.3,32.0,23.3,16.7,12.4,8.7,6.7,         SOR  210
     1    4.7,3.6,2.4,1.2,0.9,0.4,.002/                                 SOR  220
      DATA ALB/.001,.01,.03,.075,.1,.13,.155,.17,.178,.185,.2,.211,     SOR  230
     1    .231,.25,.275,.289,.285,.287,.3,.29,.3,.31,.313,.319,.329     SOR  240
     1    ,.339,.345,.350,.4/                                           SOR  250
      V=10000./VV                                                       SOR  260
      IF(VV.LE.0.) V = 1.0E+38                                          SOR  270
      IF(ISOURC.NE.1) GO TO 50                                          SOR  280
      IF(ICALL.EQ.1) GO TO 20                                           SOR  290
C                                                                       SOR  300
C     MOON PHASE ANGLE FACTOR                                           SOR  310
C                                                                       SOR  320
      FPHS=0.0                                                          SOR  330
      IF(ANGLE.GT.160.) GO TO 20                                        SOR  340
      IP=ANGLE/10.                                                      SOR  350
      IF(FLOAT(IP*10).EQ.ANGLE) GO TO 10                                SOR  360
      FPHS=PHS(IP+1)+(ANGLE-10.*IP)*(PHS(IP+2)-PHS(IP+1))/10.           SOR  370
      GO TO 20                                                          SOR  380
10    FPHS=PHS(IP+1)                                                    SOR  390
C                                                                       SOR  400
C     GEOMETRICAL ALBEDO OF THE MOON                                    SOR  410
C                                                                       SOR  420
20    FALB=0.4                                                          SOR  430
      IF(V.GE.5.) GO TO 40                                              SOR  440
      IF(V.GT.2.8) GO TO 30                                             SOR  450
      I1=V*10                                                           SOR  460
      FALB=ALB(I1)+(ALB(I1+1)-ALB(I1))*(V-I1*0.1)*10.                   SOR  470
      GO TO 40                                                          SOR  480
30    FALB=ALB(28)+(ALB(29)-ALB(28))*(V-2.8)/2.2                        SOR  490
40    CONTINUE                                                          SOR  500
C                                                                       SOR  510
C     SUN ELLIPTIC ORBIT FACTOR                                         SOR  520
C                                                                       SOR  530
50    IF(ICALL.EQ.1) GO TO 90                                           SOR  540
      FORBIT=0.0                                                        SOR  550
      IF(IDAY.GT.0 .AND. IDAY.LT.367) GO TO 55                          SOR  560
      FORBIT = 1.0                                                      SOR  570
      GO TO 90                                                          SOR  580
55    CONTINUE                                                          SOR  590
      DO 60 I=1,13                                                      SOR  600
      IF(NDAY(I).EQ.IDAY) GO TO 80                                      SOR  610
      IF(NDAY(I).GT.IDAY) GO TO 70                                      SOR  620
60    CONTINUE                                                          SOR  630
70    FORBIT=RAT(I-1)+(IDAY-NDAY(I-1))*(RAT(I)-RAT(I-1))/(NDAY(I)       SOR  640
     1   -NDAY(I-1))                                                    SOR  650
      GO TO 90                                                          SOR  660
80    FORBIT=RAT(I)                                                     SOR  670
90    CONTINUE                                                          SOR  680
      ICALL=1                                                           SOR  690
C                                                                       SOR  700
C     SOLAR INTENSITY                                                   SOR  710
C                                                                       SOR  720
      SS = SUN(VV)*FORBIT                                               SOR  730
C     CONVERT W/M-2-MICRON TO W/CM-2-MICRON                             SOR  740
      IF(ISOURC. EQ. 1) SS = SS * FPHS * FALB * 2.04472E-7              SOR  750
      SS=SS*.0001                                                       SOR  760
      RETURN                                                            SOR  770
      END                                                               SOR  780
      FUNCTION   SUN(V)                                                 SUN  100
C                                                                       SUN  110
C        EVALUATES THE EXTRA-TERRESTRIAL SOLAR IRRADIANCE               SUN  120
C                                                                       SUN  130
C        INPUT:  V  =  FREQUENCY   (CM-1)                               SUN  140
C                VALID RANGE   0 TO 57490 (CM-1)                        SUN  150
C                    (EQUIVALENT TO WAVELENGTHS > 0.174 MICROMETERS)    SUN  160
C                                                                       SUN  170
C        OUTPUT:  SUN  =  SOLAR IRRADIANCE  (WATTS M-2 MICROMETER-1)    SUN  180
C                                                                       SUN  190
C        WRITES A WARNING MESSAGE TO TAPE6  &  RETURNS  SUN = 0         SUN  200
C            IF THE INPUT FREQUENCY IS OUT OF RANGE                     SUN  210
C                                                                       SUN  220
C       USES  BLOCK DATA SOLAR   WHICH CONTAINS THE VALUES FOR SOLARA + SUN  230
C                                                                       SUN  240
      COMMON /SUNDAT/ SOLARA(1440), SOLARB(2910)                        SUN  250
      DATA  A, B / 3.50187E-13, 3.93281 /                               SUN  260
C                                                                       SUN  270
C       WM, W0, W1, W2  ARE STATEMENT FUNCTIONS USED BY                 SUN  280
C            THE 4 POINT LAGRANGE INTERPOLATION                         SUN  290
      WM(P) = P*(P - 1)*(P - 2)                                         SUN  300
      W0(P) = 3*(P**2 - 1)*(P - 2)                                      SUN  310
      W1(P) = 3*P*(P + 1)*(P - 2)                                       SUN  320
      W2(P) = P*(P**2 - 1)                                              SUN  330
C                                                                       SUN  340
C           IF  V  IS TOO SMALL,  WRITE WARNING  +  RETURN SUN = 0      SUN  350
      IF(V .LT. 0.0) THEN                                               SUN  360
        SUN = 0.0                                                       SUN  370
        WRITE(6, 900) V                                                 SUN  380
        RETURN                                                          SUN  390
C                                                                       SUN  400
      ELSEIF( V .GE. 0.0  .AND.  V .LT. 100.0 ) THEN                    SUN  410
C         FOR LOW FREQUENCIES USE A POWER LAW APPROXIMATION             SUN  420
        SUN = A*V**B                                                    SUN  430
        RETURN                                                          SUN  440
C                                                                       SUN  450
      ELSEIF( V .GE. 100.0  .AND.  V .LT. 28420.0 ) THEN                SUN  460
C         USE  4 POINT INTERPOLATION  ON  ARRAY  SOLARA                 SUN  470
C               WHICH IS AT  20 CM-1  SPACING  FROM 0 TO 28720 CM-1     SUN  480
        I = 1 + INT(V/20.0)                                             SUN  490
        P = MOD(V, 20.0)/20.0                                           SUN  500
        SUN = ( W2(P)*SOLARA(I+2) - W1(P)*SOLARA(I+1) +                 SUN  510
     +              W0(P)*SOLARA(I) - WM(P)*SOLARA(I-1) ) / 6           SUN  520
        RETURN                                                          SUN  530
C                                                                       SUN  540
      ELSEIF( V .GE. 28420.0  .AND.  V .LE. 57470.0 ) THEN              SUN  550
C         USE  4 POINT INTERPOLATION  ON  ARRAY  SOLARB                 SUN  560
C             WHICH IS AT  10 CM-1  SPACING  FROM 28400 TO 57490 CM-1   SUN  570
        I = INT(V/10.0) - 2839                                          SUN  580
        P = MOD(V, 10.0)/10.0                                           SUN  590
        SUN = ( W2(P)*SOLARB(I+2) - W1(P)*SOLARB(I+1) +                 SUN  600
     +              W0(P)*SOLARB(I) - WM(P)*SOLARB(I-1) ) / 6           SUN  610
        RETURN                                                          SUN  620
C                                                                       SUN  630
      ELSEIF( V .GT. 57470.0 ) THEN                                     SUN  640
C           IF  V  IS TOO LARGE,  WRITE WARNING  +  RETURN SUN = 0      SUN  650
        SUN = 0.0                                                       SUN  660
        WRITE(6, 900) V                                                 SUN  670
        RETURN                                                          SUN  680
C                                                                       SUN  690
      ENDIF                                                             SUN  700
C                                                                       SUN  710
      RETURN                                                            SUN  720
  900 FORMAT('0 *****  WARNING - INPUT FREQUENCY = ', 1PG12.5, 'CM-1',  SUN  730
     +  /, '   OUTSIDE VALID RANGE OF 0 TO 57470 CM-1    *******', / )  SUN  740
      END                                                               SUN  750
      BLOCK DATA SOLAR                                                  SOL  100
C>    BLOCK DATA                                                        SOL  110
C                                                                       SOL  120
C                                                                       SOL  130
C     COMMON /SUNDAT/   SOLARA(1440), SOLARB(2910)                      SOL  140
      COMMON /SUNDAT/   SUNA01( 41),SUNA02(144),SUNA03(144),SUNA04(144),SOL  150
     A      SUNA05(144),SUNA06(144),SUNA07(144),SUNA08(144),SUNA09(144),SOL  160
