      SUBROUTINE SUBSOL(THETAS,PHIS,TIME,IDAY)                          SUB  100
C                                                                       SUB  110
C     SUBROUTINE SUBSOL CALCULATES THE SUBSOLAR POINT ANGLES            SUB  120
C     THETAS AND PHIS BASED UPON IDAY AND TIME. SINCE EACH              SUB  130
C     YEAR IS 365.25 DAYS LONG THE EXACT VALUE OF THE DECLINATION       SUB  140
C     ANGLE CHANGES FROM YEAR TO YEAR.  FOR PRECISE VALUES CONSULT      SUB  150
C     ' THE AMERICAN EPHEMERIS AND NAUTICAL ALMANAC' PUBLISHED YEARLY   SUB  160
C     BY THE U.S. GOVT. PRINTING OFFICE.  ALSO, THE SOLAR POSITION      SUB  170
C     IS CHARACTERIZED BY 25 POINTS BELOW; THIS SHOULD PREDICT THE SUBSOSUB  180
C     ANGLES WITHIN ONE DEGREE.  FOR INCREASED ACCURACY ADD MORE DATA   SUB  190
C     POINTS                                                            SUB  200
C                                                                       SUB  210
C     THE EQUATION OF TIME, EQT, IS IN MINUTES                          SUB  220
C     THE DECLINATION ANGLE, DEC IS IN DEGREES                          SUB  230
C                                                                       SUB  240
      COMMON /MODEL/ ZM(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),      SUB  250
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         SUB  260
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                SUB  270
      DIMENSION NDAY(25),EQT(25),DEC(25)                                SUB  280
      DATA NDAY /1,9,21,32,44,60,91,121,141,152,160,172,182,            SUB  290
     1 190,202,213,244,274,305,309,325,335,343,355,366/                 SUB  300
      DATA DEC /-23.07,-22.22,-20.08,-17.32,-13.62,-7.88,4.23,          SUB  310
     1 14.83, 20.03,21.95,22.87,23.45,23.17,22.47,20.63,18.23,8.58,     SUB  320
     2 -2.88,-14.18,-15.45,-19.75,-21.68,-22.75,-23.43,-23.07/          SUB  330
      DATA EQT /-3.23,-6.83,-11.17,-13.57,-14.33,-12.63,-4.2,           SUB  340
     1 2.83,3.57,2.45,1.10,-1.42,-3.52,-4.93,-6.25,-6.28,-0.25,         SUB  350
     2 10.02,16.35,16.38,14.3,11.27,8.02,2.32,-3.23/                    SUB  360
      IF(IDAY.LT.1.OR.IDAY.GT.366)  GO TO 900                           SUB  370
      IF(TIME.LT.0.0.OR.TIME.GT.24.0)  GO TO 910                        SUB  380
      DO 10 I=1,25                                                      SUB  390
      IF(NDAY(I).EQ.IDAY) GO TO 30                                      SUB  400
10    IF(NDAY(I).GT.IDAY) GO TO 20                                      SUB  410
20    I=I-1                                                             SUB  420
      EQTIME=EQT(I)+(EQT(I+1)-EQT(I))*(IDAY-NDAY(I))/(NDAY(I+1)-NDAY(I))SUB  430
      DECANG=DEC(I)+(DEC(I+1)-DEC(I))*(IDAY-NDAY(I))/(NDAY(I+1)-NDAY(I))SUB  440
      GO TO 40                                                          SUB  450
30    EQTIME=EQT(I)                                                     SUB  460
      DECANG=DEC(I)                                                     SUB  470
40    THETAS=DECANG                                                     SUB  480
      EQTIME=EQTIME/60.0                                                SUB  490
      PHIS=15.0*(TIME+EQTIME)-180.0                                     SUB  500
      IF(PHIS.LT.0.0) PHIS=PHIS+360.0                                   SUB  510
      RETURN                                                            SUB  520
900   WRITE(IPR,901) IDAY                                               SUB  530
901   FORMAT(' FROM SUBSOL - IDAY OUT OF RANGE, IDAY=',I6)              SUB  540
      STOP                                                              SUB  550
910   WRITE(IPR,902) TIME                                               SUB  560
902   FORMAT(' FROM SUBSOL - TIME OUT OF RANGE, TIME=',E12.5)           SUB  570
      STOP                                                              SUB  580
      END                                                               SUB  590
      SUBROUTINE PHASEF(V,IK,ALT,SANGLE,RH,PHFA)                        PHS  100
C                                                                       PHS  110
C     RETURNS THE AEROSOL PHASE FUNCTION FROM THE STORED DATA BASE      PHS  120
C                                                                       PHS  130
C                                                                       PHS  140
C     THE TRUTH TABLE MNUM(27,26) STORED IN COMMON/MNMPHS/              PHS  150
C     IN SUBROUTINE PHSDTA IS QUERIED TO DETERMINE THE PROPER PHASE     PHS  160
C     FUNCTION NEEDED.                                                  PHS  170
C     THE 27 POSITIONS REPRESENT THE 27 SPECIFIC FREQUENCIES SHOWN IN   PHS  180
C     DATA STATEMENT WAVE  .2-40 MICRONS.                               PHS  190
C     THE NUMBERS STORED IN THESE 27 POSITIONS REPRESENT THE CORRECT    PHS  200
C     PHASE FUNCTIONS CHOSEN FROM THE DATA STATEMENT PHSFNC'S 1-70      PHS  210
C     POSSIBLE CHOICES.                                                 PHS  220
C     THE 26 DATA STATEMENTS EACH HAVING 27 FREQUENCIES REPRESENT THE   PHS  230
C     FOLLOWING 26 MODELS;                                              PHS  240
C      1=RURAL     0%RH   2=RURAL    70%RH   3=RURAL    80%RH           PHS  250
C      4=RURAL    99%RH   5=MARITIME  0%RH   6=MARITIME 70%RH           PHS  260
C      7=MARITIME 80%RH   8=MARITIME 99%RH   9=URBAN     0%RH           PHS  270
C     10=URBAN    70%RH  11=URBAN    80%RH  12=URBAN    99%RH           PHS  280
C     13=OCEANIC   0%RH  14=OCEANIC  70%RH  15=OCEANIC  80%RH           PHS  290
C     16=OCEANIC  99%RH  17=TROPOSPH  0%RH  18=TROPOSPH 70%RH           PHS  300
C     19=TROPOSPH 80%RH  20=TROPOSPH 99%RH  21=STRATOSPHERIC            PHS  310
C     22=AGED VOLCANIC   23=FRESH VOLCANIC  24=RADIATION FOG            PHS  320
C     25=ADVECTIVE FOG   26=METEORIC DUST                               PHS  330
C                                                                       PHS  340
C                                                                       PHS  350
C     IN THE PRESENT VERSION THE 4 OCEANIC MODELS 13-16                 PHS  360
C     ARE NOT UTILIZED.                                                 PHS  370
C                                                                       PHS  380
      COMMON /IFIL/ IRD,IPR,IPU,NPR,IPR1                                PHS  390
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     PHS  400
      COMMON /CARD1/ MODEL,ITYPE,IEMSCT,M1,M2,M3,IM,NOPRNT,TBOUND,SALB  PHS  410
      COMMON /CARD2/ IHAZE,ISEASN,IVULCN,ICSTL,ICLD,IVSA,VIS,WSS,WHH,   PHS  420
     1    RAINRT                                                        PHS  430
      COMMON /CARD2D/ IREG(4),ALTB(4),IREGC(4)                          PHS  440
      COMMON/MNMPHS/ MNUM(27,26),PHSFNC(34,70)                          PHS  450
      COMMON /MODEL/ ZM(34),PM(34),TM(34),RFNDX(34),DENSTY(63,34),      PHS  460
     1 CLDAMT(34),RRAMT(34),EQLWC(34),HAZEC(34)                         PHS  470
      COMMON /AER/ XX1,XX2,XX3,XX4,XX5,                                 PHS  480
     X YY1,YY2,YY3,YY4,YY5,ZZ1,ZZ2,ZZ3,ZZ4,ZZ5                          PHS  490
      DIMENSION RHPTS(4),WAVE(27),ANG(34)                               PHS  500
      DATA ANG /0.,2.,4.,6.,8.,10.,12.,16.,20.,24.,28.,32.,36.,40.      PHS  510
     1,50.,60.,70.,80.,90.,100.,110.,120.,125.,130.,135.,140.,145.      PHS  520
     2,150.,155.,160.,165.,170.,175.,180./                              PHS  530
      DATA WAVE /.2,.3,.55,.6943,1.06,1.536,2.0,2.5,2.7,3.,3.2,3.39     PHS  540
     1,5.,6.,7.2,7.9,8.7,9.2,10.0,10.59,12.5,15.0,17.2,18.5,21.3,30.    PHS  550
     2,40.0/                                                            PHS  560
      DATA RHPTS /0.0,70.0,80.0,99.0/                                   PHS  570
C                                                                       PHS  580
      PHFA=0.0                                                          PHS  590
      ALAM=1.0E4/V                                                      PHS  600
      IF(V .LT. 1.0E-5) ALAM = 1.0E+9                                   PHS  605
      IF(SANGLE.LT.0.0.OR.SANGLE.GT.180.0) GO TO 900                    PHS  610
      IF((ALAM-WAVE(1)).LT. -1.0E-6) GO TO 910                          PHS  620
      IF(ALAM.GT.WAVE(27)) THEN                                         PHS  630
           COSANG=COS(CA*SANGLE)                                        PHS  640
           CALL HENGNS(ZZ1,COSANG,PHFA)                                 PHS  650
           RETURN                                                       PHS  660
      ENDIF                                                             PHS  670
C     DETERMINE THE AEROSOL MODEL NUMBER                                PHS  680
      IF(ALT.GT.ALTB(1)) GO TO 95                                       PHS  690
C                                                                       PHS  700
C                                                                       PHS  710
C                                                                       PHS  720
      IF(IHAZE .EQ. 0) GO TO 400                                        PHS  730
      IF(IHAZE .EQ.10) THEN                                             PHS  740
           COSANG=COS(CA*SANGLE)                                        PHS  750
           CALL HENGNS(ZZ1,COSANG,PHFA)                                 PHS  760
           RETURN                                                       PHS  770
      ENDIF                                                             PHS  780
C                                                                       PHS  790
C     CHECK IF CLOUD,RAIN OR DESERT MODEL IS REQUESTED                  PHS  800
C                                                                       PHS  810
       IF(ICLD .GT. 0.  OR. IHAZE .EQ. 7 )  THEN                        PHS  820
           COSANG=COS(CA*SANGLE)                                        PHS  830
C                                                                       PHS  840
           CALL HENGNS(ZZ1,COSANG,PHFA)                                 PHS  850
              RETURN                                                    PHS  860
        ENDIF                                                           PHS  870
      IF(IHAZE.GE.8) GO TO 90                                           PHS  880
C                                                                       PHS  890
C     0-2KM BOUNDARY LAYER MODELS, RH DEPENDENT                         PHS  900
      DO 50 I1=1,4                                                      PHS  910
      I=I1                                                              PHS  920
      IF(RHPTS(I).EQ.RH) GO TO 70                                       PHS  930
      IF(RHPTS(I).GT.RH) GO TO 60                                       PHS  940
50    CONTINUE                                                          PHS  950
60    IRHLO=I-1                                                         PHS  960
      IRHHI=I                                                           PHS  970
      GO TO 80                                                          PHS  980
70    IRHLO=I                                                           PHS  990
      IRHHI=I                                                           PHS 1000
80    CONTINUE                                                          PHS 1010
C     RURAL MODEL                                                       PHS 1020
      IF(IHAZE.EQ.1.OR.IHAZE.EQ.2) NN0=0                                PHS 1030
C     MARITIME MODEL                                                    PHS 1040
      IF(IHAZE.EQ.3.OR.IHAZE.EQ.4) NN0=4                                PHS 1050
C     URBAN MODEL                                                       PHS 1060
      IF(IHAZE.EQ.5) NN0=8                                              PHS 1070
C     TROPOSPHERIC MODEL                                                PHS 1080
      IF(IHAZE.EQ.6) NN0=16                                             PHS 1090
      NN=NN0+IRHLO                                                      PHS 1100
      GO TO 130                                                         PHS 1110
C     0-2KM FOG MODELS, NO RH DEPENDENCE                                PHS 1120
90    IF(IHAZE.EQ.8) NN=24                                              PHS 1130
      IF(IHAZE.EQ.9) NN=25                                              PHS 1140
      GO TO 130                                                         PHS 1150
95    IF(ALT.GT.ALTB(2)) GO TO 110                                      PHS 1160
C     2-10KM TROPOSPHERIC MODEL                                         PHS 1170
       IF(ICLD .GT. 0.  OR. IHAZE .EQ. 7 )  THEN                        PHS 1180
           COSANG=COS(CA*SANGLE)                                        PHS 1190
C                                                                       PHS 1200
           CALL HENGNS(ZZ2,COSANG,PHFA)                                 PHS 1210
              RETURN                                                    PHS 1220
        ENDIF                                                           PHS 1230
      NN=18                                                             PHS 1240
      GO TO 130                                                         PHS 1250
110   IF(ALT.GT.ALTB(3)) GO TO 120                                      PHS 1260
C     10-30KM STRATOSPHERIC MODELS                                      PHS 1270
C     BACKGROUND MODEL                                                  PHS 1280
      IF(IVULCN.EQ.0.OR.IVULCN.EQ.1) NN=21                              PHS 1290
C     AGED VOLCANIC MODEL                                               PHS 1300
      IF(IVULCN.EQ.2.OR.IVULCN.EQ.4) NN=22                              PHS 1310
C     FRESH VOLCANIC                                                    PHS 1320
      IF(IVULCN.EQ.3.OR.IVULCN.EQ.5 .OR.IVULCN.EQ.8) NN=23              PHS 1330
C     BACKGROUND STRATO                                                 PHS 1340
      IF(IVULCN.EQ.6.OR.IVULCN.EQ.7) NN=21                              PHS 1350
      GO TO 130                                                         PHS 1360
C     30-100KM METEORIC MODEL                                           PHS 1370
120   NN=26                                                             PHS 1380
130   IRH=0                                                             PHS 1390
C                                                                       PHS 1400
C     DETERMINE THE BOUNDING ANGLE INDICES                              PHS 1410
140   DO 210 I1=1,34                                                    PHS 1420
      I=I1                                                              PHS 1430
      IF(ANG(I).EQ.SANGLE) GO TO 230                                    PHS 1440
      IF(ANG(I).GT.SANGLE) GO TO 220                                    PHS 1450
210   CONTINUE                                                          PHS 1460
220   IANG1=I-1                                                         PHS 1470
      IANG2=I                                                           PHS 1480
      GO TO 240                                                         PHS 1490
230   IANG1=I                                                           PHS 1500
      IANG2=I                                                           PHS 1510
240   CONTINUE                                                          PHS 1520
C                                                                       PHS 1530
C     DETERMINE THE BOUNDING WAVELENGTH INDICES                         PHS 1540
      DO 250 I1=1,27                                                    PHS 1550
      I=I1                                                              PHS 1560
      IF(WAVE(I).EQ.ALAM) GO TO 270                                     PHS 1570
      IF(WAVE(I).GT.ALAM) GO TO 260                                     PHS 1580
250   CONTINUE                                                          PHS 1590
260   IWAV1=I-1                                                         PHS 1600
      IWAV2=I                                                           PHS 1610
      GO TO 280                                                         PHS 1620
270   IWAV1=I                                                           PHS 1630
      IWAV2=I                                                           PHS 1640
280   CONTINUE                                                          PHS 1650
C                                                                       PHS 1660
C     FUNCTION PF CHOOSES DESIRED PHASE FUNCTION FROM LOOK UP TABLE     PHS 1670
C     MNUM(IWAV,NN)  WHERE IWAV IS FREQ. AND NN IS MODEL NO.            PHS 1680
C                                                                       PHS 1690
C     WAVELENGTH INTERPOLATION ONLY USES PF11 AND PF21                  PHS 1700
C     ANGLE INTERPOLATION ONLY USES PF11 AND PF12                       PHS 1710
C     WAVELENGTH AND ANGLE INTERPOLATION USES PF11,PF21 AND PF12,PF22.  PHS 1720
C                                                                       PHS 1730
      PF11=PF(NN,IWAV1,IANG1)                                           PHS 1740
      PF21=PF(NN,IWAV2,IANG1)                                           PHS 1750
      PF12=PF(NN,IWAV1,IANG2)                                           PHS 1760
      PF22=PF(NN,IWAV2,IANG2)                                           PHS 1770
C     INTERPOLATE IN WAVELENGTH THEN ANGLE                              PHS 1780
      IF(IWAV1.EQ.IWAV2) GO TO 310                                      PHS 1790
      IF(IANG1.EQ.IANG2) GO TO 290                                      PHS 1800
C     BOTH INTERPOLATIONS ARE NECESSARY                                 PHS 1810
      CALL INTERP(2,ALAM,WAVE(IWAV1),WAVE(IWAV2),YANG1,                 PHS 1820
     1PF11,PF21)                                                        PHS 1830
      CALL INTERP(2,ALAM,WAVE(IWAV1),WAVE(IWAV2),YANG2,                 PHS 1840
     1PF12,PF22)                                                        PHS 1850
      CALL INTERP(2,SANGLE,ANG(IANG1),ANG(IANG2),Y,YANG1,YANG2)         PHS 1860
      GO TO 330                                                         PHS 1870
C     ONLY WAVELENGTH INTERPOLATION IS NECESSARY                        PHS 1880
290   CALL INTERP(2,ALAM,WAVE(IWAV1),WAVE(IWAV2),Y,PF11,                PHS 1890
     1PF21)                                                             PHS 1900
      GO TO 330                                                         PHS 1910
310   IF(IANG1.EQ.IANG2) GO TO 320                                      PHS 1920
C     ONLY ANGLE INTERPOLATION IS NECESSARY                             PHS 1930
      CALL INTERP(2,SANGLE,ANG(IANG1),ANG(IANG2),Y,PF11,                PHS 1940
     1PF12)                                                             PHS 1950
      GO TO 330                                                         PHS 1960
C     NO INTERPOLATION IS NECESSARY                                     PHS 1970
320   Y=PF(NN,IWAV1,IANG1)                                              PHS 1980
330   CONTINUE                                                          PHS 1990
      PHFA=Y                                                            PHS 2000
C                                                                       PHS 2010
C     HUMIDITY DEPENDENCE                                               PHS 2020
      IF(ALT.GT.ALTB(1).OR.NN.GE.17.OR.IRHLO.EQ.IRHHI) GO TO 400        PHS 2030
      IF(IRH.EQ.1) GO TO 340                                            PHS 2040
      NN=NN0+IRHHI                                                      PHS 2050
      PHFA1=PHFA                                                        PHS 2060
      IRH=1                                                             PHS 2070
      GO TO 280                                                         PHS 2080
340   CONTINUE                                                          PHS 2090
      PHFA2=PHFA                                                        PHS 2100
      CALL INTERP(1,RH,RHPTS(IRHLO),RHPTS(IRHHI),                       PHS 2110
     CPHFA,PHFA1,PHFA2)                                                 PHS 2120
400   CONTINUE                                                          PHS 2130
      RETURN                                                            PHS 2140
  900 WRITE(IPR,901) SANGLE                                             PHS 2150
  901 FORMAT('0FROM PHASEF- SCATTERING ANGLE IS OUT OF RANGE, '         PHS 2160
     1    ,'ANGLE = ',E12.5)                                            PHS 2170
      STOP                                                              PHS 2180
  910 WRITE(IPR,911) ALAM                                               PHS 2190
  911 FORMAT('0FROM PHASEF- ALAM IS OUT OF RANGE, ALAM = ',E12.5)       PHS 2200
      STOP                                                              PHS 2210
      END                                                               PHS 2220
      SUBROUTINE HENGNS(ZZ,SANGLE,PHFA)                                 HEN  100
C                                                                       HEN  110
C    CALCULATES THE PHASE FUNCTION USING THE HENREY GREENSTEIN          HEN  120
C    METHOD FOR CLOUDS, DESERT AERSOL, RAIN OR USER READ IN MODEL       HEN  130
C                                                                       HEN  140
      COMMON /CNSTNS/ PI,CA,DEG,GCAIR,BIGNUM,BIGEXP                     HEN  150
C                                                                       HEN  160
      PFHG(GG,X)=(1.0-GG**2)/(4.*PI*(1.0+GG**2-2.0*GG*X)**1.5)          HEN  170
      PHFA = PFHG(ZZ,SANGLE)                                            HEN  180
      RETURN                                                            HEN  190
      END                                                               HEN  200
      FUNCTION   PF(NN,I,J)                                             PF   100
C     CALL THE APPROPRIATE PHASE FUNCTION                               PF   110
      COMMON/MNMPHS/ MNUM(27,26),PHSFNC(34,70)                          PF   120
      M=MNUM(I,NN)                                                      PF   130
      PF=PHSFNC(J,M)                                                    PF   140
      RETURN                                                            PF   150
      END                                                               PF   160
      SUBROUTINE INTERP(INTYPE,X,X1,X2,F,F1,F2)                         INT  100
C     SUBROUTINE INTERP INTERPOLATES TO DETERMINE THE VALUE OF F        INT  110
C     AT X, GIVEN F1 AT X1 AND F2 AT X2.                                INT  120
C     INTYPE=1 FOR LINEAR INTERPOLATION                                 INT  130
C     INTYPE=2 FOR LOGARITHMIC INTERPOLATION                            INT  140
      ITYPE=INTYPE                                                      INT  150
      IF(F1.LE.0.0.OR.F2.LE.0.0) ITYPE=1                                INT  160
      IF(ITYPE.EQ.2) GO TO 100                                          INT  170
C     LINEAR INTERPOLATION                                              INT  180
      F=F1+(X-X1)*(F2-F1)/(X2-X1)                                       INT  190
      RETURN                                                            INT  200
100   CONTINUE                                                          INT  210
      A1=ALOG(F1)                                                       INT  220
      A2=ALOG(F2)                                                       INT  230
      A=A1+(X-X1)*(A2-A1)/(X2-X1)                                       INT  240
      F=EXP(A)                                                          INT  250
      RETURN                                                            INT  260
      END                                                               INT  270
      FUNCTION   RAYSCT(V)                                              RAY  100
C     RADIATION FLD OUT                                                 RAY  110
C **  MOLECULAR SCATTERING                                              RAY  120
      RAYSCT=0.                                                         RAY  130
      IF(V.LE.3000.) RETURN                                             RAY  140
      RAYSCT=V**3/(9.26799E+18-1.07123E+09*V**2)                        RAY  150
C     V**4 FOR RADIATION FLD IN                                         RAY  160
      RETURN                                                            RAY  170
      END                                                               RAY  180
      SUBROUTINE EXPINT(X,X1,X2,A)                                      EXP  100
C     EXPONENTIAL INTERPOLATION                                         EXP  110
      IF(X1.EQ.0.0 .OR. X2.EQ.0.0)  GO TO 100                           EXP  120
      X = X1*(X2/X1)**A                                                 EXP  130
      RETURN                                                            EXP  140
  100 X = X1+(X2-X1)*A                                                  EXP  150
      RETURN                                                            EXP  160
      END                                                               EXP  170
      SUBROUTINE EQULWC                                                 EQU  100
CCC                                                                     EQU  110
CCC   EQUIVALENT LIQUID  WATER CONSTANTS FOR BEXT (0.55UM)=1.0KM-1      EQU  120
CCC   AWCCON(1-4) IS SET TO ONE OF THE CONSTANTS FOR EACH AEROSOL       EQU  130
CCC   IN SUBROUTINE EXABIN AND MULTIPLIED BY THE BEXT (DENSTY(N,I))     EQU  140
CCC   WHERE N=7,12,13 OR 14 AND I IS THE NUMBER OF LAYERS               EQU  150
CCC                                                                     EQU  160
      COMMON RELHUM(34),HSTOR(34),ICH(4),VH(17),TX(63),W(63)            EQU  170
