      KJ=K+(KMIN+1)/2                                                   GMR 2330
      JI = JLOW + J                                                     GMR 2340
      FAC = ((TMPTAB(ILOW)-T)*FACTOR(ILOW+1,JI,KJ)+(T-TMPTAB(ILOW+1))*  GMR 2350
     1 FACTOR(ILOW,JI,KJ))/(TMPTAB(ILOW)-TMPTAB(ILOW+1))                GMR 2360
      JI = JLOW +3-J                                                    GMR 2370
      FACIT(J) = (TFREQ(JI)-FREQ )*FAC                                  GMR 2380
   49 CONTINUE                                                          GMR 2390
      TFACT(K) = (FACIT(2)-FACIT(1))/(TFREQ(JLOW+1)-TFREQ(JLOW+2))      GMR 2400
